#define MAX_STEPS 100
#define MAX_DIST 100.
#define HIT_DIST 0.01 
#define rot2(a)      mat2(cos(a),-sin(a),sin(a),cos(a)) // V.xy *= rot2(a.z)

vec3 bezierPos(vec3 anchor1, vec3 handle1, vec3 handle2, vec3 anchor2, float t) {
    vec3 point = anchor1 * (-pow(t,3.)+3.*pow(t,2.)-3.*t+1.);
    point += handle1 * (3.*pow(t,3.)-6.*pow(t,2.)+3.* t);
    point += handle2 * (-3.*pow(t,3.)+3.*pow(t,2.));
    point += anchor2 * (pow(t,3.));
    return point;
}

vec3 getCameraPos() {
    float pathProgress = float(iFrame)/float(120);
    int bezierID = int(mod(pathProgress,3.0));
    float bezierProgress = mod(pathProgress,1.0);
    vec3 position = vec3(0.,1.,0.);
    switch (bezierID) {
        case 0:
        position = bezierPos(vec3(0.,1.,0.),vec3(-0.5,1.,-1.),vec3(-1.,1.,-5.2),vec3(1.,1.,-5.),bezierProgress);
        break;
        case 1: 
        position = bezierPos(vec3(1.,1.,-5.),vec3(3.,1,-5.),vec3(6.,1.,-2.5),vec3(6.,1.,0.),bezierProgress);
        break;
        case 2:
        position = bezierPos(vec3(6.,1.,0.),vec3(6.,1.,1.),vec3(0.5,1.,1.),vec3(0.,1.,0.),bezierProgress);
        break;
    }
    return position;
}

float sphereSDF(vec3 pos,float radius) {
    return length(pos) - radius;
}

float cubeSDF(vec3 pos, vec3 scale) {
    vec3 offset = abs(pos) - scale;
    float unsignedDist = length(max(offset,0.));
    float signedDist = max(max(min(offset, 0.).x,min(offset, 0.).y),min(offset, 0.).z);
    return signedDist + unsignedDist;
}

float torusSDF(vec3 pos, vec2 radii) {
    return length( vec2(length(pos.xz)-radii.x,pos.y) )-radii.y;
}

vec3 rotateYZ(vec3 vector, float angle) {
    vector.zy *= rot2(angle);
    return vector;
}

vec3 rotateXZ(vec3 vector, float angle) {
    vector.zx *= rot2(angle);
    return vector;
}

vec3 rotateXY(vec3 vector, float angle) {
    vector.yx *= rot2(angle);
    return vector;
}

float boolOperation(float subjectDist, float actorDist, int operation) {
    float returnValue = subjectDist;
    switch (operation) {
        case 0:
        //Union
        returnValue = min(subjectDist,actorDist);
        break;
        case 1:
        //Difference
        returnValue = max(subjectDist, -actorDist);
        break;
        case 2:
        //Intersect
        returnValue = max(subjectDist,actorDist);
        break;
    }
    return returnValue;
}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float smoothBoolOperation(float subjectDist, float actorDist, int operation,float k) {
    float returnValue = subjectDist;
    switch (operation) {
        case 0:
        //Union
        returnValue = smin(subjectDist, actorDist, k);
        break;
        case 1:
        //Difference
        returnValue = smin(-subjectDist, actorDist, k);
        break;
        case 2:
        //Intersect
        returnValue = smin(-subjectDist, -actorDist, k);
        break;
    }
    return returnValue;
}

vec4 getDist(vec3 center) {
    vec4 minDist = vec4(0.5,0.5,1.,101.);
    vec4 plane = vec4(0.25, 1.0, 0.3, center.y);
    vec4[] objects = vec4[](
        vec4(0.25,0.5,1.0,mix(cubeSDF(center - vec3(0.0,2.0,7.0),vec3(0.5,1.5,0.5)),sphereSDF(center-vec3(0.0,2.0,7.0),1.0),0.5+0.5*sin(iTime))),
        vec4((1.+cos(iTime))/2.,(1.+cos(iTime+radians(120.)))/2.,1.0,torusSDF(center - vec3(3,0.5,7),vec2(1,0.5)))
    );
    if (minDist.w > plane.w) minDist = plane;
    int objectCount = objects.length();
    for (int i = 0; i < objectCount; i++) {
        if (minDist.w > objects[i].w) minDist = objects[i];
    }
    return minDist;
}

float rayMarch(vec3 ro, vec3 rd) {
	float dO=0.0;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = getDist(p).w;
        dO += dS;
        if(dO>MAX_DIST || dS<HIT_DIST) break;
    }

    return dO;
}

vec3 getNormal(vec3 p) {
	float d = getDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        getDist(p-e.xyy).w,
        getDist(p-e.yxy).w,
        getDist(p-e.yyx).w);

    return normalize(n);
}

float getLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 1);
    vec3 l = normalize(lightPos-p);
    vec3 n = getNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = rayMarch(p+n*HIT_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

vec3 colorFromScene(vec3 rayOrigin, vec3 rayDirection, vec3 lightDir) {
    vec3 returnValue = vec3(0.5,0.5,1);
    //march to scene
    float sceneDist = rayMarch(rayOrigin, rayDirection);
    vec3 landingPosition = rayOrigin + rayDirection * (sceneDist - 0.1);
    vec4 colorContender = getDist(rayOrigin + rayDirection * sceneDist);
    if (colorContender.w < HIT_DIST) {
        // if hit march towards light
        float dif = getLight(landingPosition);
        returnValue = colorContender.xyz * dif;

    }    
    return returnValue;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 cameraPosition = getCameraPos();
    vec2 rotations = vec2(0.,0.);
    float theta = rotations.x;
    float phi = rotations.y;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec3 rayDir = normalize(vec3(uv.xy,1));
    rayDir.zy *= rot2(phi);
    rayDir.zx *= rot2(theta);
    vec3 col = colorFromScene(cameraPosition, rayDir, vec3(1,0,0));
    fragColor = vec4(col,1.0);
}

#define PI 3.1415926
#define PI_HALF (.5*PI)
#define PIx2 (2.*PI)

// 2D rotation matrix
mat2 rotmat(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

/// @brief 返回带有抗锯齿的 2D 圆的距离场
/// @param uv 纹理坐标
/// @param origin 圆心坐标
/// @param radius 圆的半径
/// @param alias 抗锯齿的区间
float smooth_circle(in vec2 uv, in vec2 origin, float radius, float alias)
{
    return /*1.0 - */smoothstep(radius - alias, radius + alias,
                                length(uv - origin));
}

/// @brief 返回默认带抗锯齿的 2D 圆的距离场
/// @param uv 纹理坐标
/// @param origin 圆心坐标
/// @param radius 圆的半径
float circle(in vec2 uv, in vec2 origin, float radius)
{
    return smooth_circle(uv, origin, radius, 0.005);
}

/// 可以分别控制四个角的大小 [z, x
///                         w, y]
float sdRoundBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x > 0.0) ? r.xy : r.zw;
    r.x  = (p.y > 0.0) ? r.x  : r.y;

    vec2 q = abs(p) - b + r.x;
    float d = max(q.x, q.y);
    return min(d, 0.0) + length(max(q, 0.0)) - r.x;
}

/// @brief 绘制心形
float inHeart (vec2 p, vec2 center)
{
    //移动中心
    p = p - center;
    //旋转
    vec2 q = rotmat(45. / 180.*PI) * p;

    float ret = sdRoundBox(q, vec2(.15), vec4(0.03));

    ret = smoothstep(0.01, .012, ret);
    ret = min(ret, circle(p, vec2(.1, .1), .161));
    ret = min(ret, circle(p, vec2(-.1, .1), .161));

    return ret;
}

