vec3 color1 =vec3(255.0, 220.0, 156.0)/255.0; // orange lighting
vec3 bk_color1 =vec3(46, 42, 39)/255.0; //dark brown


// the following helper function is adapted from https://www.shadertoy.com/view/Md23DV
// returns the 1.0 inside the disk area and 0.0 outside the disk area
// and has a outer glow
float disk(vec2 r, vec2 center, float radius, float glow) {
	float distanceFromCenter = length(r-center);
	float outsideOfDisk = smoothstep( radius-glow, radius, distanceFromCenter);
	float insideOfDisk = 1.0 - outsideOfDisk;
	return insideOfDisk;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //relocate center
    vec2 r = 2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y; 
    //relocate mouse to center
    vec2 mouse = 2.0*vec2(iMouse.xy - 0.5*iResolution.xy)/iResolution.y; 

    vec3 col = bk_color1; //base dark brown

    //calculate lights
    vec2 uv = fragCoord/iResolution.xy; // Normalized pixel coordinates (from 0 to 1)
    float x = (iTime/2.0)+uv.x;
    float flicker1 = ((0.3)*sin(5.0*x)+(0.5)*sin(2.0*x)+0.2*sin(0.2*x)+1.0)/2.0;
    float flicker2 = (0.3*sin(0.2*x)+0.2*sin(7.0*x)+0.3*sin(-7.0*x)+1.0)/4.0;
    float flicker3 = (0.2*cos(4.0*x)+(0.5)*cos(0.3*x)+0.2*cos(9.0*x)+1.0)/2.0;
    float flicker4 = (0.03*cos(5.0*x)+(0.75)*cos(2.0*x)+0.09*cos(0.2*x)+1.0)/3.0;

    col += disk(r, mouse, flicker1, flicker1)*color1;
    col += disk(r, vec2(-mouse.x, -mouse.y), flicker2, flicker2)*color1;
    col += disk(r, vec2(2.0*mouse.x, -1.5*mouse.y), flicker3, flicker3)*color1;
    col += disk(r, vec2(-1.7*mouse.x, 0.5*mouse.y), flicker4, flicker4)*color1;

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Cole Peterson (Plento)

// An endless weaving highway, with no exits. Mouse controls zoom.

#define R iResolution.xy
#define m ((iMouse.xy - .5*R.xy) / R.y)
#define ss(a, b, x) smoothstep(a, b, x)
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

#define w_scale 5.
#define car_size vec2(0.07, 0.1)

// Dave Hoshkin
float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float hsh(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
// Standard perlin
float perlin(vec2 p){
    vec2 i = floor(p);
    vec2 f = fract(p);

    float a = hsh(i);
    float b = hsh(i+vec2(1., .0));
    float c = hsh(i+vec2(0. ,1 ));
    float d = hsh(i+vec2(1., 1. ));

    vec2 u = smoothstep(0., 1., f);

    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

// box sdf
float box( in vec2 p, in vec2 b, float r){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0) - r;
}

// is a cell be flipped or not
bool flipped(vec2 id){
    float rnd = hash12(id*200.);
    if(rnd > .5) return true;

    return false;
}


vec3 car(vec3 col, vec2 uv, vec2 uid){
    uv.y *= .6; // stretch in direction of travel so they have more room to move in their cell

    float spd = iTime*.1;

    // Repeated uvs for cars (We only draw one)
    vec2 cm = vec2(0., spd );
    vec2 cv = fract((uv-cm)*w_scale*2.)-0.5;
    vec2 id = floor((uv-cm)*w_scale*2.);
    float idc = floor((uv.x-cm.x)*w_scale);

    // Switch direction "randomly"
    if(cos(idc*2.) > 0.){
        cm = vec2(0., -spd );
        cv = fract((uv-cm)*w_scale*2.)-0.5;
        id = floor((uv-cm)*w_scale*2.);
    }

    if(int(id.y) % 7 == 0) return col; // Leave some cells empty

    // Make cars move down road in somewhat non uniform way
    float t = id.y*114. + id.x*116.;
    vec2 p = vec2(.0, .3*cos(iTime*1. + t));

    // Adjust car position based on side of road
    if(int(id.x) % 2 == 0) p.x -= .2; 
    else p.x += .2;

    cv += p;
    float cars = ss(.01, .0, box(cv, car_size, .03)); // Car mask

    // Car color
    float ct = (id.x*3. + id.y*5.);
    vec3 carCol = .5+.26*cos(vec3(4., 2., 1.)*ct + vec3(3., 4., 7.));
    carCol *= max(ss(-.1, .21, abs(cv.y + .07)), .45);
    carCol += .16*ss(0.055, 0.01, abs(cv.y));

    // Randomly add some variation
    if(cos(ct) > 0.){
        carCol *= max(abs(cos(ct)), .6);
        carCol = mix(carCol, .6*vec3(.75, .85, .99), .7*ss(.3, .2, abs(cv.y*2. - .38)));
    }

    // Shadow under car
    float shdw = max(ss(-.08, .08, box(cv-vec2(0., .01), car_size+vec2(.015, .01), .03)), .3);

    return mix(col * shdw, carCol, cars) + vec3(0., 0., 0.);
}


vec3 road(vec2 uv, float y){
    float rd = ss(.02, .00, abs(uv.x)); // road mask
    rd *= ss(.25, .35, abs(fract(y*14.)-.5)); // road line
    float bdg = ss(.35, .37, abs(uv.x)); // bridge mask
    float shdw = ss(.49, .18, abs(uv.x)); // bridge shadow

    float nse = perlin(uv*45.); // perlin noise
    float rc = clamp(nse*.4, .22, .24); // road color
    float bc = clamp(nse, .46, .5); // bridge color
    return mix(shdw * mix(vec3(rc), vec3(1), rd), vec3(bc), bdg);
}


void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
    vec2 cv = uv;
    uv *= 0.99 + (.5+.5*sin(iTime*.5))*0.77;

    if(iMouse.z > 0.)
        uv *= max(0.3, (m.y+.4)*2.5);

    uv*=rot(-0.3);
    uv += iTime*.1;

    // Road uvs
    vec2 ruv = fract(uv*w_scale)-.5;
    vec2 id = floor(uv*w_scale);

    vec3 col = vec3(1);

    // rotate uv 90 degree based on cell
    float rnd = hash12(id*200.);
    if(flipped(id)){
        ruv = vec2(ruv.y, -ruv.x);
        uv = vec2(uv.y, -uv.x);
    }

    // cell containing current pixel flipped status
    bool me = flipped(id);

    // main color
    col = road(ruv, uv.y);
    col = car(col, uv, id);

    // neighbooring cells flipped?
    bool lft = flipped(id + vec2(-1., 0.));
    bool rgt = flipped(id + vec2(1., 0.));
    bool up = flipped(id + vec2(0., 1.));
    bool dwn = flipped(id + vec2(0., -1.));

    // Add a shadow based on surrounding cells
    if(me && !lft) col *= ss(.78, .12, (ruv.y));
    if(me && !rgt) col *= ss(.78, .12, (-ruv.y));
    if(!me && up) col *= ss(.78, .12, (ruv.y));
    if(!me && dwn) col *= ss(.78, .12, (-ruv.y));

    col = pow(col*1.2, vec3(1.1));

    // intro thingy
    float r = min(iTime, 3.);
    if(iTime < 3.) col *= ss(r, r-.01, length(cv));
    f = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    precision highp float;

    float time = iTime;
    vec2 mouse = iMouse.xy;
    vec2 resolution = iResolution.xy;

	const int n =       25;
	float size =       0.12;
	float width =      0.8;
	float height =      1.;
	float speed =     0.15;
    float blur =      0.2;
    float clmp =      0.5;

    float seedx =     3.01;

	width/=2.;
	height/=2.;
	vec2 uv = ( fragCoord.xy / resolution.xy );
	float ratio = resolution.x/resolution.y;
	vec3 col = 0.5 + 0.5*cos(time+uv.xyx+vec3(0,2,4));
	float dis = 1.0;
	vec2 pos;
	uv.x *= ratio;

	for (int i=0;i<n;i++){

            pos.x=sin(float(i)*seedx)*width+0.5;
            pos.y=(float(i)/float(n))*abs(pow(cos(float(i)*12.8+time*speed*(1.+float(i)/111.)),3.))*height*2.;
            pos.x*=ratio;
            dis *= smoothstep(size-blur,size+blur,length(uv-pos));		

    }
    dis = step(clmp,dis);
    if (mod(float(int(time/10.)),2.)==0.){
        fragColor = vec4( col, 1.0 )*(1.-dis);
    }
    else {
        fragColor = vec4( col, 1.0 )*dis;
    }

}

