// Wowie! by Kristian Sivonen (ruojake)
// CC BY-SA 4.0 (https://creativecommons.org/licenses/by-sa/4.0/)

#define saturate(x) clamp((x), 0., 1.)

struct seg
{
    vec2 p0, p1;
    float len, r, c;
};

float sm(float a, float b, float q)
{
    float d = saturate((b - a) / q + .5);
    float m = -.125 * q * d * (1. - d);
    return mix(a, b, d) - m;
}

float line(vec2 p, float r, vec2 a, vec2 b)
{
    vec2 a2b = b - a,
         a2p = p - a;
    return length(a2p
                - saturate(dot(a2p, a2b) / dot(a2b, a2b))
                * a2b) - r;
}

void solveIK(vec2 root, vec2 target, inout seg segs[6])
{
    #define P0 segs[i].p0
    #define P1 segs[i].p1

    for(int i = 5; i > -1; --i)
    {
        P1 = target;
        P0 = P1 + normalize(P0 - P1) * segs[i].len;
        target = P0;
    }

    vec2 dir = normalize(segs[0].p1 - segs[0].p0);

    for(int i = 0; i < 6; ++i)
    {
        P0 = root;

        vec2 newDir = normalize(P1 - P0);
        float t = dot(dir, newDir) * -.5 + .5;
        t *= segs[i].c;

        newDir = normalize(mix(newDir, dir, t));

        P1 = P0 + newDir * segs[i].len;

        dir = newDir;
        root = P1;
    }
    #undef P0
    #undef P1
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    seg segs[6] = seg[]
    (
        seg(vec2(-.1),  vec2(.01), .1, .025,  1. ),
        seg(vec2(.02), vec2(-.01), .1, .02,   1. ),
        seg(vec2(-.02), vec2(.03), .1, .015,  .75),
        seg(vec2(.04), vec2(.03), .1, .0125, .5 ),
        seg(vec2(.04), vec2(-.05), .1, .01,   .25),
        seg(vec2(-.06), vec2(.05), .1, .0075, 0. )
    );

    vec2 target = vec2(sin(iTime), sin(iTime * 1.333)) * .4;
    vec2 root = vec2(-.5);

    float d = 1000.;

    solveIK(root, target, segs);
    solveIK(root, target, segs);

    for(int i = 0; i < 6; ++i)
    {
        d = sm(d, line(uv, segs[i].r, segs[i].p0, segs[i].p1), -.1);
    }

    vec2 flip = vec2(-1, 1);

    solveIK(root, target * flip, segs);
    solveIK(root, target * flip, segs);

    for(int i = 0; i < 6; ++i)
    {
        d = sm(d, line(uv * flip, segs[i].r, segs[i].p0, segs[i].p1), -.1);
    }

    solveIK(root * flip, -target, segs);
    solveIK(root * flip, -target, segs);

    for(int i = 0; i < 6; ++i)
    {
        d = sm(d, line(-uv, segs[i].r, segs[i].p0, segs[i].p1), -.1);
    }

    solveIK(root, -target, segs);
    solveIK(root, -target, segs);

    for(int i = 0; i < 6; ++i)
    {
        d = sm(d, line(-uv, segs[i].r, segs[i].p0, segs[i].p1), -.1);
    }

    vec3 col = vec3(.8,.55,.05);


    float blush = smoothstep(.2, 0., length(vec2(.4, 0) - abs(uv + vec2(0,.1))));
    col = mix(col, vec3(1,.2,.2), blush * .3);


    float eyeball = length(abs(uv) - vec2(.2, 0)) - .1;

    vec2 posPup = vec2(-.2, 0) + normalize(target - vec2(-.2,0)) * .07;
    float pupL = length(uv - posPup) - .01;

    posPup = vec2(.2, 0) + normalize(target - vec2(.2,0)) * .07;
    float pupR = length(uv - posPup) - .01;

    float outline = abs(eyeball) - .002;
    outline = smoothstep(fwidth(outline), 0., outline);
    eyeball = smoothstep(fwidth(eyeball), 0., eyeball);
    pupL = smoothstep(fwidth(pupL), 0., pupL);
    pupR = smoothstep(fwidth(pupR), 0., pupR);

    col = mix(col, vec3(.9), eyeball);
    col *= 1. - outline;
    col *= 1. - max(pupL, pupR);


    float mouth = line(abs(uv + vec2(0.,.25)), .07, vec2(0), vec2(.2, 0) * (length(target) * 2.));
    mouth = smoothstep(0.,fwidth(mouth), mouth);
    col *= mouth;


    float ball = length(uv - target) - .04;
    outline = abs(ball) - .002;
    outline = min(outline, abs(min(ball, d)) - .001);

    d = smoothstep(fwidth(d), 0., d);
    ball = smoothstep(fwidth(ball), 0., ball);
    outline = smoothstep(0., fwidth(outline), outline);
    col = mix(col, vec3(.6,.45,0.), d);
    col = mix(col, vec3(1,.4,.8), ball) * outline;

    col *= smoothstep(1., .3, length(fragCoord / iResolution.xy - .5));
    fragColor = vec4(col,1.0);
}

