// needs gamma correction

#define AA                       // uncomment to turn on anti aliasing
#define RADIAL_AA
#define FOUR_ROOK_AA

#define ITS 7                      // iteration count
#define SPEED 7e-2                 // global speed
#define ZOOM 100.                  // zoom level
//#define ORIGIN                   // highlight origin
//#define DEBUG                    // flash any isnan red

// performs two 2d offset rotations moving field origin in trefoil orbit
#define ROTATE(a) mat2(cos(a),-sin(a),sin(a),cos(a))
//#define ROTATE(a) 1.   // no rotation
//#define ROTATE(a) 2.   // see the bigger picture

#define iR iResolution

void calcImage( out vec4 k, in vec2 p )
{
    p*=ZOOM;

    float wt = iTime*SPEED;        // wall time

         wt += 2.5;                // starting time offset

          p *= .05;                // unzoom compensation
          p -= iR.xy/2.;           // center coords
          p += vec2(-1e3);         // offset
          p *= ROTATE(wt);         // outer rotation

     vec2 c  = p-iR.xy/2.;         // centered coords
          c /= iR.y;               // aspect corrected/scaled
          c *= ROTATE(-wt*3.);     // inner rotation
          c += vec2(1);            // movement

     float t = wt+.2;              // local time

     vec4  P = vec4(2,3,5,7),      // primes
          vx =  (P*c.x),           // x space component
          vy =  (P*c.y);           // y space component

           k = vec4(0,0,0,1);      // initial condition

    for(int i=ITS; i>0; i--)      // iterate mat4 pipeline
    {
      // main transform
      mat4 M = mat4(vx,vy,      
                    t*P,
                    k*(c.xyyx-1.5));

      vec4 l = k;
      k *= M;
      k = sin(k/9.);

      #ifdef DEBUG
          if(any(isnan(k))) k = l;
      #endif
    }

//    k = vec4(.5/length(k));    // grayscale
//    k = normalize(k);

    // show origin
    #ifdef ORIGIN
        k.g += smoothstep(.1,.0,length(c));
    #endif

    // flash any isnan values red
    #ifdef DEBUG
       if(any(isnan(k))) k = vec4(mod(iTime,1.),0,0,0);
    #endif
}

void mainImage( out vec4 k, in vec2 p )
{
// shader is pretty much torture test for aliasing
#ifdef AA
    vec4 s = k = vec4(0);

#ifdef RADIAL_AA
    for (float a=6.18; a>0.; a-=1.2){
        calcImage(s,p+(a/9.)*vec2(cos(a), sin(a)));  
        k = mix(k,s,.5);
    }
#endif

#ifdef FOUR_ROOK_AA
    // apply four rook anti-aliasing 
    //   improves but doesn't eliminate issue in high contrast/freq. areas
    float d1 = .125, d2 = .375;
    calcImage(s,p+vec2( d1, d2));  k = mix(k,s,.5);
    calcImage(s,p+vec2( d2,-d1));  k = mix(k,s,.5);
    calcImage(s,p+vec2(-d2, d1));  k = mix(k,s,.5);
    calcImage(s,p+vec2(-d1,-d2));  k = mix(k,s,.5);
#endif

#else
    calcImage(k,p);
#endif
}

//change these constants to get different patterns!
#define c2 0.0
#define c1 vec4(3.0+c2,2.5+c2,1.5,0)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);    
    vec3 col;  
    float t1 = 4.5, offset = .5;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale2 = 1.5;
    float bend = 1.;
    float scale = c1.z;
    for(int i=0;i<3;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 9; k++){    
            uv /= -scale2;
            t2 = triangle_wave(uv.yx-offset,scale);
            t3 = triangle_wave(uv,scale);
            uv.yx = t2/bend+t3*bend;
            bend *= -1.;
            bend -= 1./3.-col.x;
        }
        //scale /= 1.+(scale2)*col.x/(8.);
        scale2 -= (col.x-1.)/(4.);
        col = col.yzx;
        col[0] = abs((uv.x)-(uv.y));
    }

    fragColor = vec4(vec3(col*2.),1.0);

}

#define WIDTH 3.0
#define FCOLOR vec3(1.0, 0.0, 0.0)
#define BCOLOR vec3(1.0)
#define DELTA 0.001
#define CELL 50.0

float func(float x) {
    return sin(x * 10.0 + iTime * 3.0) / 2.5 + 0.5;
}

float grad(float x) {
    return (func(x + DELTA) - func(x)) / DELTA;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;
    vec3 colorCell = vec3(min(mod(fragCoord.x, CELL), mod(fragCoord.y, CELL)));
    vec3 color = mix(FCOLOR, BCOLOR, smoothstep(0.0, WIDTH / iResolution.y, abs((func(uv.x) - uv.y) / length(vec2(grad(uv.x), 1)))));
    color = min(colorCell, color);
    fragColor = vec4(color, 1.0);
}

// simple textured line test

// returns distance, u, v
vec3 TexturedLine( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
    q = mat2(d.x,-d.y,d.y,d.x)*q;
    vec2 qq = (q)-vec2(l,th)*0.5;
    q = abs(q)-vec2(l,th)*0.5;
    float dd =  length(max(q,0.0)) + min(max(q.x,q.y),0.0);
    float u = abs(qq.x)/l;
    float v = abs(qq.y)/th;
    return vec3(dd,u,v);
}

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}    

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float size = 0.3+(sin(iTime)*0.25);

    vec2 p1 = vec2(0.0,size)*rotate(iTime);
    vec2 p2 = vec2(size,0.0)*rotate(iTime);

    vec3 res = TexturedLine(uv,p1,p2,0.1);


    float d = step(res.x,0.0);
    if (iMouse.z>0.5)
        col = d*texture(iChannel0, -res.yz ).xyz;    
    else
        col = d*vec3(res.y,res.z,0.0);

    fragColor = vec4(col,1.0);

}

//difficulty(0-1) lower is more difficult
float diff=.4;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 uv = vec3((fragCoord-.5*iResolution.xy)/iResolution.y,0.f); 

    //Shift center
    vec2 center = vec2(
    (sin(iTime*.7+iTime*.0002))*.5*cos(iTime*0.03),
    (cos(iTime)*(.2+iTime*.0001))*cos(iTime*0.07));
    uv.xy+=center;
    uv.z = length(uv);

    vec2 mousePos = (iMouse.xy-.5*iResolution.xy)/iResolution.y + center; 

    //beat frame
    vec3 beat = vec3(smoothstep(.08,.11,uv.z)*smoothstep(.11,.1,uv.z));

    float circles,vbar,hbar;
    hbar=sin(.23*iTime-uv.x-iTime*.0001);
    vbar=cos(.33*iTime+uv.y-.7+iTime*.0001);

    //closing bars
    beat += vec3(pow(1.f-abs(hbar),27.f));
    beat += vec3(pow(1.f-abs(vbar),27.f));

    //closing rings
    float speed = (1.f+cos(iTime*.6f)*.001f+.5f)+iTime*.0003;
    circles=sin(speed*iTime+uv.z);
    beat += vec3(pow(1.f-abs(circles),15.f));    
    circles=min(abs(circles),min(abs(hbar),abs(vbar)));

    if (beat.x>1.01f && uv.z<.11){
        if(iMouse.z>.1 && length(mousePos-uv.xy)<.19){            
            beat*=vec3(0,1,0);
            beat*=beat;
        }else{
            beat*=vec3(1,0,0);
        }
    }
    if(circles>diff && iMouse.z>.1 && uv.z<.11){            
        beat*=vec3(1,0,0);
    }

    // Output to screen
    fragColor = vec4(beat,1.0);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.0,0.3,0.4) + 0.1*cos(iTime+uv.xyx+vec3(0,1,1));

    vec4 ns = texture( iChannel0, uv );

    float gltr1 = fract(iTime/121.0);
    float gltr2 = fract(iTime/1922.0);
    float gltr3 = fract(iTime/2363.0);
    float gltr4 = fract(iTime/3435.0);
    float glit1 = smoothstep( gltr1-0.001, gltr1, ns.x ) * smoothstep( gltr1+0.001, gltr1, ns.x );
    float glit2 = smoothstep( gltr2-0.001, gltr2, ns.y ) * smoothstep( gltr2+0.001, gltr2, ns.y );
    float glit3 = smoothstep( gltr3-0.001, gltr3, ns.z ) * smoothstep( gltr3+0.001, gltr3, ns.z );
    float glit4 = smoothstep( gltr4-0.001, gltr4, ns.w ) * smoothstep( gltr4+0.001, gltr4, ns.w );

    col = max( max( max(vec3(glit1),vec3(glit2)), max(vec3(glit1),vec3(glit2)) ), col );

    // Output to screen
    fragColor = vec4(col,1.0);
}

