#define PI 3.1415
#define ITERATIONS 25.

float ran21(vec2 uv) {
    return fract(cos(dot(cos(uv.x*uv.y)-32.2,tan(uv.x/uv.y)-23.5)*1322.24)*432122.62);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;
    vec2 uvb = uv;

    vec3 col = vec3(0);


        vec2 gv = fract(uv)-.5;
        vec2 id = floor(uv);


    for(float i=1.;i<=ITERATIONS;i++) {
        float cur = i/ITERATIONS;
        uv = uvb;

        uv *= (ITERATIONS*ITERATIONS/(ITERATIONS+i))-(cur*1.)/(length(uvb/uv));
        uv.y += iTime*0.6;
        float halftime = iTime*.3;

        gv = fract(uv)-.5;
        id = floor(uv);


        float mul = mod(id.x+id.y,25./2.)>=1.5 ? -.675 : -ITERATIONS/i;


        float modval = mod(iTime/1000., mul*PI);

        gv.x *= (ran21(id)>(.5)) ? -1. - mod(modval+id.y, mod(id.y, uv.x)) * mul - abs(cos( halftime/gv.x - modval/gv.y)* modval) : 
                                    1. + mod(modval-id.y, mod(id.y, uv.x)) * mul - abs(sin( iTime/gv.x + modval/gv.y)* modval);

        // gv.x *= (ran21(id)>(.5)) ? -1.- abs(sin(iTime+gv.x+modval*PI)*modval) : 1.+ abs(cos(-iTime-gv.x-i*PI)*modval);  

        vec2 ruv = gv-sign(gv.x+gv.y+.001)*.5;
        float tile = smoothstep(.01,-.01,abs(length(ruv)-.5)-.1);
        float rot = atan(ruv.x,ruv.y)/PI;


        float h = smoothstep((ITERATIONS/cur+.1)/gv.x,cur,sin(abs(rot*cur*mul+iTime)*PI)*.25+.75);

        col.b -= (h*tile*cur)/(h/i);
        col.b *= .85-((h/i)/(h*tile*cur));
        col.g *= .95-((col.r + col.g )/(h*i));


        col = max(col,h*tile*cur);
    }

    fragColor = vec4(col,1.0);
}

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

#define pi 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv.y += 0.08 * thc(2., 10. * mlength(uv) +
    + iTime);
    //uv.y += 0.01 * cos(100. * mlength(uv) + iTime);
    float r = length(uv);
    float a = atan(uv.y/uv.x);

    //uv = fract(uv / r);// + ths(1.,0.5 * iTime);
   // uv = vec2(cos(0. * r + mlength(uv) + iTime),
    //          sin(0. * a + mlength(uv) + iTime));
    uv.x = r; // * 20.;
    uv.y = a * 10.;

    float time = iTime;

    float sc = 0.5;
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float d = 0.1 * (0.5 + 0.5 * cos(iTime)) + length(fpos);
    float s = smoothstep(-0.1,0.1,0.5-d) - smoothstep(-0.1,0.1,0.42 - d);
    s *= (0.5 + 0.5 * cos(uv.x + uv.y - iTime)) * h21(ipos + floor(length(ipos) * 100. + 0.2 * time));

    vec3 col = 2. * s * pal(time + 10. * r + 0.5 * s, vec3(1.), vec3(1.), vec3(1.), vec3(0.,0.33,0.66));

    // Output to screen
    fragColor = vec4(col,1.0);
}

