#define nsin(x) (.5+.5*sin(x))
#define pmod(x,j) (mod(x,j)-.5*(j))

#define MISS(x) (x>1000.)


float sphere(vec3 p, float r) { return length(p)-r; }
float box(vec3 p, vec3 a) {
  vec3 q = abs(p)-a;
  return length(max(q, 0.)) + min(max(q.x,max(q.y,q.z)),0.);
}

float boxle(vec3 p, float r, float q) {
  return max(box(p, vec3(r)),-sphere(p,q*r));
}

float S(vec3 p) {
  float gt = time*2.;
  gt = (gt +.2*sin(gt))*1.5;
  //float gtm = pmod(gt, .1);
  p.xy *= rot(time*2.);
  //p.yz *= rot(gt*2.);
  //p+=vec3(.2,.2,2);
  float rad = fract(gt);
  float root_id = floor(gt);
  float S = 1001.;

  float ft=1.;

  for (float i=0.;i<=2.;i+=1.){
    float id = root_id + i;
    float r=rad+i;
    p.xz *= rot(r*(.35+.2*sin(gt)));
    p.xy *= rot(r*.1);
    p.xy *= rot(r*.05);

    S = min(S, boxle(p, r, 1.+.07*r*r*r));
  }



  return S+max(0.,-S)*.2;
}


float ray(vec3 p, vec3 dir) {
  float d=0.;
  for(int i=0;i<50;i++){
    float c=S(p+d*dir);
    d+=c;
    if (c<.1) return d;
    if (MISS(d)) return d;
  }

  return d;
}

vec3 normal(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(1e-3);
  return normalize(S(p)-vec3(S(k[0]), S(k[1]),S(k[2])));
}

vec3 sat(vec3 p, float amt) {
    vec3 mid = vec3((p.x+p.y+p.z)/3.);
    vec3 side = p - mid;

    return mix(mid, side,amt);

}

