  x-=.1;

  if (x<1.) { HUE=vec3(0, p); return px(T1,p); }
  if (x<2.) { HUE=vec3(p,0); return px(T2,p); }
  if (x<3.) { HUE=vec3(p.y,0,p.x);return px(T3,p); }
  return px(T4,p);
}

#define tri(x) (2.*abs(.5-fract(x)))
#define gmix(A,B,Q) (A * pow(B/A, Q))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
     uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 C;

    float t = time;
    t = tri(.1*t+.3);
    //t -= nsin(t)*pmod(t, 1./200.);

    float RES=mix(6., 88., t*t);


    vec2 px_aln = uv;
    px_aln *= RES;
    px_aln = floor(px_aln);
    px_aln /= RES;

    vec3 o = vec3(0,0,-5);
    vec3 dir = normalize(vec3(px_aln,1));

    float dist = ray(o,dir);
    float l = light(o,dir,dist);

    if (MISS(dist)) {
      C=pxsel(nsin(time), (uv+t)*RES)+vec3(1);

      C/=(RES*.2);
    }
    else {
      float pix = pxsel(light(o,dir,dist),RES*(uv-px_aln));
      C = vec3(rot(time)*uv+1.,0)*pix;
      C.g *= .5;
      C -= HUE;

    }

    C = C.zxy;
    //C = sqrt(C);

    //C = sqrt(C);

    // Output to screen
    fragColor = vec4(sqrt(C),1.0);
}

//Voronoz by Kastorp
//----------------------------

vec3 erot(vec3 p, vec3 ax, float ro) { //@Blackle
    return mix(dot(p,ax)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

float xt( in vec3 p, in float sdf, in float h ){//@iq
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

vec2 hash22(vec2 p) //@Dave_Hoskins 
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float voro(vec2 p){
    float  d=1e5;
    vec2 s=step(vec2(.5),fract(p));    
    for(float i=-1.+s.x;i<2.+s.x;i++)for(float j=-1.+s.y;j<2.+s.y;j++) d=min(d,length(fract(p)-vec2(i,j)-.5+ 
        hash22(floor(p)+vec2(i,j) )
        //mix( hash22(floor(p)+vec2(i,j) ), hash22(.5 +floor(p)+vec2(i,j) ),.5+.5*sin(iTime*.5))
        ));
    return d;
}

float kk;
float scene(vec3 p){
     if(p.z>.65) return (p.z-.6); //+10% framerate
    float z =.3, h=iMouse.z>0.? .2-iMouse.x/iResolution.x*.1:.1,  d=1e5, s=voro(p.xy/z), tk=iMouse.z>0.?-1.+iMouse.y/iResolution.y*2.:0.;    
    for( float k = -.5+fract(iTime)*h;k<.5;k+=h) { 
       float d1=  -h*.1*(1.1-tk)+xt(p+vec3(0,0,k*.2), -.0001+abs(s-k-.5)*z ,.5);
       if(d1<d){kk=k;d=d1;}
    }
    return d;
}

vec3 norm(vec3 p) {//@blackle
    mat3 k = mat3(p,p,p)-mat3(0.001);    
    return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}
// IQ Occ
float getAmbiantOcclusion(vec3 p, vec3 n, float k)
{
    const float aoStep = 0.1; 
	float occl = 0.;
    for(int i = min(iFrame,0); i < 6; ++i)
    {
        float diff = float(i)*aoStep;
        float d = scene(p + n*diff);
        occl += (diff - d) * pow(2., float(-i));
    }
    return min(1., 1. - k*occl);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 cam = normalize(vec3(1.5,uv));
    vec3 init = vec3(1.,0,3.)+vec3(0,.2,0)*iTime;

    float yrot =.8;
    cam = erot(cam, vec3(0,1,0), yrot);
    init = erot(init, vec3(0,1,0), yrot);

    vec3 p = init;
    bool hit = false;
    for (int i = 0; i < 80 && !hit; i++) {
        float dist =scene(p); 
        hit = dist*dist < 1e-6;
        p+=dist*cam;
        if (dist>5. ||hit) break;
    }
    float k=kk;
    vec3 n = norm(p);
    vec3 ld= normalize(vec3(0,1,.5));

    vec3 base = (.5+sin(vec3(3,2,1)*k*4.)*.5);
    float diff = pow(dot(n, ld) * .5 + .5,2.0);
    float ao = getAmbiantOcclusion(p, n, 40.0);
    float spec= pow(max(dot(-cam, reflect(-ld, n)), 0.0), 32.0);     

    bool sh =false;
    if(hit){

        p+=n*.002;
        for (int i = 0; i < 50 && !sh; i++) {
            float dist = scene(p);
            sh = dist*dist < 1e-6;
            p+=dist*ld;
            if (dist>2. ||sh) break;
        }
    }
    vec3 col = (base *diff) * (sh?.4:1.) +(sh?0.:spec) ;
    col = clamp(col, 0., 1.);


    fragColor = smoothstep(-.02,1.05,sqrt(vec4(col,1))) * (1.- dot(uv,uv)*.5);
}

#define PI 3.141592
#define TAU 2.0*PI


void amod(inout vec2 p, float c) {
    float m = TAU / c;
    float a = mod(atan(p.x, p.y)-m*.5, m) - m*.5;
    p = vec2(cos(a), sin(a)) * length(p);
}

void mo(inout vec2 p, vec2 d) {
    p = abs(p) - d;
    if(p.y>p.x)p=p.yx;
}

mat2 r2d(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

float smooth_stairs(float x) {
    return tanh(5.*(x-floor(x)-0.5))/tanh(2.5)*0.5+floor(x)+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    //vec2 mouse = iMouse.xy / iResolution.xy;
    uv.y = abs(uv.y);

    uv *= 3.0;
    //amod(uv, 5.5+sin(iTime*0.2)*2.5);
    float nrays = abs(mod(iTime*0.3, 10.)-5.);
    amod(uv, 8.-smooth_stairs(nrays));

    mo(uv, vec2(1.2+sin(iTime*0.3), 0.6+sin(iTime*0.5)*0.3));
    uv *= r2d(PI/12.-PI/8.*mod(iTime*0.2, 16.));
    mo(uv, vec2(1.1+sin(iTime*0.5)*0.7, 0.4+0.5*1.5));
    uv *= r2d(PI/6.-mod(iTime*0.25, 12.0)*PI/6.0);
    mo(uv, vec2(.7+sin(iTime*0.45)*0.2, .2));

    //uv *= 10.;
    uv *= 30.;
    //float l = min(abs(uv.x), abs(uv.y));
    //float l = max(abs(uv.x), abs(uv.y));
    float l = abs(uv.x) + abs(uv.y);
    float d = sin(l) - .3;

    d = smoothstep(0., fwidth(d), d);
    fragColor = vec4(sqrt(d),sqrt(d),sqrt(d),0);
}

