    //vec3 random2 = hash31(1.+floor((iTime)/10.0+uv.x));
    float t2 = floor((iTime/4.+uv.x)/10.0);
    vec3 random1 = (hash31(3.+t2)-.5)/8.;
    vec3 random2 = (hash31(4.+t2)-.5)/8.;
    vec3 random3 = (hash31(3.+t2)-vec3(.5))/4.;
    vec3 random4 = (hash31(4.+t2)-vec3(.5))/4.;

    float offset = .5;
    float scale2 = 1.5;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        //float scale1 = 1.0;
        for(int i=0;i<3;i++)
        {
            for(int k = 0; k < 6; k++){       
                uv /= scale2;
                //float bend = abs(fract((uv.x/3.))-.5)/3.+1.;
                //float bend = 1.+random3[k%3];
                //float bend = 1.+1./3.;
                float bend = 1.;
                uv.yx = -triangle_wave(uv.yx-offset,scale)/bend+triangle_wave(uv,scale)*bend;
                uv += vec2(random1[k%3],random2[k%3]);                
            }


            //offset += .5;
            scale /= 1.+(scale2)*col.x/(8.);
            scale2 -= (col.x-1.)/4.;


            col[c] = abs(col[c] - abs((uv.x)-(uv.y)));
            col = col.yzx;
            //random2 = col - random2;


        }
	}

    fragColor = vec4(vec3(col*2.),1.0);

