/**
    License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

    MultiScale Truchet [learning]
    11/6/21 @byt3_m3chanic

    https://christophercarlson.com/portfolio/multi-scale-truchet-patterns/

    My original attempt at this >> https://www.shadertoy.com/view/fl33zn 

    I had some gaps I couldn't figure out so I turned to @Shane's example 
    to understand how to create layered trickery required for this. 

    @Shane >> Quadtree Truchet ::  https://www.shadertoy.com/view/4t3BW4

    As the pattern scales down the colors alternate, add in that each 
    tile can have "wings" that overlap to create the solid positive
    and negative spaces around the scaling neighbors.

    I added an extra depth dimension to the quadtree and figured out
    how to layer that in (i think?). I kept the patterns mostly simple
    just to figure things out.

    You should also look at this to get an idea of quadtrees in general.
    @Shane >> Random Quadtree  ::  https://www.shadertoy.com/view/llcBD7

*/

#define R          iResolution
#define M          iMouse
#define T          iTime
#define PI         3.14159265359
#define PI2        6.28318530718

mat2 rot (float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p){ return fract(sin(dot(p,vec2(26.34,45.32)))*4324.23); }

// @Shane 2/2 hash.
vec2 hash22(vec2 p) { 
    // Faster, but doesn't disperse things quite as nicely. However, when framerate
    // is an issue, and it often is, this is a good one to use. Basically, it's a tweaked 
    // amalgamation I put together, based on a couple of other random algorithms I've 
    // seen around... so use it with caution, because I make a tonne of mistakes. :)
    float n = sin(dot(p, vec2(57, 27)));
    return fract(vec2(262144, 32768)*n);
}

float bkptrn(vec2 p, float res) {
    p *= res/10.;
    vec2 id = floor(p*.5)-.5;
    float hatch = clamp(sin((p.x - p.y)*PI*3.)*3. + 0.25, 0., 1.);
    return clamp(hatch,.3,.7);
}

void mainImage( out vec4 O, in vec2 F ) {

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec2 suv = uv;

    uv += T*.040; 

    // change scale if window is bigger?
    // for pattern things you can get a
    // better - detailed view
    vec2 vuv= R.x>1000.?uv*4.:R.x>800.?uv*6.:uv*2.;

    float px = fwidth(vuv.x*2.);

    // threshold values
    const vec2 rndX[4]=vec2[4]( vec2(.5, .25), vec2(.5, .25), vec2(.5, .75), vec2(.5, 1));
    // Distance field values.
    vec4 d=vec4(1e5), d2=vec4(1e5), d3=vec4(1e5);
    float level=1.;

    // Main loop and neighbor checking from @Shane's shader.
    // Comments are reduced to the main points I wanted to
    // remember - please see his shader for full comments and
    // explanations https://www.shadertoy.com/view/4t3BW4

    for(int k=0; k<4; k++){

        vec2 id = floor(vuv*level);

        for(int j=-1; j<=1; j++){
            for(int i=-1; i<=1; i++){

                // neighboring cell ID.
                vec2 neighbors = vec2(i,j);
                vec2 rnd = hash22(id+neighbors);

                // tiles need to be laid down from largest to smallest. 
                // If a large tile has taken up the space, you need to
                // check on the next iterations and skip.

                vec2 rnd2 = hash22(floor((id+neighbors)/2.));
                vec2 rnd4 = hash22(floor((id+neighbors)/4.));
                vec2 rnd8 = hash22(floor((id+neighbors)/8.));

                // If the previous large tile has been rendered, continue.
                if(k==1 && rnd2.y<rndX[0].y) continue;
                // If any of the two previous larger tiles have been rendered, continue.
                if(k==2 && (rnd2.y<rndX[1].y || rnd4.y<rndX[0].y)) continue;
                // If any of the three previous larger tiles have been rendered, continue.
                if(k==3 && (rnd2.y<rndX[2].y || rnd4.y<rndX[1].y  || rnd8.y<rndX[0].y )) continue;

                // threshold
                if(rnd.y<rndX[k].y) {

                    vec2 p = vuv -(id+.5+neighbors)/level;

                    // square to mask off tiles.
                    float square = max(abs(p.x), abs(p.y)) - .5/level;

                    // TILE COLOR ONE.
                    // Standard Truchet rotation and flipping.
                    if(rnd.x<rndX[k].x) p.xy = p.yx;
                    if(fract(rnd.x*57.5 + .35)<rndX[k].x) p.x = -p.x;

                    // Four circles on the midway points of the grid boundary
                    vec2 p2 = abs(vec2(p.y - p.x, p.x + p.y)*.7071) - vec2(.5, .5)*.7071/level;
                    float c3 = length(p2) - .5/3./level;

                    // Truchet arc one.
                    float c = abs(length(p - vec2(-.5, .5)/level) - .5/level) - .5/3./level;
                    float c2 = abs(length(p - vec2(.5, -.5)/level) - .5/level) - .5/3./level;

                    // Line variant @Shane
                    if(fract(rnd.x*113.467 + .51)<.15) c = abs(p.x) - .5/3./level;
                    if(fract(rnd.x*123.853 + .49)<.15) c2 = abs(p.y) - .5/3./level;

                    float truchet = min(c, c2);

                    c = min(c3, max(square, truchet));
                    // Tile color one.
                    d[k] = min(d[k], c);
                    // for extra decoration
                    d3[k] = min(d3[k], c);

                    // TILE COLOR TWO.
                    p = abs(p) - .5/level;
                    float l = length(p);

                    // Four circles at the grid vertices and the square.
                    c = min(l - 1./3./level, square);
                    //c = max(c, -truchet);
                    //c = max(c, -c3);

                    // Tile color two.
                    d2[k] = min(d2[k], c); 

                }
            }
        }    

        level*=2.;
    }

    // layerd mixdown as each iteration is stored in xyzw
    d.x = max(d2.x, -d.x);

    float dz = abs(abs(abs(min(d3.w,d3.z))-.05)-.02)-.01;
    d.x = min(max(d.x, -d2.y),  d.y);
    d.x = max(min(d.x,  d2.z), -d.z);
    d.x = min(max(d.x, -d2.w),  d.w);
    // reuse of d.w - making border
    d.w=abs(d.x)-.0025;

    float dm = smoothstep(px, -px,d.x);
    float dn = smoothstep(-px, px,d.x);
    dz = smoothstep(px, -px, abs(dz)-.0025);

    float ptrn = max(dn,bkptrn(vuv,125.));
    vec3 C = mix(vec3(.045),vec3(.6),clamp((suv.y+.25)*.5,0.,1.))*ptrn;

    // color gradient 
    vec3 clrA = mix(vec3(0.902,0.380,0.098),vec3(0.137,0.580,0.804),clamp((suv.y+.45)*1.25,0.,1.));
    vec3 clrB = vec3(0.706,0.659,0.941);
    vec3 clrC = vec3(0.031,0.541,0.608);

    // extra decoration
    C = mix(C, clrA, clamp(dz-dm,0.,1.));
    C = mix(C, clrC, smoothstep(px, -px, abs(d.y+.075)-.002));
    // background
    C = mix(C, C*clrA, smoothstep(.05+px, -px, d.x));
    // outlines
    C = mix(C, clrB, smoothstep(px, -px, d.w));

    // Gamma and output
    C = pow(C, vec3(.4545));        
    O = vec4(C,1.0);
}

// fork of Angular Lighting 2 https://shadertoy.com/view/Nlc3z7
// fork of AngularSeg https://shadertoy.com/view/ssyXzy

// obtains view space normal estimate from cross product.
// no actual marching or root solving!
// lighting is imperfect but fast.

void mainImage(out vec4 o, vec2 p)
{
    vec2 r = iResolution.xy; float ry = min(r.x, r.y);
    vec3 d = normalize(vec3((.5*r - p) / ry, -1)), n = -.1*d;
    float N = 18., poof = .5, cov = .0, z = 9e9, ci = 0.; 
    for (float i = 1.; i > 1./N; i -= 2./N) { // brute force example toy
        // points in view coordinates, skipping transformation
        // animate point pos relative eye
      #define P(t) vec3(0,0,-9) + 15.*poof/N * sin(vec3(9,8,7) * ((t) + .2* iTime)) + 3.*sin(vec3(0,11,7) + .12*iTime + vec3(3,4,5) * .08*iTime + 6.28*(t))
        vec3 a = P(i), b = P(i - 1./N)
        , dXa = cross(d, a), dXb = cross(d, b);
        vec3 m = dXb - dXa;
        // when the lighting looks wrong, this is why:
        a = mix(b, a, clamp(dot(dXb, m) / dot(m, m), 0., 1.)); // now "closest" point on seg
        // FIXME surely there's some redundancy in here that could be exploited
        vec3 v = cross(d, a);
        float aa = dot(a, a) // squared distance to point
        , vv = dot(v, v);
    //    if (dot(a, d) > 0.) // only if not behind camera! not needed here.. they never go behind camera
        if (vv < poof*poof) { // only if hit something!
            // FIXME too many sqrt!
            float vl = sqrt(vv)
            , ve = vl - poof
            , al = sqrt(aa);
            vec3 n3 = v / poof;
            n3.xy = -n3.yx * vec2(-1,1); // un-cross-ify HACK only works in view space?
            n3.z = sqrt(max(0., 1. - dot(n3.xy, n3.xy))); // compute normal
            float z2 = al - n3.z * poof
            , focpl = 10.
            , defocus = min(1., abs(al - focpl) * .05)
            , c2 = min(1., -ve * r.y / al // antialias edge coverage
                / (1. + defocus * ry/35.) // "depth of field" blur
                );
            if (z2 < z) { // nearer? blend over bg
                ci = i;
                z = z2;                
                n = mix(n, n3, c2);
            } else { // blend behind fg
                c2 = max(0., c2);                
                n = mix(n, n3, (1.-cov) * c2);
            }
            cov = mix(cov, 1., c2);
        }
    }
    n = normalize(n);
    vec3 cp = z * d;
    vec3 lp = vec3(0,2.*sin(.5*iTime),-9), l = normalize(lp - cp); //vec3 l = normalize(vec3(.6,.5,.7)); // point or directional light?
    // could do shadows but would require iterating over the segments again!  should refactor
    float nl = max(.04, dot(n,l)) // diffuse lighting
    , spec = dot(l, reflect(d, n)) // phong specular lighting
    , sexp = 32.;
    spec = pow(max(spec, 0.), sexp) * (sexp / 8. / acos(-1.) + 1.) * nl; // simple phong specular / 8pi
    vec3 c = vec3(nl * cov);
    //c *= mix(vec3(1), n * .5 + .5, cos(.2*iTime)*cos(.2*iTime)); // show normals sometimes
    // for it to blur correctly, would need to colorize inside the loop, probably lighting also
    c *= max(cos(ci * 2. * 6.2 - vec3(0,2,4)), 0.); // colorize by capsule index
    c += spec * cov;
    vec3 lx = cross(d, lp); float ll = dot(lx, lx); // view ray vs. light 
    float cl = 1.2 * exp2(-6. * ll);  // halo shows light position
    if (dot(lp, lp) > z*z) // not integrated properly so clips geo
        cl *= 1.-cov;
    c += cl;
    o = vec4(pow(c, vec3(.45)), 1); // gamma
}

// this work after shadertoy Lesson 4 by Martijn Steinrucken 
// and some Book of Shader, Lewis Lepton
// and Bruno Simon's threejsjourney course

// Band and Rect make a rectangle
float Band(float t, float start, float end, float blur){
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    return step1*step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
    float band1 = Band(uv.x,left,right,blur);
    float band2 = Band(uv.y,bottom,top,blur);

    return band1*band2;
}
// make a circle
float Circle(vec2 uv, vec2 p, float r, float blur){
    float d = length(uv-p);
    float c = smoothstep(r,r-blur,d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set up uv
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    // set up color
    vec3 col = vec3(.8);

    // x and y coords
    float x = uv.x;
    float y = uv.y;

    // rectangles
    float rect01 = Rect(vec2(x,y),-.8,-.6,-.45,.45,.001);  
    col -= vec3(.2,.8,.9)*rect01;

    float rect02 = Rect(vec2(x,y),-.75,-.65,.35,.4,.001);
    col += vec3(0.,.9,.8)*rect02;

    x += y*.2-0.05;
    float rect03 = Rect(vec2(x,y),-.75,-.65,.15,.3,.001);
    col += vec3(.7,.9,.8)*rect03;

    float circle01 = Circle(vec2(x,y),vec2(-.75,-.375),.1,.001);
    col += vec3(.9,.2,.2)*circle01;

    x = uv.x;
    float rect04 = Rect(vec2(x,y),-.5,-.4,-.25,.45,.001);  
    col -= vec3(.9,.4,.3)*rect04;

    for(int i = 0; i < 3;i++){
        float circle01 = Circle(vec2(x,y),vec2(-.45,float(i)*.15),.035,.001);
        col += circle01;
    }

    float top = sin(x*iTime*1.1)-.625*(cos(x*5.)-.25);
    float rect05 = Rect(vec2(x,y),-.3+y*.2,.75-y*.2,-.45,top,.001);  
    col -= vec3(1.,.2,.2)*rect05;

    fragColor = vec4(col,1.0);
}

vec3 getBackgroundColor(vec2 uv) {
    uv += 0.5; // remap uv from <-0.5,0.5> to <0,1>
    vec3 gradientStartColor = vec3(1., 0., 1.);
    vec3 gradientEndColor = vec3(0., 1., 1.);
    return mix(gradientStartColor, gradientEndColor, uv.y); // gradient goes from bottom to top
}

float sdCircle(vec2 uv, float r, vec2 offset, float yOsc) {
  float x = uv.x - offset.x;
  float y = uv.y - offset.y;

  //x += sin(iTime*3.)*.03;
  y += sin(iTime*yOsc)*.1;

  return length(vec2(x, y)) - r;
}

float opRepLim(vec2 p, float r, float c, vec2 l, float yOsc)
{
  vec2 q = p-c*clamp(round(p/c),-l,l);
  return sdCircle(q, r, vec2(0),yOsc);
}


vec3 drawScene(vec2 uv) {
  vec3 col = getBackgroundColor(uv);
  float circle = opRepLim(uv,.075,.2,vec2(7., 3.),1.);
  float circle2 = opRepLim(uv+.15,.05,.2,vec2(7., 3.),1.9);
  float circle3 = opRepLim(uv-.15,.05,.2,vec2(7., 3.),1.5);


  col = mix(vec3(1.,0.,.25),col,step(0.,circle));
  col = mix(vec3(0.,.2,.8),col,step(0.,circle2));
  col = mix(vec3(8.,.7,0.),col,step(0.,circle3));

  return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = drawScene(uv);

    fragColor = vec4(col,1.);
}

// Loved the original shader so much 
//     (Inside the Matrix (https://www.shadertoy.com/view/4t3BWl))
// But in converting it to Unity cG/HLSL, I needed a more simplistic version
// that didn't move or respond to mouse events, so I stripped all that out. 
// What's resulting here is the base unmoving Matrix view, which was
// then converted to a Unity ready shader (available freely, below)
// https://github.com/universalbri/MyUnityShaders/blob/main/MatrixRainV2.shader
// Thanks to the original designer, your code absolutely rocked!

const int ITERATIONS = 40;   //use less value if you need more performance
const float SPEED = 1.;

const float STRIP_CHARS_MIN =  3.;
const float STRIP_CHARS_MAX = 40.;
const float STRIP_CHAR_HEIGHT = 0.15;
const float STRIP_CHAR_WIDTH = 0.10;
const float ZCELL_SIZE = 1. * (STRIP_CHAR_HEIGHT * STRIP_CHARS_MAX);  //the multiplier can't be less than 1.
const float XYCELL_SIZE = 12. * STRIP_CHAR_WIDTH;  //the multiplier can't be less than 1.
const int BLOCK_SIZE = 8;  //in cells
const int BLOCK_GAP = 0;    //in cells

const float PI = 3.14159265359;

float hash(float v) {
    return fract(sin(v)*43758.5453123);
}

float hash(vec2 v) {
    return hash(dot(v, vec2(5.3983, 5.4427)));
}

vec2 hash2(vec2 v)
{
    //v = vec2(v * mat2(127.1, 311.7,  269.5, 183.3));
	return fract(sin(v)*43758.5453123);
}

vec4 hash4(vec2 v)
{
    vec4 p = vec4(v * mat4x2( 127.1, 311.7,
                              269.5, 183.3,
                              113.5, 271.9,
                              246.1, 124.6 ));
    return fract(sin(p)*43758.5453123);
}

vec4 hash4(vec3 v)
{
    vec4 p = vec4(v * mat4x3( 127.1, 311.7, 74.7,
                              269.5, 183.3, 246.1,
                              113.5, 271.9, 124.6,
                              271.9, 269.5, 311.7 ) );
    return fract(sin(p)*43758.5453123);
}


float rune_line(vec2 p, vec2 a, vec2 b)
{
    p -= a, b -= a;
	float h = clamp(dot(p, b) / dot(b, b), 0., 1.);   // proj coord on line
	return length(p - b * h);                         // dist to segment
}

float rune(vec2 U, vec2 seed, float highlight)
{
	float d = 1e5;
	for (int i = 0; i < 4; i++)	// number of strokes
	{
        vec4 pos = hash4(seed);
		seed += 1.;

		// each rune touches the edge of its box on all 4 sides
		if (i == 0) pos.y = .0;
		if (i == 1) pos.x = .999;
		if (i == 2) pos.x = .0;
		if (i == 3) pos.y = .999;
		// snap the random line endpoints to a grid 2x3
		vec4 snaps = vec4(2, 3, 2, 3);
		pos = ( floor(pos * snaps) + .5) / snaps;

		if (pos.xy != pos.zw)  //filter out single points (when start and end are the same)
		    d = min(d, rune_line(U, pos.xy, pos.zw + .001) ); // closest line
	}
	return smoothstep(0.1, 0., d) + highlight*smoothstep(0.4, 0., d);
}

float random_char(vec2 outer, vec2 inner, float highlight) {
    vec2 seed = vec2(dot(outer, vec2(269.5, 183.3)), dot(outer, vec2(113.5, 271.9)));
    return rune(inner, seed, highlight);
}


vec3 rain(vec3 ro3, vec3 rd3, float time) {
    vec4 result = vec4(0.);

    // normalized 2d projection
    vec2 ro2 = normalize(ro3.xy);
    vec2 rd2 = normalize(rd3.xy);

    // we use formulas `ro3 + rd3 * t3` and `ro2 + rd2 * t2`, `t3_to_t2` is a multiplier to convert t3 to t2
    bool prefer_dx = abs(rd2.x) > abs(rd2.y);
    float t3_to_t2 = prefer_dx ? rd3.x / rd2.x : rd3.y / rd2.y;

    // at first, horizontal space (xy) is divided into cells (which are columns in 3D)
    // then each xy-cell is divided into vertical cells (along z) - each of these cells contains one raindrop

    ivec3 cell_side = ivec3(step(0., rd3));      //for positive rd.x use cell side with higher x (1) as the next side, for negative - with lower x (0), the same for y and z
    ivec3 cell_shift = ivec3(sign(rd3));         //shift to move to the next cell

    //  move through xy-cells in the ray direction
    float t2 = 0.;  // the ray formula is: ro2 + rd2 * t2, where t2 is positive as the ray has a direction.
    ivec2 next_cell = ivec2(floor(ro2/XYCELL_SIZE));  //first cell index where ray origin is located
    for (int i=0; i<ITERATIONS; i++) {
        ivec2 cell = next_cell;  //save cell value before changing
        float t2s = t2;          //and t

        //  find the intersection with the nearest side of the current xy-cell (since we know the direction, we only need to check one vertical side and one horizontal side)
        vec2 side = vec2(next_cell + cell_side.xy) * XYCELL_SIZE;  //side.x is x coord of the y-axis side, side.y - y of the x-axis side
        vec2 t2_side = (side - ro2) / rd2;  // t2_side.x and t2_side.y are two candidates for the next value of t2, we need the nearest
        if (t2_side.x < t2_side.y) {
            t2 = t2_side.x;
            next_cell.x += cell_shift.x;  //cross through the y-axis side
        } else {
            t2 = t2_side.y;
            next_cell.y += cell_shift.y;  //cross through the x-axis side
        }
        //now t2 is the value of the end point in the current cell (and the same point is the start value in the next cell)

        //  gap cells
        vec2 cell_in_block = fract(vec2(cell) / float(BLOCK_SIZE));
        float gap = float(BLOCK_GAP) / float(BLOCK_SIZE);
        if (cell_in_block.x < gap || cell_in_block.y < gap || (cell_in_block.x < (gap+0.1) && cell_in_block.y < (gap+0.1))) {
            continue;
        }

        //  return to 3d - we have start and end points of the ray segment inside the column (t3s and t3e)
        float t3s = t2s / t3_to_t2;

        //  move through z-cells of the current column in the ray direction (don't need much to check, two nearest cells are enough)
        float pos_z = ro3.z + rd3.z * t3s;
        float xycell_hash = hash(vec2(cell));
        float z_shift = xycell_hash*11. - time * (0.5 + xycell_hash * 1.0 + xycell_hash * xycell_hash * 1.0 + pow(xycell_hash, 16.) * 3.0);  //a different z shift for each xy column
        float char_z_shift = floor(z_shift / STRIP_CHAR_HEIGHT);
        z_shift = char_z_shift * STRIP_CHAR_HEIGHT;
        int zcell = int(floor((pos_z - z_shift)/ZCELL_SIZE));  //z-cell index
        for (int j=0; j<2; j++) {  //2 iterations is enough if camera doesn't look much up or down
            //  calcaulate coordinates of the target (raindrop)
            vec4 cell_hash = hash4(vec3(ivec3(cell, zcell)));
            vec4 cell_hash2 = fract(cell_hash * vec4(127.1, 311.7, 271.9, 124.6));

            float chars_count = cell_hash.w * (STRIP_CHARS_MAX - STRIP_CHARS_MIN) + STRIP_CHARS_MIN;
            float target_length = chars_count * STRIP_CHAR_HEIGHT;
            float target_rad = STRIP_CHAR_WIDTH / 2.;
            float target_z = (float(zcell)*ZCELL_SIZE + z_shift) + cell_hash.z * (ZCELL_SIZE - target_length);
            vec2 target = vec2(cell) * XYCELL_SIZE + target_rad + cell_hash.xy * (XYCELL_SIZE - target_rad*2.);

            //  We have a line segment (t0,t). Now calculate the distance between line segment and cell target (it's easier in 2d)
            vec2 s = target - ro2;
            float tmin = dot(s, rd2);  //tmin - point with minimal distance to target
            if (tmin >= t2s && tmin <= t2) {
                float u = s.x * rd2.y - s.y * rd2.x;  //horizontal coord in the matrix strip
                if (abs(u) < target_rad) {
                    u = (u/target_rad + 1.) / 2.;
                    float z = ro3.z + rd3.z * tmin/t3_to_t2;
                    float v = (z - target_z) / target_length;  //vertical coord in the matrix strip
                    if (v >= 0.0 && v < 1.0) {
                        float c = floor(v * chars_count);  //symbol index relative to the start of the strip, with addition of char_z_shift it becomes an index relative to the whole cell
                        float q = fract(v * chars_count);
                        vec2 char_hash = hash2(vec2(c+char_z_shift, cell_hash2.x));
                        if (char_hash.x >= 0.1 || c == 0.) {  //10% of missed symbols
                            float time_factor = floor(c == 0. ? time*5.0 :  //first symbol is changed fast
                                    time*(1.0*cell_hash2.z +   //strips are changed sometime with different speed
                                            cell_hash2.w*cell_hash2.w*4.*pow(char_hash.y, 4.)));  //some symbols in some strips are changed relatively often
                            float a = random_char(vec2(char_hash.x, time_factor), vec2(u,q), max(1., 3. - c/2.)*0.2);  //alpha
                            a *= clamp((chars_count - 0.5 - c) / 2., 0., 1.);  //tail fade
                            if (a > 0.) {
                                float attenuation = 1. + pow(0.06*tmin/t3_to_t2, 2.);
                                vec3 col = (c == 0. ? vec3(0.67, 1.0, 0.82) : vec3(0.25, 0.80, 0.40)) / attenuation;
                                float a1 = result.a;
                                result.a = a1 + (1. - a1) * a;
                                result.xyz = (result.xyz * a1 + col * (1. - a1) * a) / result.a;
                                if (result.a > 0.98)  return result.xyz;
                            }
                        }
                    }
                }
            }
            // not found in this cell - go to next vertical cell
            zcell += cell_shift.z;
        }
        // go to next horizontal cell
    }

    return result.xyz * result.a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (STRIP_CHAR_WIDTH > XYCELL_SIZE || STRIP_CHAR_HEIGHT * STRIP_CHARS_MAX > ZCELL_SIZE) {
        // error
        fragColor = vec4(1., 0., 0., 1.);
        return;
    }

	vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y;
    float time = iTime * SPEED;

    float level1_size = float(BLOCK_SIZE) * XYCELL_SIZE;
    float level2_size = 4. * level1_size;
    float gap_size = float(BLOCK_GAP) * XYCELL_SIZE;

    vec3 ro = vec3(gap_size/2., gap_size/2., 0.);
    vec3 rd = vec3(uv.x, 2.0, uv.y);
    vec2 p;
    ro.xy += level1_size * p;

    ro += rd * 0.2;
    rd = normalize(rd);

    vec3 col = rain(ro, rd, time);

    fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

//    float d = pow(uv.x,.2) + pow(uv.y,2.) - mod(iTime,4.)/8.0;
    float d = (length(uv))-.8;
    d = smoothstep(.13,.3+sin(iTime)*col.z,cos(mod(iTime/20.,d))-col.y);
    // Output to screen
    fragColor = vec4(vec3(d,col.y, col.z),1.0);
}

#define PI 3.1415926
#define ANTI_ALIAS .005


// from https://www.shadertoy.com/view/4djSRW
float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float getNormalizedNonPeriodic(float seed) {
    return (sin(2. * seed) + sin(PI * seed)+ 2.) / 4.;
}

float getNonPeriodic(float seed) {
    return sin(2. * seed) + sin(PI * seed);
}

mat2x2 createRotationMatrix(float rotation) {
    return mat2x2(
        cos(rotation), -sin(rotation),
        sin(rotation), cos(rotation)
    );
}

vec4 getColorBlend(float timeScale, vec2 uv, float xHash, float xHashAA, float yHash, float yHashAA, float xBlend, float yBlend) {
    vec2 xyFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xySecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xyColor = xyFirst * xySecond;

    vec2 xAyFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xAySecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xAyColor = xAyFirst * xAySecond;

    vec2 xyAFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));

    vec2 xyASecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));

    vec2 xyAColor = xyAFirst * xyASecond;

    vec2 xAyAFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));

    vec2 xAyASecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));


    vec2 xAyAColor = xAyAFirst * xAyASecond;

    vec2 xLower = mix(xAyAColor, xyAColor, xBlend);
    vec2 xUpper = mix(xAyColor, xyColor, xBlend);

    vec2 finalBlend = mix(xLower, xUpper, yBlend);

    return vec4(finalBlend.x, finalBlend.y, getNormalizedNonPeriodic(iTime * timeScale + 200.), 1.);
}

vec4 getBWBlend(float timeScale, vec2 uv, float xHash, float xHashAA, float yHash, float yHashAA, float xBlend, float yBlend) {
    vec2 xyFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xySecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xyColor = xyFirst * xySecond;

    vec2 xAyFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xAySecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHash) + yHash));

    vec2 xAyColor = xAyFirst * xAySecond;

    vec2 xyAFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));

    vec2 xyASecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHash) + xHash), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));

    vec2 xyAColor = xyAFirst * xyASecond;

    vec2 xAyAFirst = vec2(
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));

    vec2 xAyASecond = vec2(
        getNormalizedNonPeriodic(uv.x + getNonPeriodic(iTime * timeScale + xHashAA) + xHashAA), 
        getNormalizedNonPeriodic(uv.y + getNonPeriodic(iTime * timeScale + yHashAA) + yHashAA));


    vec2 xAyAColor = xAyAFirst * xAyASecond;

    vec2 xLower = mix(xAyAColor, xyAColor, xBlend);
    vec2 xUpper = mix(xAyColor, xyColor, xBlend);

    vec2 finalBlend = mix(xLower, xUpper, yBlend);

    vec4 blended = vec4(finalBlend.x, finalBlend.y, getNormalizedNonPeriodic(iTime * timeScale + 200.), 1.);
    return vec4(vec3(blended.x * blended.y), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.x / iResolution.y;

    uv.x *= aspectRatio;

    mat2x2 rotationMatrix = createRotationMatrix(PI / 4.);

    uv.y += getNonPeriodic(iTime * .1 + uv.x) * .1 + 100.;
    uv = uv * rotationMatrix;

    float gridSize = .01;

    float xFloor = floor(uv.x / gridSize);
    float xHash = hash(vec2(xFloor));
    float xHashAA = hash(vec2(xFloor - 1.));
    float xBlend = smoothstep(0., ANTI_ALIAS / gridSize, fract(uv.x / gridSize));

    float yFloor = floor(uv.y / gridSize);
    float yHash = hash(vec2(yFloor));
    float yHashAA = hash(vec2(yFloor - 1.));
    float yBlend = smoothstep(0., ANTI_ALIAS / gridSize, fract(uv.y / gridSize));

    float timeScale = .15;

    fragColor = getColorBlend(timeScale, uv, xHash, xHashAA, yHash, yHashAA, xBlend, yBlend);

    vec2 trueUv = fragCoord/iResolution.xy;
    if (iMouse.z > 1.) {

        float cursorDist = distance(vec2(trueUv.x * aspectRatio, trueUv.y), vec2(iMouse.x / iResolution.x * aspectRatio, iMouse.y / iResolution.y));

        float spotlightRadius = .4;
        float spotlightFade = spotlightRadius * .5;
        float cursorVal = smoothstep(spotlightRadius - spotlightFade, spotlightRadius + spotlightFade, cursorDist);

        vec4 spotlightColor = getBWBlend(timeScale, uv, xHash, xHashAA, yHash, yHashAA, xBlend, yBlend) * vec4(1.0, 0.5, 0., 1.);

        fragColor = mix(spotlightColor, fragColor, cursorVal * cursorVal);
    }
}

// some color constants
const vec3 RED = vec3(1.,0.,0.);
const vec3 GREEN = vec3(0.,1.,0.);
const vec3 BLUE = vec3(0.,0.,1.);
const vec3 YELLOW = vec3(1.,1.,0.);
const vec3 GRAY = vec3(0.8);
const vec3 BLACK = vec3(0.);
const vec3 WHITE = vec3(1.);

// --------------------------- drawing on the plane ---------------------------

// To change what is actually drawn on the plane, change the pixelColor function below.


struct Ring {
    vec2 center;
    float radius;
    float width;
};

bool inRing(vec2 p, Ring ring){
    return abs(distance(p,ring.center)-ring.radius)<ring.width;
}

struct Line {
    vec2 point;
    vec2 direction; // should be normalized
    float width;
};

bool inLine(vec2 p, Line line){
    vec2 v = p-line.point;
    float len = dot(v, line.direction);
    v -= len*line.direction; // The part perpendicular to the line

    return length(v)<line.width;
}

//                      center,      radius, width.
const Ring ring1 = Ring(vec2(1.),       1.,  0.05);
const Ring ring2 = Ring(vec2(-1.),      0.5, 0.05);
const Ring ring3 = Ring(-vec2(-3., 3.), 1.,  0.05);
const Ring ring4 = Ring(-vec2(2., -2.), 2.,  0.05);

const Line xAxis = Line(vec2(0.), vec2(1., 0.), 0.03);
const Line yAxis = Line(vec2(0.), vec2(0., 1.), 0.03);

vec3 pixelColor(vec2 p){

    if (inLine(p, xAxis) || inLine(p, yAxis))
        return RED;
    if (abs(p.x-round(p.x))<0.03 || abs(p.y-round(p.y))<0.03)  // draw grid
        return YELLOW;
    if (inRing(p, ring1) || inRing(p, ring2) || inRing(p, ring3) || inRing(p, ring4))
        return GREEN;

    float d = (length(p)-1.+sin(iTime))/0.07;
    d = 1. - d*d;
    d = clamp(d, 0., 1.);

    return mix(texture(iChannel0, p/2.).rgb * 0.7, BLUE, d);
}


// --------------------------- ray computation ---------------------------


const float MAX_DIST = 1000000000.;

vec3 planeHit(vec3 origin, vec3 direction, out float dist){
    dist = MAX_DIST;

    if (direction.z == 0.)
        return GRAY;

    dist = -origin.z/direction.z;
    if (dist<0.){
        dist = MAX_DIST;
        return GRAY;
    }
    vec3 hitPoint = origin + dist*direction;


    return pixelColor(hitPoint.xy);
}


struct Light{
    vec3 position;
    float strength; //between 0 and 1
};

const Light light = Light(vec3(2.), 0.5);

vec3 sphereHit(vec3 origin, vec3 direction, out float dist){
    dist = MAX_DIST;
    vec3 center = vec3(0.,0.,0.5);
    float r = 0.5;
    // compute the intersection of the ray with the sphere
    // |origin + t*direction - center|^2 = r^2
    // |origin-center|^2 +2t<direction,origin-center> + t^2 |direction|^2 = r^2

    vec3 v = origin-center;                     
    float a = dot(direction, direction);         
    float b = 2.*dot(direction,v);               
    float c = dot(v,v)-r*r;                      
    // solve a*t^2 + b*t + c = 0
    float disc = b*b-4.*a*c;
    if (disc<0.)
        return GRAY; // no intersection with the sphere

    dist = (-b - sqrt(disc))/(2.*a);
    vec3 hitPoint = origin + dist*direction;
    // Look at the ray starting at the north pole, and going through the hitting point,
    // and find out its intersection with the plane
    vec3 newDirection = hitPoint-center-vec3(0.,0.,r);
    float planeDist;
    vec3 planeColor = planeHit(
        hitPoint, 
        newDirection,
        planeDist);

    // Just to differentiate a little bit the sphere from the plane, add some lighting to the sphere.    
    vec3 toLight = normalize(light.position-hitPoint);
    vec3 normal = normalize(hitPoint-center);
    vec3 lightReflection = reflect(toLight, normal);
    float dotProduct = dot(direction, lightReflection)*light.strength;
    return planeColor + WHITE*dotProduct;
}


// Set the location of the camera.
void camera(out vec3 origin, out vec3 forward, out vec3 right, out vec3 up){

    float angle = iTime*0.5;
    float r = 4.+sin(iTime);

    origin = vec3(r*cos(angle),r*sin(angle),3. + 2.3*cos(angle*0.8));

    forward = -normalize(origin);
    right = cross(forward, vec3(0.,0.,1.));
    up = cross(right,forward);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (height goes from 0 to 1, and keep the ratio with the width)
    vec2 uv = fragCoord/iResolution.y;

    // camera
    vec3 origin, forward, right,up;
    camera(origin,forward, right,up);

    // ray direction
    uv -= vec2(0.5*iResolution.x/iResolution.y, 0.5);
    vec3 direction = normalize(forward + uv.x*right + uv.y*up);

    // compute intersection with sphere
    float dist = MAX_DIST;
    vec3 color = BLACK;

    float sphereDist;
    vec3 sphereColor = sphereHit(origin, direction, sphereDist);
    if (sphereDist<dist){
        dist = sphereDist;
        color = sphereColor;
    }

    // compute intersection with the plane
    float planeDist;
    vec3 planeColor = planeHit(origin, direction, planeDist);
    if (planeDist<dist){
        dist = planeDist;
        float fog = 2.-dist/10.;
        fog = clamp(fog, 0., 1.);
        color = planeColor*fog;
    }

    // Output to screen
    fragColor = vec4(color ,1.0);

}

// Author: bitless
// Title: Rusty metal cubes

// Thanks to Patricio Gonzalez Vivo & Jen Lowe for "The Book of Shaders"
// and Fabrice Neyret (FabriceNeyret2) for https://shadertoyunofficial.wordpress.com/
// and Inigo Quilez (iq) for  http://www.iquilezles.org/www/index.htm
// and whole Shadertoy community for inspiration.

#define maxd .48    //disk max diameter
#define mind .1     //disk min diameter
#define cnum 10.    //num of disks

#define p(t, a, b, c, d) ( a + b*cos( 6.28318*(c*t+d) ) )                   //iq's palette
#define rot(a)   mat2(cos(a + vec4(0,11,33,0)))                             //rotate 2d
#define h21(a) ( fract(sin(dot(a.xy,vec2(12.9898,78.233)))*43758.5453123) ) //hash21

//  Minimal Hexagonal Grid - Shane
//  https://www.shadertoy.com/view/Xljczw
vec4 getHex(vec2 p) //hex grid coords 
{
    vec2 s = vec2(1, 1.7320508);
    vec4 hC = floor(vec4(p, p - vec2(.5, 1))/s.xyxy) + .5;
    vec4 h = vec4(p - hC.xy*s, p - (hC.zw + .5)*s);
    return dot(h.xy, h.xy)<dot(h.zw, h.zw) ? vec4(h.xy, hC.xy) : vec4(h.zw, hC.zw + .5);
}

vec3 HexToSqr (vec2 st) //hexagonal cell coords to square face coords 
{ 
    vec3 r;
    if (st.y > 0.-abs(st.x)*0.57777)
        if (st.x > 0.) 
            r = vec3(fract(vec2(-st.x,(st.y+st.x/1.73)*0.86)*2.),2.); //right face
        else
            r = vec3(fract(vec2(st.x,(st.y-st.x/1.73)*0.86)*2.),3.); //left face
        else
            r = vec3(fract(vec2((st.x+st.y*1.73),(st.x-st.y*1.73))),1.); //upper face
    return r;
} 

float sinc( float x, float k, float l)
{
    float h = k*x;
    return sin(x*l)*h*exp(1.0-h);
}

vec4 Tex (vec4 hx, vec3 sqr, vec2 sh) //face texture
{
    float r = h21(hx.zw*vec2(sqr.z))*5.;
    vec2 uv = ((sqr.xy + vec2(r))  - sh)*.5* rot(r);
    vec4 t = texture(iChannel0, uv);
    return mix(vec4(p(h21(vec2(hx.zw))*1.75,vec3(.18,.38,.32),vec3(.46,.42,.58),vec3(.11,.20,.22),vec3(.14,.16,.13))*.125,1.),t,.1);  
}

void tile(vec4 hx,inout vec4 C) 
{
    vec3 sqr = HexToSqr(hx.xy);
    vec2    st = sqr.xy - .5  //face square coordinates
            ,shift;  //disk shift
    float   n = sqr.z       //face id
            ,sm = 3./iResolution.y  //smoothness
            ,h = -sinc (mod(iTime+h21(hx.wz*vec2(n))*20., 10.), .75,3.5)*.3 //bump stright
            ,df =  abs(h) * .8
            ,b = (4.-n)*1.2+.8; //face lightness

    C = Tex(hx,sqr,vec2(max(h,0.)*sin(1.2)*1.5)*vec2(1.,-1.))*b*(1.-max(h,0.)*5.);

    for (float i = 0.; i < cnum; i++)
    {
        float k = (h < 0.) ? cnum - 1. - i : i;
        float diam = mind + (maxd-mind)/cnum*k;
        shift = vec2(h*sin((cnum-1.-k)/cnum*1.2)*1.5)*vec2(1,-1);
        vec4 col = Tex(hx,sqr,shift)*b;

        if (h < 0.) //outer disks
        {
            C = mix(C, vec4(0.), smoothstep (diam+df,diam-sm,length(st-shift))*.2); //shadow;
            C = mix(C, col * (1. - (cnum-k)/cnum*h*5.), smoothstep (diam+sm,diam-sm,length(st-shift))); //disk
            C = mix(C, vec4(max(-st.y,-.1)*2.), smoothstep (.01,.0,abs(length(st-shift)-diam))*df); //disk edge
        }
        else //inner disks
        {
            C = mix(C, col* (1. - (cnum - 1. -i )/cnum*h*5.) , smoothstep (diam-sm,diam+sm,length(st-shift))); //disk
            C = mix(C, vec4(st.y), smoothstep (.01,.0,abs(length(st-shift)-diam))*df); //disk edge
        }
    }
    C = mix(C, vec4(0.),(smoothstep(.4,.5,-st.x)+smoothstep(.4,.5,st.y))*.3); //ambient occlusion
    C = mix(C, vec4(1.),(smoothstep(.45,.5,st.x)+smoothstep(.45,.5,-st.y))*.08); //edge bevel
}

void mainImage( out vec4 C, in vec2 g)
{
    vec2 rz = iResolution.xy
        ,uv = (g+g-rz)/-rz.y;

    uv *= 1.2+sin(iTime*.3)*.25; //camera scale
    uv += uv * pow(length(uv),2.)*.025 + vec2(sin(iTime*.2)+5.,-cos(iTime*.2)+9.); //lens distortion and camera moving

    vec4 hx = getHex(uv);
    C = -C;
    tile(hx,C);
}

// Creates the inverse of the view matrix
mat4 invLookAt(in vec3 eye, in vec3 center, in vec3 up) {
    vec3 f = vec3(normalize(center - eye));
    vec3 s = vec3(normalize(cross(f, up)));
    vec3 u = vec3(cross(s, f));

    mat4 m = mat4(1.0);
    m[0][0] = s.x;
    m[0][1] = s.y;
    m[0][2] = s.z;
    m[1][0] = u.x;
    m[1][1] = u.y;
    m[1][2] = u.z;
    m[2][0] =-f.x;
    m[2][1] =-f.y;
    m[2][2] =-f.z;

    m[3][0] = dot(s, eye);
    m[3][1] = dot(u, eye);
    m[3][2] =-dot(f, eye);

    return m;
}

// Creates a view matrix
mat4 look_at(vec3 eye, vec3 center,vec3 up) {
    vec3 f = vec3(normalize(center - eye));
    vec3 s = vec3(normalize(cross(f, up)));
    vec3 u = vec3(cross(s, f));

    mat4 m = mat4(1.0f);
    m[0][0] = s.x;
    m[1][0] = s.y;
    m[2][0] = s.z;
    m[0][1] = u.x;
    m[1][1] = u.y;
    m[2][1] = u.z;
    m[0][2] =-f.x;
    m[1][2] =-f.y;
    m[2][2] =-f.z;
    m[3][0] =-dot(s, eye);
    m[3][1] =-dot(u, eye);
    m[3][2] = dot(f, eye);
    return m;
}

float sdSphere( in vec3 p, in float r ) {
    return length(p)-r;
}

float smoothUnion( float d1, float d2, float k ) {
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}

// Scene geometry
float map(in vec3 wRayPos) {
    float d = 1e10;

    float sphereD0, sphereD1, sphereD2;

    { // Sphere0
        vec3 wSpherePosition = 4.0 * vec3(sin(iTime), 0.0, cos(iTime));
        sphereD0 = sdSphere(wRayPos - wSpherePosition, 1.0);
    }

    { // Sphere1
        vec3 wSpherePosition = vec3(2.0, sin(iTime) * 2.0, cos(iTime));
        sphereD1 = sdSphere(wRayPos - wSpherePosition, 2.0);
    }

    { // Sphere2
        vec3 wSpherePosition = vec3(cos(iTime) * 2.0, sin(iTime) * 2.0, 4.0 * cos(iTime));
        sphereD2 = sdSphere(wRayPos - wSpherePosition, 1.5);
    }

    d = min(d, smoothUnion(sphereD0, sphereD1, 0.6));
    d = min(d, smoothUnion(d, sphereD2, 0.6));

    return d;
}

// Calculates the normal of a point on a surface.
vec3 calcNormal(in vec3 pos) {
    const float ep = 0.0001;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}

struct Light {
    vec3 wPosition;
    vec3 color;
    float strength;
    float speed;
};

const int NUM_LIGHTS = 4;
Light lights[NUM_LIGHTS];

// Calculates the pixel coordinate of a 3D point in the world
vec2 calcPixelCoord(in vec3 wPosition, in mat4 viewMatrix) {
    float nearPlane = -1.0;

    vec3 vPosition = (viewMatrix * vec4(wPosition, 1.0)).xyz;

    float ratio = nearPlane / vPosition.z;

    vec2 screenPos = -ratio * vPosition.xy;

    return screenPos;
}

// Calculates the diffuse
vec3 calcSurfaceLighting(
    in float dist,
    in vec3 wPixelPosition, 
    in vec3 wRayDirection,
    in Light light) {
    vec3 wHitPosition = wPixelPosition + wRayDirection * dist;
    vec3 wNormal = calcNormal(wHitPosition);

    float diffuse = dot(wNormal, normalize(light.wPosition - wHitPosition));
    diffuse = clamp(diffuse, 0.1, 1.0);

    return vec3(diffuse) * light.color * 15.0;
}

// Classic raymarching
float raymarch(
    in vec3 wRayStart, 
    in vec3 wRayDirection) {
    float stride = 0.0;
    for (int i = 0; i < 64; i++) {
        vec3 wCurrent = wRayStart + wRayDirection * stride;

        float field = map(wCurrent);

        if (abs(field) < 0.001) {
            // We hit an object
            return stride;
        }
        else {
            // Keep marching
            stride += field;
        }

        if (stride > 10000.0) {
            return -1.0;
        }
    }

    return -1.0;
}

// Calculates the light glow (this depends on the 2D coordinate of the light on the screen).
vec4 screenLight(
    vec2 p, 
    vec3 vLightPos,
    vec2 center, 
    float strength, 
    vec4 color,
    in mat4 invViewMatrix) {
    // We need to see if this light is being occluded
    vec3 vPixelPosition = vec3(center, -1.0);
    vec3 vRayDirection = normalize(vPixelPosition);

    vec3 wPixelPosition = (invViewMatrix * vec4(vPixelPosition, 1.0)).xyz;
    vec3 wRayDirection = (invViewMatrix * vec4(vRayDirection, 0.0)).xyz;

    float dist = raymarch(wPixelPosition, wRayDirection);

    if (dist == -1.0 || dist < length(vLightPos)) {
        return strength * color / dot(p - center, p - center);
    }
    else {
        return vec4(0.0);
    }
}

struct LightContribution {
    vec3 bleed;
    vec3 diffuse;
};

// Calculates both the diffuse contribution and light bleeding (glow)
LightContribution calcLightContrib(
    in Light light,
    in mat4 viewMatrix,
    in mat4 invViewMatrix,
    in vec2 fragCoord,
    in float dist,
    in vec3 wPixelPosition,
    in vec3 wRayDirection) {
    LightContribution contrib;
    contrib.bleed = vec3(0.0);
    contrib.diffuse = vec3(0.0);

    vec2 pLightPosition = calcPixelCoord(light.wPosition, viewMatrix);
    vec3 vLightPosition = (viewMatrix * vec4(light.wPosition,1.0)).xyz;
    contrib.bleed = screenLight(
        fragCoord,
        vLightPosition,
        pLightPosition, 
        light.strength, 
        vec4(light.color, 1.0),
        invViewMatrix).rgb;

    if (dist == -1.0) {
        contrib.diffuse = vec3(0.0);
    }
    else {
        contrib.diffuse = calcSurfaceLighting(dist, wPixelPosition, wRayDirection, light);
    }

    return contrib;
}

// Calculates the color of a pixel (includes raymarching, lighting etc...)
vec3 render(
    in vec3 wPixelPosition, 
    in vec3 wRayDirection, 
    in mat4 invViewMatrix, 
    in mat4 viewMatrix, 
    in vec2 fragCoord) {
    float dist = raymarch(wPixelPosition, wRayDirection);

    vec3 finalColor = vec3(0.0);

    for (int i = 0; i < NUM_LIGHTS; i++) {
        LightContribution contrib = calcLightContrib(
            lights[i],
            viewMatrix,
            invViewMatrix,
            fragCoord,
            dist,
            wPixelPosition,
            wRayDirection);

        finalColor += contrib.diffuse + contrib.bleed;
    }

    return finalColor;
}

// Hashing function used for move
vec2 hash( vec2 x ) {
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

// Given an n, a unique varying position is produced
vec3 move(int n, float speed) {
    float h = hash(vec2(float(n))).x;

    return vec3(
        cos(speed * iTime / 10.0 - h) * 0.7, 
        sin(speed * iTime / 20.0 + h) * 0.2,
        sin(speed * iTime / 15.0 + h) * 0.5);
}

// Light parameters configuration
void configureLights() {
    lights[0].color = vec3(0.4, 0.2, 0.7);
    lights[0].strength = 0.1;
    lights[0].speed = 13.0;

    lights[1].color = vec3(0.1, 0.2, 0.8);
    lights[1].strength = 0.1;
    lights[1].speed = 8.0;

    lights[2].color = vec3(0.4, 0.6, 0.8);
    lights[2].strength = 0.1;
    lights[2].speed = 10.0;

    lights[3].color = vec3(0.9, 1.2, 0.6);
    lights[3].strength = 0.1;
    lights[3].speed = 7.0;
}

// Calculates the final color of the pixel with a given exposure
vec3 calcFinalColor(vec3 color, float exposure) {
    vec3 one = vec3(1.0);
    vec3 expValue = exp(-color / vec3(1.0) * exposure);
    vec3 diff = one - expValue;
    vec3 gamma = vec3(1.0 / 2.2);
    return pow(diff, gamma);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Sets up the light parameters
    configureLights();

    // Calculates the next positions of the lights
    for (int i = 0; i < NUM_LIGHTS; i++) {
        lights[i].wPosition = move(i, lights[i].speed) * 10.0;
    }

    // Set up world space properties fo the camera
    //vec3 wEyePosition = vec3(0.0, 5.0, 15.0);
    vec3 wEyePosition = vec3(0.0, 0.0, 1.0) * 15.0;
    vec3 wEyeDirection = normalize(vec3(0.0) - wEyePosition);
    vec3 wUp = normalize(vec3(sin(iTime * 0.5), cos(iTime * 0.4) + 1.1, 0.0));

    // Matrix will be used to convert world space positions of the SDF objects
    // into view space coordinates (which the view ray will be in).
    mat4 viewMatrix = invLookAt(wEyePosition, wEyePosition + wEyeDirection, wUp);
    mat4 invViewMatrix = invLookAt(wEyePosition, wEyePosition + wEyeDirection, wUp);

    // View space
    vec3 vPixelPosition = vec3((fragCoord - 0.5*iResolution.xy)/iResolution.y, -1.0);
    vec3 vRayDirection = normalize(vPixelPosition);

    // World space
    vec3 wPixelPosition = (invViewMatrix * vec4(vPixelPosition, 1.0)).xyz;
    vec3 wRayDirection = (invViewMatrix * vec4(vRayDirection, 0.0)).xyz;

    vec3 color = render(wPixelPosition, wRayDirection, invViewMatrix, viewMatrix, vPixelPosition.xy);

    // Final color
    fragColor.rgb = calcFinalColor(color, 0.06);
}

vec3 light = vec3(1.0, 1.0, 1.0);

struct scene_sf 
{
    float d;
    float depth;
    vec3 col;
    int type; // 0 - regular, 1 - mirror, 2 glass
    vec3 n;
};

scene_sf scene_dist(vec3 ap)
{
    const float gridsteps = 11.0;
    scene_sf rs;
    //vec3 so = vec3(sin(iTime*0.4) * 0.3, sin(iTime*3.14*0.4)*0.1, cos(iTime*0.4) * 3.7 + 3.0);
    vec3 so = vec3(sin(iTime*0.4) * 0.2, 0.1, cos(iTime*0.4) * 0.2);
    rs.d = length(ap - so) - 0.1;
    vec3 pn = -normalize(ap - so);
    float ldot = dot(pn, light);
    if (ldot < 0.0) ldot = 0.0;
    float vdot = dot(pn, vec3(0.0, -sin(iTime * 1.2), cos(iTime * 1.2)));
    float hdot = dot(pn, vec3(0.0, cos(iTime * 1.2), sin(iTime * 1.2)));
    float sdot = dot(pn, vec3(1.0, 0.0, 0.0));
    float vang = acos(vdot);
    float hang = acos(hdot);
    float sang = acos(sdot);
    rs.col = vec3(1.0, 1.0, 1.0);
    if (int(hang * gridsteps / 3.14) % 2 == 0)
        rs.col = rs.col - vec3(1.0, 0.0, 0.0);
    if (int(vang * gridsteps / 3.14) % 2 == 0)
        rs.col = rs.col - vec3(0.0, 1.0, 0.0);
    if (int(sang * gridsteps / 3.14) % 2 == 0)
        rs.col = rs.col - vec3(0.0, 0.0, 1.0);
    rs.type = 0;
    rs.n = -pn;
    //ldot = 1.0;

    so = vec3(sin(iTime*0.4 + 2.093) * 0.2, 0.1, cos(iTime*0.4 + 2.093) * 0.2);
    float glsd = length(ap - so) - 0.1;
    if (glsd < rs.d) {
        rs.d = glsd;
        pn = -normalize(ap - so);
        ldot = dot(pn, light);
        if (ldot < 0.0) ldot = 0.0;
        rs.col = vec3(0.0, 0.5, 1.0);
        rs.type = 1;
        rs.n = -pn;
    }

    so = vec3(sin(iTime*0.4 + 4.186) * 0.2, 0.1, cos(iTime*0.4 + 4.186) * 0.2);
    glsd = length(ap - so) - 0.1;
    if (glsd < rs.d) {
        rs.d = glsd;
        pn = -normalize(ap - so);
        ldot = dot(pn, light);
        if (ldot < 0.0) ldot = 0.0;
        rs.col = vec3(0.0, 0.5, 1.0);
        rs.type = 2;
        rs.n = -pn;
    }

    float plane_h = 0.7;
    if ((ap.y + plane_h) < rs.d) {
        vec3 plnorm = vec3(0.0, 1.0, 0.0);
        rs.d = (ap.y + plane_h);
        if ((int(floor(ap.x)) % 2 + 2) % 2 == (int(floor(ap.z)) % 2 + 2) % 2 )
            rs.col = vec3(1.0, 1.0, 1.0);
        else
            rs.col = vec3(0.0, 0.0, 0.0);
        //rs.col = vec3(1.0, 0.0, 0.0);
        ldot = -dot(plnorm, light);
        if (ldot < 0.0) ldot = 0.0;
        rs.type = 0;
        rs.n = plnorm;
    }

    float athmo_r = 5.0;
    if (athmo_r - length(ap) < rs.d) {
        rs.d = athmo_r - length(ap);
        float ag = atan(ap.x, ap.z) / 3.14;
        float cr = 1.0 - 30.0 * abs(fract(ag * 36.0) - 0.5);
        if (cr < 0.0) cr = 0.0;
        float agv = atan(sqrt(ap.z*ap.z + ap.x*ap.x), ap.y) / 3.14;
        float cg = 1.0 - 30.0 * abs(fract(agv * 36.0) - 0.5);
        if (cg < 0.0) cg = 0.0;
        rs.col = vec3(cr, cg, ap.y / athmo_r);
        ldot = -dot(vec3(0.0, 1.0, 0.0), light);
        if (ldot < 0.0) ldot = 0.0;
        rs.type = 0;
        rs.n = -normalize(ap);
    }

    rs.col= rs.col * ldot;
    return rs; // sphere
}

scene_sf rmarch(vec3 aro, vec3 ard) 
{
    vec3 p = aro;
    float depth = 0.0;
    scene_sf sf;
    float prefsfd = 1.0;
    for (int i = 0; i < 250; i++) {
        sf = scene_dist(p);
        float ds = abs(sf.d);
        if (ds < 0.0001) ds = 0.0001;
        p += ds * ard;
        depth += ds;
        if (depth > 100.0)
           break;
        if (sf.type == 1 && sf.d < 0.000) {
            if (dot(ard, sf.n) < 0.0) {
                ard = reflect(ard, sf.n);
            }
        }
        else
        if (sf.type == 2) {
            if (prefsfd > 0.0 && sf.d <= 0.0) {
                ard = refract(ard, sf.n, 1.0/1.6290);
            }
            if (prefsfd <= 0.0 && sf.d > 0.0) {
                ard = refract(ard, -sf.n, 1.6290);
            }
        }
        else
        if (sf.d < 0.000)
            break;

        prefsfd = sf.d;
    }
    if (depth > 100.0)
        depth = 100.0;
    sf.depth = depth;
    return sf;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
