float smooth_lerp(float a, float b, float t){
	float f = ((6.0f*t-15.0f)*t+10.0f)*t*t*t;
    return a*(1.0f-f)+b*f;
}

float getPerlin(vec3 pos){
    //x,y,x+1,y+1,bottom left,bottom right, top left, top right
	int x,y,z,x1,y1,z1,bl0,br0,tl0,tr0,bl1,br1,tl1,tr1;
    //fractional values
    float x_f,y_f,z_f;
    x = pos.x<0.0f?(int(pos.x)):int(pos.x)+1;
    y = pos.y<0.0f?(int(pos.y)):int(pos.y)+1;
    z = pos.z<0.0f?(int(pos.z)):int(pos.z)+1;
    //make sure all are positive modulo 256
    x=x<0?x%256+256:x;
    y=y<0?y%256+256:y;
    z=z<0?z%256+256:z;

    x_f = pos.x-floor(pos.x);
    y_f = pos.y-floor(pos.y);
    z_f = pos.z-floor(pos.z);

    x1 = x+1;
    y1 = y+1;
    z1 = z+1;
    bl0 = random_idx[(random_idx[(random_idx[x%256 ]+y )%256]+z )%256];
    br0 = random_idx[(random_idx[(random_idx[x1%256]+y )%256]+z )%256];
    tl0 = random_idx[(random_idx[(random_idx[x%256 ]+y1)%256]+z )%256];
    tr0 = random_idx[(random_idx[(random_idx[x1%256]+y1)%256]+z )%256];

    bl1 = random_idx[(random_idx[(random_idx[x%256 ]+y )%256]+z1)%256];
    br1 = random_idx[(random_idx[(random_idx[x1%256]+y )%256]+z1)%256];
    tl1 = random_idx[(random_idx[(random_idx[x%256 ]+y1)%256]+z1)%256];
    tr1 = random_idx[(random_idx[(random_idx[x1%256]+y1)%256]+z1)%256];



    vec3 grad_bl0 = gradient[bl0%12];
    vec3 grad_br0 = gradient[br0%12];
    vec3 grad_tl0 = gradient[tl0%12];
    vec3 grad_tr0 = gradient[tr0%12];
    vec3 grad_bl1 = gradient[bl1%12];
    vec3 grad_br1 = gradient[br1%12];
    vec3 grad_tl1 = gradient[tl1%12];
    vec3 grad_tr1 = gradient[tr1%12];

    vec3 dist_bl0 = vec3(x_f     ,y_f     , z_f);
    vec3 dist_br0 = vec3(x_f-1.0f,y_f     , z_f);
    vec3 dist_tl0 = vec3(x_f     ,y_f-1.0f, z_f);
    vec3 dist_tr0 = vec3(x_f-1.0f,y_f-1.0f, z_f);

    vec3 dist_bl1 = vec3(x_f     ,y_f     , z_f-1.0f);
    vec3 dist_br1 = vec3(x_f-1.0f,y_f     , z_f-1.0f);
    vec3 dist_tl1 = vec3(x_f     ,y_f-1.0f, z_f-1.0f);
    vec3 dist_tr1 = vec3(x_f-1.0f,y_f-1.0f, z_f-1.0f);

    //value at each point
    /*float v_bl = grad_bl.x*x_f        + grad_bl.y*y_f;
    float v_br = grad_br.x*(1.0f-x_f) + grad_br.y*y_f;
    float v_tl = grad_tl.x*x_f        + grad_tl.y*(1.0f-y_f);
    float v_tr = grad_tr.x*(1.0f-x_f) + grad_tr.y*(1.0f-y_f);*/
    float v_bl0 = dot(grad_bl0,dist_bl0);
    float v_br0 = dot(grad_br0,dist_br0);
    float v_tl0 = dot(grad_tl0,dist_tl0);
    float v_tr0 = dot(grad_tr0,dist_tr0);

    float v_bl1 = dot(grad_bl1,dist_bl1);
    float v_br1 = dot(grad_br1,dist_br1);
    float v_tl1 = dot(grad_tl1,dist_tl1);
    float v_tr1 = dot(grad_tr1,dist_tr1);
    /*v_bl = grad_bl.x;
    v_br = grad_br.x;
	v_tl = grad_tl.x;
	v_tr = grad_tr.x;*/

    return SQRT2*smooth_lerp(
        smooth_lerp(smooth_lerp(v_bl0,v_br0,x_f),smooth_lerp(v_tl0,v_tr0,x_f),y_f),
        smooth_lerp(smooth_lerp(v_bl1,v_br1,x_f),smooth_lerp(v_tl1,v_tr1,x_f),y_f),
        z_f);



}
float getNoise(vec3 pos){
	//return 1.0f/(1.0f+0.5f+0.25f+0.125f)*(getPerlin(pos)+0.5f*getPerlin(pos*2.0f)+0.25*getPerlin(pos*4.0f)+0.125*getPerlin(pos*8.0f));
    float frequency = 2.f;
    float attenuation = 0.5f;
    float val_acc=0.0f;
    float acc=0.0f;
    float mul_val = 1.0f;
    float mul_freq = 1.0f;

    for(int i = 0; i<LAYERS;i++){
        acc+= mul_val*getPerlin(pos*mul_freq);
        val_acc+= mul_val;
        mul_val*=attenuation;
        mul_freq*=frequency;
    }
    return acc/val_acc;

    //return (getPerlin(pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    // Normalized pixel coordinates (from 0 to 1)
    float scale = 10.0f;
    float aspect = iResolution.y/iResolution.x;
    vec2 uv = fragCoord/iResolution.xy;
	vec3 pos = vec3(uv.x*scale,uv.y*aspect*scale,iTime);
    float value = getNoise(pos)*0.5f+0.5f;
    // Time varying pixel color
    vec3 col = vec3(value);

    // Output to screen
    fragColor = vec4(col,1.0);
}
