struct FWave
{
    float AngleRange; //x axis range of graph
    float Amplitude;  //y axis range of graph
    float YOffset;
    vec4 Color;
};

struct FLight
{
    float Intensity;
    vec4  Color;
    vec2 Position;
};


float GetY(FWave Wave, float CurrentAngle_X);
vec2 CalculateSlope(vec2 Point1, vec2 Point2);
vec2 CalculateNormal(vec2 Slope);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    FWave Wave;
    Wave.AngleRange = acos(-1.0) / 2.0 ;
    Wave.Amplitude  = 0.5;
    Wave.YOffset    = 0.0;
    Wave.Color      = vec4(0.2, 0.9, 0.6, 1.0);

    vec2 uv = fragCoord/iResolution.xy;
    float AspectRatio = iResolution.x/iResolution.y;

    uv.x  = uv.x * 2.0 - 1.0;
    uv.x *= AspectRatio;

    float XShift = iTime;
    float CurrentAngle = (uv.x + XShift) * Wave.AngleRange;
    float CurrentY  = GetY(Wave, CurrentAngle);

    vec2 CurrentPoint = vec2(uv.x, CurrentY); //FOR LATER

    CurrentY = (CurrentY + 1.0) / 2.0; //convert to 0 to 1 so we can easily select the pixels within range

    float Output_Based_On_Current_Y =  uv.y / CurrentY;

    Output_Based_On_Current_Y = min(Output_Based_On_Current_Y, 1.0);

    float PushToEdge  = 15.0;

    Output_Based_On_Current_Y = pow(Output_Based_On_Current_Y, PushToEdge);

    //Invert and get a line output instead
    Output_Based_On_Current_Y = (1.0 - Output_Based_On_Current_Y) * Output_Based_On_Current_Y;

    Output_Based_On_Current_Y = clamp(Output_Based_On_Current_Y * 5.0, 0.0, 1.0);

    //Calculate surface normal
    uv.x  = (fragCoord.x - 0.1) / iResolution.x; 
    uv.x  = uv.x * 2.0 - 1.0; 
    uv.x *= AspectRatio;

    float PreviousAngle = (uv.x + XShift) * Wave.AngleRange;
    float PreviousY = GetY(Wave, PreviousAngle);
    vec2  PreviousPoint = vec2(uv.x, PreviousY);

    vec2 Normal = CalculateNormal( CalculateSlope(  CurrentPoint, PreviousPoint ) );

    //Calculate light
    FLight Light;
    Light.Intensity = 1.0;
    Light.Position  = vec2(0.0, .80);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)); //from shadertoy
    Light.Color  = vec4(0.4, 0.7, .9, 1);

    float CircleRadius = 0.15;
    uv = fragCoord/iResolution.xy; uv = uv * 2.0 - 1.0; uv.x *= AspectRatio;
    float Circle = max( CircleRadius - distance(uv, Light.Position), 0.0) / CircleRadius;
    Circle = Circle / 0.4;
    Circle = clamp(Circle, 0.0, 1.0);

    float I = dot( Light.Position - CurrentPoint, Normal) * Light.Intensity;
    I = max(I, 0.0);

    //Final output
    vec4 FinalOutput = Output_Based_On_Current_Y * I * Light.Color;
    vec4 LightShape = Circle * Light.Color * Light.Intensity ;

    FinalOutput = mix(FinalOutput, LightShape, 0.6);

    fragColor = ( FinalOutput );

}

float GetY(FWave Wave, float CurrentAngle_X)
{
    return Wave.Amplitude * cos(CurrentAngle_X) + Wave.YOffset;
}

vec2 CalculateSlope(vec2 Point1, vec2 Point2)
{
    return Point1 - Point2;
}

vec2 CalculateNormal(vec2 Slope)
{
    Slope = normalize(Slope);

    return vec2( -Slope.y, Slope.x);
}

// Watch the recording of the live coding on YouTube.
// https://youtu.be/bp37xTVNRrM?t=6125

#define time iTime
#define saturate(x) clamp(x,0.,1.)
#define rotpi4(v) v=vec2(v.x+v.y,-v.x+v.y)/sqrt(2.)
const float pi=acos(-1.);

mat2 rot(float a){
  float s=sin(a),c=cos(a);
  return mat2(c,s,-s,c);
}

#define odd(x) step(1.,mod(x,2.))
float sqWave(float x){
  float i=floor(x);
  float s=.1;
  return mix(odd(i),odd(i+1.),smoothstep(.5-s,.5+s,fract(x)));
}

float smin(float a,float b,float k){
  float h=max(k-abs(a-b),0.);
  return min(a,b)-h*h*.25/k;
}

vec3 pos;
float map(vec3 p){
  float d;
  //d=length(p)-.3;
  vec3 q=p;
  q.y=.7-abs(q.y);

  d=q.y;
  q.zx=fract(q.zx)-.5;

  vec2 dq=mix(vec2(-.2),vec2(.27,.1),sqWave(time*.15));
  float a=1.;
  for(int i=0;i<5;i++){
    vec3 v=q;
    v.zx=abs(v.zx);
    if(v.z>v.x)v.zx=v.xz;
    d=min(d,max(v.x-.1,(v.x*2.+v.y)/sqrt(5.)-.3)/a);
    q.zx=abs(q.zx);
    rotpi4(q.xz);
    q.xy-=dq;
    rotpi4(q.yx);

    q*=2.;
    a*=2.;
  }

  q=p-pos;
  float t=length(q)-.3;
  q*=15.;
  q.xy*=rot(time*1.3);
  q.yz*=rot(time*1.7);
  t=max(t,(abs(dot(sin(q),cos(q.yzx)))-.2)/15.);

  d=smin(d,t,.3);

  return d;
}

vec3 calcN(vec3 p){
  vec2 e=vec2(.001,0);
  return normalize(vec3(map(p+e.xyy)-map(p-e.xyy),
  map(p+e.yxy)-map(p-e.yxy),
  map(p+e.yyx)-map(p-e.yyx)));
}

vec3 hsv(float h,float s,float v){
  vec3 res=fract(h+vec3(0,2,1)/3.)*6.-3.;
  res=saturate(abs(res)-1.);
  res=(res-1.)*s+1.;
  res*=v;
  return res;
}

vec3 march(inout vec3 rp,inout vec3 rd,inout vec3 ra,inout bool hit){
  vec3 col=vec3(0);
  float d,t=0.;
  hit=false;
  for(int i=0;i<100;i++){
    d=map(rp+rd*t);
    if(abs(d)<.0001){
      hit=true;
      break;
    }
    t+=d;
  }
  rp+=t*rd;

  vec3 ld=normalize(-rp);

  vec3 n=calcN(rp);
  vec3 ref=reflect(rd,n);

  float diff=max(dot(ld,n),0.);
  float spec=pow(max(dot(reflect(ld,n),rd),0.),20.);
  float fog=exp(-t*t*.2);

  d=length(rp-pos)-.3;
  float mat=smoothstep(.01,.1,d);
  float phase=length(rp)*4.-time*2.;
  vec3 al=hsv(floor(phase/pi)*pi*.4,.8,1.);
  al=mix(vec3(.9),al,mat);
  float f0=mix(.01,.8,mat);
  float m=mix(.01,.9,mat);
  float fs=f0+(1.-f0)*pow(1.-dot(ref,n),5.);
  float lp=3./abs(sin(phase));

  col+=al*diff*(1.-m)*lp;
  col+=al*spec*m*lp;
  col=mix(vec3(0),col,fog);

  col*=ra;
  ra*=al*fs*fog;

  rp+=.01*n;
  rd=ref;

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(gl_FragCoord.x / iResolution.x, gl_FragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1)*.5;
  pos=sin(vec3(13,0,7)*time*.1);

  vec3 col=vec3(0);

  vec3 ro=vec3(0,-.3,1.9);
  ro.zx*=rot(time*.1);

  vec3 rd=normalize(vec3(uv,-2));
  rd.zx*=rot(time*.1);

  vec3 ra=vec3(1);
  bool hit=false;

  col+=march(ro,rd,ra,hit);
  if(hit)col+=march(ro,rd,ra,hit);
  if(hit)col+=march(ro,rd,ra,hit);

  col=pow(col,vec3(1./2.2));

  fragColor = vec4(col,1.);
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define M(p)vec2(asin(sin(atan(p.x,p.y)*6.))/8.,1)*length(p)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g=0.,e,t=iTime;++i<99.;){
        p=g*d-vec3(2,2,9);
        p=R(p,
            normalize(R(vec3(1,2,3),vec3(.577),t*.3))
            ,t*.2);
        for(int j=0;j++<4;){
            p.xy=M(p.xy);
            p.y-=3.2+sin(t*.2)*.6;
            p.yz=M(p.yz);
            p.z-=6.5;
        }
        g+=e=dot(sqrt(p*p+.005),normalize(vec3(1.6,2.1,.9)))-1.;
        O.xyz+=mix(vec3(1),H(length(p)*3.),.3)*.015*exp(-3.*i*i*e);
    }
    O*=O;
}

precision highp float;


mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

const float pi = acos(-1.0);
const float pi2 = pi*2.0;

vec2 pmod(vec2 p, float r) {
    float a = atan(p.x, p.y) + pi/r;
    float n = pi2 / r;
    a = floor(a/n)*n*sin(iTime*0.01);
    return p*(rot(-a));
}

float box( vec3 p, vec3 b ) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float ifsBox(vec3 p) {
    for (int i=0; i<5; i++) {
        p = abs(p) - 1.0;
        p.xy *= rot(iTime*0.3);
        p.xz *= rot(iTime*0.1);
    }
    p.xz *= rot(iTime);
    return box(p, vec3(0.4,0.8,0.3));
}

float map(vec3 p, vec3 cPos) {
    vec3 p1 = p;
    p1.x = mod(p1.x-5., 10.) - 5.;
    p1.y = mod(p1.y-5., 10.) - 5.;
    p1.z = mod(p1.z, 32.)-16.;
    p1.xy = pmod(p1.xy, 5.0);
    return ifsBox(p1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 cPos = vec3(0.0,0.0, sin(iTime)*0.1);
    vec3 cDir = normalize(vec3(0.0, 0.0, -1.0));
    vec3 cUp  = vec3(0., 1.0, 0.0);//sin(iTime) in x for spin
    vec3 cSide = cross(cDir, cUp);

    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir);

    float acc = 0.;
    float acc2 = 0.0;
    float t = 0.0;
    for (int i = 0; i < 60; i++) {
        vec3 pos = cPos + ray * t;
        float dist = map(pos, cPos);
        dist = max(abs(dist), 0.014);
        float a = exp(-dist*7.0);
        if (mod(length(pos)+22.0*iTime, 30.0) < 2.0) {
            a *= 1.6;
            acc2 += a;
        }
        acc += fract(a*1.2);
        t += dist * 0.7;
    }

    vec3 col = vec3(acc * 0.01, acc * 0.011 + acc2*0.007, acc * 0.013+ acc2*0.006);
    fragColor = vec4(col, 1.0 - t * .5);
}

// from https://thebookofshaders.com/07/
// Distance fields

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

  vec3 color = vec3(0.0);
  float d = 0.0;

  // normalize 
  vec2 st = fragCoord.xy/iResolution.xy;

  // Remap the space to -1. to 1.
  st = st *2.-1.;

  // account for aspect ratio
  st.x *= iResolution.x/iResolution.y;

  // Make the distance field
  d = length( abs(st) -0.75 );

  // Visualize the distance field
  fragColor = vec4(vec3(fract(d*10.0 * iTime)),1.0);

}

#define R           iResolution
#define M           iMouse
#define T           iTime
#define PI          3.14159265358
#define PI2         6.28318530718
#define MIN_DIST    .0001
#define MAX_DIST    50.

// globals & const
vec3 hit,hitPoint;
vec4 gtile,stile;
mat2 flip;

const vec3 colorA = vec3(0.890,0.722,0.110);
const vec3 colorB = vec3(0.039,0.322,0.741);
const vec3 colorC = vec3(0.831,0.125,0.047);
// cell size
const float size = 2.75;
const float hlf = size/2.;
// grid size
const vec2  grid = vec2(5,2);
// tube size
const float thick = .85/size;
// floor tile scale
const vec3 bs = vec3(hlf*.95);
// random hash - in a steady state
float u_hash = 0.12482938;

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 p){return fract(sin(dot(p,vec2(23.43,84.21)))*4832.3234); }

float box(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(q,0.))+min(max(q.x,max(q.y,q.z)),0.);
}
float cap(vec3 p,float r,float h){
    vec2 d = abs(vec2(length(p.xy),p.z))-vec2(h,r);
    return min(max(d.x,d.y),0.)+length(max(d,0.));
}

float trs( vec3 p,vec2 t){
    vec2 q = vec2(length(p.zx)-t.x,p.y);
    return length(q)-t.y;
}

vec2 map(vec3 p){
    vec2 res = vec2(1e5,0.);
    p.yz*=flip;
    vec3 q = p;

    vec2 id = floor((q.xz + hlf)/size)-grid;

    q.xz = q.xz-size*clamp(round(q.xz/size),-grid,grid);
    vec3 fq = q;

    float ht = .75+.75*sin(p.x*.3+T*.8);
    q.y-=ht;
    float hs = hash21(id+u_hash);
    if(hs>.5) q.z*=-1.;

    vec2 d2 = vec2(length(q.xz-hlf), length(q.xz+hlf));
    vec2 gx = d2.x<d2.y ? vec2(q.xz-hlf) : vec2(q.xz+hlf);
    vec3 tq = vec3(gx.x,q.y,gx.y);

    float xhs = fract(2.*hs+id.y);
    float rhs = fract(hs+id.x);
    float trh = 1e5;
    if(rhs>.9){
        trh = length(vec3(abs(q.x),q.yz)-vec3(hlf,.25,0))-thick;
        trh = min(length(vec3(q.xy,abs(q.z))-vec3(0,.25,hlf))-thick,trh);
    }else if(rhs>.7){
        //if(fract(xhs+id.x)>.75) q.zx*=rot(1.5707);
        float qy = .15+.15*sin((q.z+.5)*size);
        float qx = .15+.15*sin((q.x+.5)*size);
        trh = cap(q-vec3(0,.25+qy,0),hlf,thick);
        trh = min(cap(q.zyx-vec3(0,.25-qx,0),hlf,thick),trh);
    } else{
        trh = trs(tq-vec3(0,.25,0),vec2(hlf,thick));

    }

    float brh=trh;
    trh=max(abs(trh)-.075,-trh);
    trh=max(trh,box(q,bs));

    if(trh<res.x ) {
        float mt = xhs>.725?5.:xhs>.61?4.:xhs>.25?2.:3.;
        gtile.xyz = vec3(id,1.);
        gtile.w=mt;
        res = vec2(trh,mt);
        hit = tq;
    } 

    float bls = cap(vec3(q.xy,abs(q.z))-vec3(0,.25,hlf),thick*.4,thick*1.1);
    bls = min(cap(vec3(q.zy,abs(q.x))-vec3(0,.25,hlf),thick*.4,thick*1.1),bls);

    float crt = cap(vec3(q.xy,abs(q.z))-vec3(0,.25,hlf),thick*.34,thick*1.45);  
    crt = min(cap(vec3(q.zy,abs(q.x))-vec3(0,.25,hlf),thick*.34,thick*1.45),crt);

    bls=max(crt,-bls);

    if(bls<res.x) {
       res = vec2(bls,1.);
       hit = q;
    } 

    float flr = box(fq+vec3(0,2,0),vec3(bs.x,.05,bs.xy));
    flr=max(flr,-brh);
    if(flr<res.x) {
       res = vec2(flr,7.);
       gtile.xyz = vec3(id,0.);
       gtile.w=6.;
       hit = fq;
    } 

    return res;
}
// Tetrahedron technique @iq
// https://www.iquilezles.org/www/articles/normalsSDF
vec3 normal(vec3 p, float t)
{
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

float stripes(vec3 p){
    vec2 uv = p.xz;
    uv*=rot(.785);
    vec2 ff = floor(uv);
    float f = clamp(mod(ff.x,2.)*1.-.5,0.,1.);
    float h = mix(1.,.0,f);
    return h;
}

void mainImage( out vec4 O, in vec2 F )
{

    vec2 uv = (2.* F.xy-R.xy)/max(R.x,R.y);
    uv = floor(uv*(216.*R.x/R.y))/(126.*R.x/R.y);

    u_hash+=floor(T*.3);
    vec3 ro = vec3(0, -1.5, 12.5);
    vec3 rd = normalize(vec3(uv, -1.0));

    // mouse //
    float x = M.z < 0. || M.xy==vec2(0) ? 0.0 : (M.y/R.y*2.-1.)*PI;
    float y = M.z > 0. ? (M.x/R.x * 2. - 1.) * PI : T*.1;
    mat2 rx =rot(-.65);
    mat2 ry =rot(y);
    flip=rot(x);
    ro.zy*=rx;rd.zy*=rx;
    ro.xz*=ry;rd.xz*=ry;

    vec3 C = vec3(.0);
    float m = 0.;
    float d = 0.;
    vec3 p = ro;

    for(int i=0;i<128;i++)
    {
        p = ro + rd * d;
        vec2 ray = map(p);
        if(abs(ray.x)<MIN_DIST*d||d>MAX_DIST)break;
        d += i<32? ray.x*.75: ray.x *.95;
        m  = ray.y;
    } 


    float alpha = 1.;
    if(d<MAX_DIST)
    {

        hitPoint = hit;
        alpha *=1e-5;
        vec3 n = normal(p,d);
        vec3 lpos =  vec3(0,15,-5.);
        vec3 l = normalize(lpos);
        float spec = pow(max(dot(reflect(l, n), rd ), .1), 32.)*.75;
        float shdw = 1.0;
        float t=.001;
        for( int i =1; i<20; i++ ) {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 24.*h/t);
            t += h;
            if( shdw<MIN_DIST || t>32. ) break;
        }

        vec3 h = vec3(.0);
        float diff = clamp(dot(n,l),0. , 1.);
        if(m==1.) {h=vec3(.43);}
        if(m==2.) {h=colorB;}
        if(m==3.) {h=colorC;}
        if(m==4.) {h=colorA;}
        if(m==5.) {h=vec3(.075);}
        if(m==7.) {h=mix(colorB*.3,colorB*.1,stripes(hitPoint));}

        h = mix(h,h*shdw,.15);
        if(diff<.3) h=clamp(h*.5,vec3(0),vec3(1));
        if(diff<.4&&(mod(F.x,4.)==mod(F.y,4.))) h*=.3;
        if(shdw<.5&&(mod(F.x,2.)==mod(F.y,2.))) h*=.2;
        if(spec>.25) h=clamp(h*1.15,vec3(0),vec3(1));
        if(spec>.5&&(mod(F.x,2.)==mod(F.y,2.))) h=clamp(h*1.5,vec3(0),vec3(1));
        C = h;
    }
    float mask = smoothstep(.1,.7,length(uv)-.5);
    vec3 clr = mix(colorA,colorA*.05 ,mask );

    uv*=rot(.785);
    vec2 ff = floor(uv*8.);
    float f = clamp(mod(ff.x,2.)*1.-.5,0.,1.);
    clr = mix(clr*.5,clr*.2,f);
    C = mix(C,clr,alpha);
    if(C.r<.2&&C.g<.2&&C.b<.2) C = hash21(uv)>.5 ? C+.005 : C;
    C=pow(C, vec3(.4545));
    O = vec4(C,1.0);
}

// CC0 : Saturday abstract logo
//  Tinkering with recreating an abstract logo.
//  Not perfect but good enough for my standard.
//  Inspired by: https://www.logomoose.com/featured/abstract-logo/

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define PI_2        (0.5*3.141592654)
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

const float logo_radius= 0.25;
const float logo_off   = 0.25;
const float logo_width = 0.1;

float rcp(float x) {
  return 1.0 / x;
}

//P. Gilcher '21, strange approximation
// Source found at: https://www.shadertoy.com/view/flSXRV
float fast_atan2(float y, float x) {
  float cosatan2 = x * rcp(abs(x) + abs(y));
  float t = PI_2 - cosatan2 * PI_2;
  return y < 0.0 ? -t : t;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float stripes(float d) {
  const float cc = 0.42;
  d = abs(d)-logo_width*cc;
  d = abs(d)-logo_width*cc*0.5;
  return d;
}

vec4 merge(vec4 s0, vec4 s1) {
  bool dt = s0.z < s1.z; 
  vec4 b = dt ? s0 : s1;
  vec4 t = dt ? s1 : s0;

  b.x *= 1.0 - exp(-max(80.0*(t.w), 0.0));

  vec4 r = vec4(
      mix(b.xy, t.xy, t.y)
    , b.w < t.w ? b.z : t.z 
    , min(b.w, t.w)
    );

  return r;
}

vec4 figure_8(vec2 p) {
  float aa = 2.0/RESOLUTION.y;
  vec2  p1 = p-vec2(logo_off, -logo_off);
  float d1 = abs(circle(p1, logo_radius));
  float a1 = fast_atan2(-p1.x, -p1.y);
  float s1 = stripes(d1);
  float o1 = d1 - logo_width;

  vec2  p2 = p-vec2(logo_off, logo_off);
  float d2 = abs(circle(p2, logo_radius));
  float a2 = fast_atan2(p2.x, p2.y);  
  float s2 = stripes(d2);
  float o2 = d2 - logo_width;

  vec4 c0 = vec4(smoothstep(aa, -aa, s1), smoothstep(aa, -aa, o1), a1, o1);
  vec4 c1 = vec4(smoothstep(aa, -aa, s2), smoothstep(aa, -aa, o2), a2, o2);

  return merge(c0, c1);
}

vec4 clogo(vec2 p, out float d) {
  p *= ROT(TAU*TIME/60.0);

  float sgn = sign(p.y);
  p *= sgn;

  vec4 s0 = figure_8(p);
  vec4 s1 = figure_8(p*ROT(PI/4.0));
  vec4 s2 = figure_8(p-vec2(-0.5, 0.0));
  vec4 s3 = figure_8((p*ROT(5.0*PI/4.0)));

  // This is very hackish to get it to look reasonable

  const float off = -PI;
  s1.z -= off;
  s3.z -= off;

  vec4 s = s0;
  s = merge(s, s1);
  s = merge(s, s2);
  s = merge(s, s3);

  d = s.w;
  return vec4(vec3(1.0)*s.x, s.y);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/index.htm
vec3 postProcess(vec3 col, vec2 q) {
  //  Found this somewhere on the interwebs
  col = clamp(col, 0.0, 1.0);
  // Gamma correction
  col = pow(col, 1.0/vec3(2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  // Vignetting
  col*= 0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

