}

vec4 Brow(vec2 uv, float smile){

    float offs = mix(.2, 0., smile);
    uv.y += offs;

    float y = uv.y;
    uv -= .5;
    uv.y += uv.x*.9-.02;
    uv.x -= .2;
    vec4 col = vec4(0.);

    float blur = .1;
    float d1= length(uv);
    float s1 = S(.5, .5-blur, d1);
    float d2 = length(uv-vec2(.05, -.2)*.4);
    float s2 = S(.53, .57-blur, d2);

    float browMask = sat(s1-s2);

    float colMask = remap01(.7, .8, y) *.75;
    colMask*= S(.6, .9, browMask);
    vec4 browCol = vec4(.4, .2, .2, 1.);
    col = mix(col, browCol, S(.2, .4, browMask));

    return col;
}

vec4 Eye(vec2 uv, float side, vec2 m, float smile){
    uv -=.5;
    uv.x *= side;

    float d = length(uv);
    vec4 irisCol = vec4(.3, .5, 1., 1.);
    vec4 col = mix(vec4(1.), irisCol, S(.1, .7, d)*.5);
    col.a = S(.5, .48, d);

    col.rgb *= 1. - S(.37, .42, d)*sat(uv.y-uv.x*side);

    float e = length(uv-m*.5);

    col.rgb = mix(col.rgb, vec3(0.), S(.3, .28, e));

    irisCol.rgb *= 1. + S(.3, .05, e); 

    float irisMask = S(.28, .26, e);
    col.rgb = mix(col.rgb, irisCol.rgb, irisMask);

    float g = length(uv-m*.8);

    float pupilSize = mix(.4, .16, smile);
    float pupilMask = S(pupilSize, pupilSize*.85, g);
    pupilMask*=irisMask;
    col.rgb = mix(col.rgb, vec3(0.), pupilMask);

    float t = iTime*5.;
    vec2 offs = vec2(sin(t+uv.y*25.), sin(t+uv.x*25.));
    offs *= .01*(1.-smile);

    uv+= offs;
    float highlight = S(.1, .09, length(uv-vec2(-.1, .1)));
    highlight += S(.07, .05, length(uv-vec2(.12, -.07)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    col.a = S(.42, .4, d);
    return col;
}

vec4 Mouth(vec2 uv, float smile){

    uv-=.5;
    vec4 col = vec4(.5, .18, .05, 1.);

    uv.y *=1.5;
    uv.y -= uv.x*uv.x*2.*smile;

    uv.x *= mix(2., 1., smile);

    float d = length(uv);
    col.a = S(.42, .4, d);

    vec2 tUv = uv;
    tUv.y += abs(uv.x)*.5;
    float td = length(tUv-vec2(0., .6));

    vec3 toothCol = vec3(1.)*S(.45, .35, d);
    col.rgb = mix(col.rgb, toothCol, S(.4, .37, td));

    td = length(uv+vec2(0., .5));
    col.rgb = mix(col.rgb, vec3(1., .5, .5), S(.5, .2, td));
    return col;
}

vec4 Head(vec2 uv){

    vec4 col = vec4(.9, .65, .1, 1.);

    float d = length(uv);

    col.a = S(.5, .49, d);

    float edgeShade = remap01(.35, .5, d);
    edgeShade *= edgeShade;
    col.rgb *= 1.-edgeShade*.5;

    col.rgb = mix(col.rgb, vec3(.6, .3, .1), S(.47, .48, d));

    float highlight = S(.41, .405, d);
    highlight *= remap(.41, -.1, .75, 0., uv.y);
    highlight *= S(.13, .15, length(uv-vec2(.21, .1)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);



    d = length(uv-vec2(.25, -.15));
    float cheek = S(.25, .01, d);
    cheek *= S(.2, .1, d);
    col.rgb = mix(col.rgb, vec3(1., .5, .1), cheek);

    return col;
}

vec4 Smiley(vec2 uv, vec2 m, float smile){

    vec4 col = vec4(0.);

    float side = sign(uv.x);
    uv.x = abs(uv.x);
    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv, vec4(.03, -.1, .37, .25)), side, m, smile);
    vec4 mouth = Mouth(within(uv, vec4(-.3, -.4, .3, -.1)), smile);
    vec4 brow = Brow(within(uv, vec4(.03, .2, .3, .35)), smile); 

    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);
    col = mix(col, brow, brow.a);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -=.5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 m = iMouse.xy /iResolution.xy;
    m -= .5;


    uv -= m*(.25-dot(uv, uv));
    float smile = cos(iTime)*.5+.5;

    fragColor = Smiley(uv, m, smile);
}

#define B (1.-fract(t*2.))
#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.,t=iTime;
    for(;i++<99.;){
        p=d*g;
        p.z-=-t*2.5;
        p=R(p,vec3(.577),clamp(sin(t/4.)*6.,-.5,.5)+.7);
        p=asin(sin(p/2.))*3.; 
        vec4 q=vec4(p,.5);
        s=2.;
        for(int i=0;i++<8;){
            q=abs(q);
            q=q.x<q.y?q.zwxy:q.zwyx;
            s*=e=9./min(dot(q,q),6.);
            q=q*e-vec4(5.,2.+B,4.+B,8.);
        }
        g+=e=abs(length(q.zw)/s)+.001;
        c+=mix(vec3(1),H(log(s)*.2),.8)*2e-4/e;   
    }  
    c*=c*c;
    O=vec4(c,1);
}

/* 
This Voronoi Shader is based on:

1. An article by IQ: 
    https://www.iquilezles.org/www/articles/voronoilines/voronoilines.htm
2. Tomkh's method where you get borders based around uv\
    https://www.shadertoy.com/view/llG3zy
3.Shane's Rounded border shader::
    https://www.shadertoy.com/view/ll3GRM
4. Tomkh, Abje and glk7 all have some sort of voronoi traversal
   algorithm

=============================================================

An important discorvery I made, assuming I am understanding
tomkh's and Iq's work is that actually:

It seems in this case that IQ's method makes 
more sense than tomkh's. If we want to find borders 
so we can find the nearest intersection, we actually 
need to find all the borders around pointA, NOT around 
the uv (or in this case the ro).

This way we are garanteed that the edges will be 
the ones that fully enclose the cell.

If you check for borders around ro, you will get borders
close to ro for the 3x3 loop which may not find the borders
in the direction ro+rd*t is even going. So you would then need 
a 5x5 loop to do that which is super slow.

==========================================================

Here is a voronoi traversal algorithm I came up with.
There are a few hacks involved:

1. When I check edges for a hit I actually check a 5x5 neighborhood
because otherwise the way I do things there are times when not all
the correct edges are drawn.

2. The weirdest thing. I find I can't use a constant value 
(such as ray origin) as an input to this algorithm. It has to 
be moving even a little bit so I jitter with sin(iTime)*0.001

No idea why. A mouse position will work find and give me the edges, 
but a set value like vec2(1.,0.) will not. 

EDIT: The jitter can be a constant value, we just need to 
be a tiny bit off from pointA when we checkk pointB for some reason.
so eps = 0.001 wo

*/

//I used this visual to create "Border Skipping"
//where we don't check every border
//https://www.shadertoy.com/view/7tcSDn

#define pi acos(-1.)
#define eps 8./iResolution.y

#define maxPoints 16.
#define screenSize 4.

#define bg (0.5+0.5*sin(vec3(iTime,iTime*3.+403.,iTime*1.3+902.)))
#define fg (0.5+0.5*sin(vec3(-iTime*2.1,-iTime*0.9+403.,-iTime+902.)))

vec2 rnd2(vec2 id){
    return vec2(fract(sin(dot(id,vec2(14.27,74.97)))*54329.34),
           fract(sin(dot(id+912.35,vec2(49.27,102.74)))*54329.34));
}

mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

float smin2(float a, float b, float r)
{
   float f = max(0., 1. - abs(b - a)/r);
   return min(a, b) - r*.25*f*f;
}

float point(vec2 uv, float r){
    return smoothstep(r+eps,r-eps,length(uv));
}

float ring(vec2 uv, float r){
    return smoothstep(eps+0.03, 0., abs(length(uv)-r+0.03));
}

float line(vec2 P, vec2 A, vec2 B, float r){
    vec2 PA = P-A;
    vec2 AB = B-A;
    float t = clamp(dot(PA,AB)/dot(AB,AB),0.,1.);
    return smoothstep(r+eps,r-eps,length(PA-AB*t));
}

/***************************************************

Here is the actual voronoi border finding algorithm
using IQ's method of search for point Bs around PointA.
This is more effective in the case where you have a single
cell that you want to find all the borders around.

Using just a given point in the cell (like a mouse position)
would lead to you finding all the borders closest to the 
moust position. When we only look for 9 borders (3x3 double for loop)
we risk getting some of the wrong borders.

When we instead check for borders around the "pointA"
that we found from the first voronoi loop, we get all the 
correct borders!

*************************************************** */


vec4 voronoiLine(vec2 uv, vec2 m, inout vec3 col){

    vec2 st = fract(m);
    vec2 stFL = floor(m);
    vec2 d = vec2(10.);
    vec2 pointA;
    vec2 pointB;


    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

            vec2 id = vec2(i,j);
            vec2 rndShift = rnd2(stFL+id);
            vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime);
            vec2 testA = stFL + coords;
            float c = length(coords.xy - st);

            if(c < d.x){
                d.x = c;
                pointA = testA;

                }
             }
          }
    col = mix(col,vec3(1.,1.,0.),
                      clamp( point(uv-pointA, 0.1), 0., 1.));

    //we want to find the next point from pointA to get 
    //correct edges. Like in IQ's article
    st = fract(pointA);
    stFL = floor(pointA+sin(iTime)*0.001);
    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){   

            vec2 id = vec2(i,j);
            vec2 rndShift = rnd2(stFL+id);
            vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime);
            vec2 testB = coords+stFL;

            //check edge

            if(length(testB-pointA) > 0.001){
                vec2 edgePoint = (pointA+testB) / 2.;

                col = mix(col,vec3(0.,0.,1.),
                      clamp( point(uv-edgePoint, 0.1), 0., 1.));

                vec2 perp = normalize(testB-pointA).yx*vec2(-1.,1.);
                vec2 edgeSideA = edgePoint - perp;
                vec2 edgeSideB = edgePoint + perp;

                col = mix(col, vec3(1.), 
                          line(uv, edgeSideA, edgeSideB, 0.04));
            }
        }
    }

    return vec4(pointA, d);
}

