vec4 voronoi(vec2 uv, inout vec2 pos){

    vec2 st = fract(uv);
    vec2 stFL = floor(uv);
    vec2 d = vec2(10.);
    vec2 A, B=vec2(100.);
    pos = stFL;
    vec2 mind;

    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime) - st;

        float c = length(coords.xy);

        if(c < d.x){
            d.x = c;
            d.y = (stFL+id).x*20.+(stFL+id).y;
            A = coords;
            pos = stFL+id;
            }
        }
    }


    mind = d;

    d = vec2(10.);

    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime) - st;

        float c = length(coords.xy);

        if(length(A-coords) > 0.00){
            B.x = smin2(B.x, dot( 0.5*(A+coords), 
                       normalize(coords-A) ), 0.06 );
            }
        }
    }
    B.y = B.x;
    return vec4(mind,0.5+0.5*sin(pow(B*100.,vec2(1./2.))));

}

