#define AA 0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera movement	
        float an =0.0;//0.25*iTime;
    float hh = mix(0.75,1.5, 0.5+sin(iTime)*0.5);

	vec3 ro = vec3( 3.*cos(an), hh, 3.*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 20.0;
        float t = 0.0;
        for( int i=0; i<128; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting
        vec3 col = vec3(0.4,0.5,0.9);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            vec3 basecol = GetCol(basemat,pos);
            col = vec3(0.1,0.1,0.1)*amb + basecol*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv -= 0.2 * vec2(cos(0.2 * iTime), sin(0.2 * iTime));

    float a = atan(uv.y, uv.x);
    float r = length(uv);

    float sc = 12. + 1. * cos(10. * uv.x + iTime);
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float v = h21(ipos);
    float t = 11. * v + iTime;
    vec2 p = cos(t) * 0.2 * vec2(cos(2. * v * t), sin(2. * (1.-v) * t));
    float d = mlength(fpos - p);
    float k = 0.5 + 0.4 * cos(t);
    float s = smoothstep(-k,k, 0.25 + 0.25 * thc(4., 20. * v + iTime) - d);
    s *= 2. * s;
    vec3 col = step(d, 0.45) * pal(1. * mlength(uv) + 0.08 * fract(s + atan(fpos.y, fpos.x)/pi + t) - 0.2 * t, vec3(0.6), vec3(0.6), vec3(1.), 0.22 * (1. + cos(ceil(4. * v) * s + t)) * vec3(0.,0.33,0.66));

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926535897932;

float atanM(float y, float x){
    if(x >= 0.){
        return atan(y/x)/pi/2.+.75;
    }
    return (pi+2.*atan(y/x))/4./pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int amount = 2;
    float width = 2.5;
    float speed = 0.05;
    float speed2 = 0.075;
    float speed3 = 0.10;
    vec3 color1 = vec3(2.0, 2.6, 2.9);
    vec3 color2 = vec3(-0.1, 0.0, 0.1);
    vec3 color12 = vec3(1.0, 2.5, 2.0);
    vec3 color22 = vec3(-0.11, 0.11, 0.11);
    vec3 color13 = vec3(2.3, 2.9, 2.0);
    vec3 color23 = vec3(-0.01, 0.01, 0.01);


    vec2 R = iResolution.xy;
    vec2 uv = fragCoord-R/2.;
    vec3 col;
    vec3 col2;
    vec3 col3;
    float d = length(uv);
    float d2 = length(uv)/50.;
    float d3 = length(uv)/80.0;
    d -= iTime*speed;
    d2 -= iTime*speed2;
    d3 -= iTime*speed3;

    float mascara = smoothstep(R.y/2.+width*2., R.y/2.-width*2., (length(uv)));
    float mascara2 = smoothstep(R.y/2.+width*2., R.y/2.-width*2., tan(tan(length(uv.x*30.) + length(uv.y/-30.))));

    col = vec3(tan(tan(2.*pi*atanM(uv.y, uv.x)+d*width)*float(amount))+1.)/2.;
    col2 = vec3(cos(tan(4.*pi*atanM(uv.y, uv.x)+d2*width)*float(amount))+1.)/2.;
    col3 = vec3(cos(tan(2.*pi*atanM(uv.y, uv.x)+d3*width)*float(amount))+1.)/2.;
    col = color1+col2*(color2-color1);
    col *= mascara;
    col2 = color23+col3*(color13-color22);
    col2 *= mascara2;
    col3 = color22+col2*(color12-color23);
    col3 *= mascara2;
    fragColor = vec4(col-col2-col3,1.0);
}

#define MAX_STEPS 500

float sdSphere(in vec3 pos, float rad){
    float d = length(pos)-rad;
    return d;
}

float distBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float smin(in float a, in float b, float k){
    float h = max(k - abs(a-b), 0.);
    return min(a,b) - h*h/(k*4.);
}

float smax(in float a, in float b, float k){
    float h = max(k - abs(a-b), 0.);
    return max(a,b) + h*h/(k*4.);
}

