vec2 getDist(in vec3 pos, float aTime){

    // ground
    float d = pos.y;

    vec2 res = vec2(d, 1.);

    vec3 cen = vec3(0.5, 0.9, 0.);
    d = sdSphere(pos-cen, .35);
    if(d<res.x) res = vec2(d, 2.);

    cen.x *= -1.;
    d = min(d, distBox(pos-cen, vec3(0.3, 0.3, 0.3)));
    if(d<res.x) res = vec2(d, 3.);

    return res;
}

vec2 map(in vec3 pos, float time) {
    vec2 d = getDist(pos, time);

    float d2 = pos.y - (-.25);

    return (d2<d.x) ? vec2(d2, 1.) : d;
}

vec3 calcNormal(in vec3 pos, float time) {
    vec2 e = vec2(0.0001, 0.);
    return normalize(vec3(map(pos+e.xyy, time).x-map(pos-e.xyy, time).x,
                          map(pos+e.yxy, time).x-map(pos-e.yxy, time).x,
                          map(pos+e.yyx, time).x-map(pos-e.yyx, time).x));
} 

vec2 rayMarch(in vec3 ro, in vec3 rd, float time) {
    vec2 res = vec2(-1.,-1.);

    float tmin = .5;
    float tmax = 20.;

    float t = tmin;
    for(int i=0; i<MAX_STEPS && t<tmax; i++) {

        vec3 pos = ro + rd * t; 
        vec2 h = map(pos, time);

        if(abs(h.x)<(.001*t)) {
            res = vec2(t, h.y);
            break;
        }
        t += h.x;
    }  

    return res;
}

float calcSoftshadow(in vec3 ro, vec3 rd, float time){
    float res = 1.0;

    float tmax = 12.0;
    float tp = (3.4-ro.y)/rd.y;

    if( tp>0.0 ) tmax = min( tmax, tp );  

    float t = 0.02;
    for( int i=0; i<50; i++ )
    {
		float h = map( ro + rd*t, time ).x;
        res = min( res, mix(1.0,16.0*h/t, 1.) );
        t += clamp( h, 0.05, 0.40 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float calcOcclusion(in vec3 pos, in vec3 nor, float time) {

    float occ = 0.;
    float sca = 1.;

    for(int i=0; i<5; i++) {
        float h = .01 +.11 * float(i)/4.;
        vec3 opos = pos + h * nor;
        float d = map(opos, time).x;
        occ += (h-d)*sca;
        sca *= .95;
    }
    return clamp(1. - 2.*occ, 0., 1.);
}

vec3 render(in vec3 ro, vec3 rd, float time) {

    //sky
    vec3 col = vec3(0.5, .8, .9) - max(rd.y, 0.)*.5;

    vec2 res = rayMarch(ro, rd, time);

    if (res.y > 0.) {
        float t = res.x;
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos, time);
        vec3 ref = reflect(rd, nor);

        vec3 mat = vec3(0.);

        if(res.y<1.5) //floor material
        {
            mat += .2*vec3(.11, .2,.04);

        } else if(res.y<2.5) // sphere
        {
            mat = vec3(.2, .1, .02);

        } else if(res.y<3.5) // boc
        {
            mat = vec3(.2, .1, .02);
        }

        //see normal
        //col = vec3(nor)*.5+.5;

        //simple lighting
        //vec3  light_pos = normalize( vec3(0.0, 0.35, 0.5) );
        //float light = clamp(dot( nor, light_pos ), 0.0, 1.0 );

        //col = vec3(light);//*.5+.5;

        // lighting 2 
        float occ = calcOcclusion(pos, nor, time);
        float fre = clamp(1.0+dot(nor,rd),0.0,1.0);

        vec3 sun_dir = normalize( vec3(cos(time) * .08, 0.4, sin(time)) );
        float sun_dif = clamp( dot(nor, sun_dir), 0.0, 1.0 );
        float sun_sha = step(rayMarch( pos+0.001*nor, sun_dir,time ).y, 0.);

        float sky_dif = clamp( 0.5 + 0.5*dot(nor, vec3(0.0, 1.0, 0.0)), 0.0, 1.0 );
        float bou_dif = clamp( 0.5 + 0.5*dot(nor, vec3(0.0, -1.0, 0.0)), 0.0, 1.0 );

        float sky_ref = smoothstep(0., .3, ref.y);

        col = mat*vec3(10.0,6.0,3.0)*sun_dif*sun_sha;
        col += mat*vec3(.4, .7, 1.)*sky_dif*occ;
        col += mat*vec3(.40,1.0,.40)*bou_dif*occ;
        col += mat*vec3(.2,.6,.5)*fre*8.*(.5+.5*sky_dif*occ);

        col += sky_ref*.05*vec3(.4, .7, 1.)*sky_dif*occ;

    }

    return col;
}

mat3 setCamera(in vec3 ro, in vec3 ta, float cr) {

    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = cross(cu, cw);
    return mat3(cu, cv, cw);
}

