void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.);
    vec2 m = iMouse.xy/iResolution.xy;

    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 offset = -.5+vec2(float(m), float(n))/float(AA);

        vec2 p = (2.0*(fragCoord+offset)-iResolution.xy)/iResolution.y;
        float time = iTime;
        time *= .9;

        //camera
        float an = 10.*iMouse.x/iResolution.x;
        float cd = .0;//.9*sin(time);
        vec3 ta = vec3(.0, .9, .2+cd);
        vec3 ro = ta + vec3(1.5*sin(an),.0,1.5*cos(an));

        mat3 cam = setCamera(ro, ta, 0.);

        vec3 rd = cam * normalize(vec3(p, 1.8));

        vec3 col = render(ro, rd, time);

        //gama correction
        col = pow(col, vec3(.4545));

        tot += col;
    }

    tot /= float(AA*AA);

    fragColor = vec4(tot,1.0);
}

