#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec2 Hashv2f (float p);
float Fbm2 (vec2 p);

vec3 ltDir, qHit, pStomp;
vec2 pAct, pActP;
float tCur, dstFar, boxSz, tCyc, tPhs;
int idObj;
int idPlat = 1, idBallR = 2, idBallM = 3, idStomp = 4, idTube = 5, idGrnd = 6;
const float pi = 3.1415927;

struct TbCon {
  vec3 pLo, pHi;
  vec2 aLimCs, tRotCs[2], pRotCs[2];
  float chLen, chDist, ang, rad;
};
TbCon tbCon;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, db, a;
  dMin = dstFar;
  q = p;
  db = PrBox2Df (q.xz, vec2 (boxSz));
  d = max (PrBoxDf (q, vec3 (vec2 (boxSz + 0.5), 0.5).xzy), - max (db,
     PrCylDf (vec3 (fract (q.xz) - 0.5, q.y - 0.5), 0.4, 0.8)));
  DMINQ (idPlat);
  d = max (db, - PrBox2Df (q.xz - pAct, vec2 (0.5)));
  q.xz = fract (q.xz) - 0.5;
  d = max (PrSphDf (vec3 (q.xz, q.y - 0.15).xzy, 0.4), d);
  DMINQ (idBallR);
  q = p;
  q.xz -= pAct;
  q.y -= 0.15;
  if (tPhs < 0.8) q.y -= 0.4 * (1. - cos (10. * pi * tCyc));
  d = PrSphDf (q, 0.4);
  DMINQ (idBallM);
  q = p;
  q -= pStomp;
  d = min (PrCylDf (q.xzy, 0.35, 0.05), max (PrCapsDf (q.xzy, 0.1, 0.3), - q.y));
  DMINQ (idStomp);
  q = p;
  q.xz -= tbCon.pLo.xz;
  d = PrCylDf (q.xzy, 0.4, 0.5);
  DMINQ (idPlat);
  q.y -= 0.5;
  d = max (PrCapsDf (q.xzy, 0.1, 0.3), - q.y);
  DMINQ (idTube);
  d = min (PrSphDf (p - tbCon.pLo, 0.1), PrSphDf (p - tbCon.pHi, 0.1));
  DMINQ (idTube);
  q = p - tbCon.pLo;
  q.xz = Rot2Cs (q.xz, tbCon.tRotCs[0]);
  q.yz = Rot2Cs (q.yz, tbCon.tRotCs[1]) - vec2 (tbCon.chLen, tbCon.chDist);
  a = fract ((256. / tbCon.ang) * atan (q.y, - q.z) / (2. * pi));
  d = max (dot (vec2 (abs (q.y), - q.z), tbCon.aLimCs), length (vec2 (length (q.yz) -
     tbCon.rad, q.x)) - (0.1 - 0.015 * smoothstep (0.15, 0.35, 0.5 - abs (0.5 - a))));
  DMINQ (idTube);
  q = p;
  d = max (q.y - 0.4, - db);
  DMINQ (idGrnd);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  int idObjT;
  idObjT = idObj;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  idObj = idObjT;
  return 0.5 + 0.5 * sh;
}

#define F(x) (sin (x) / x - b)

float SecSolve (float b)
{  // (from "Bucking Bronco")
  vec3 t;
  vec2 f;
  float x;
  if (b < 0.95) {
    t.yz = vec2 (0.7, 1.2);
    f = vec2 (F(t.y), F(t.z));
    for (int nIt = 0; nIt < 4; nIt ++) {
      t.x = (t.z * f.x - t.y * f.y) / (f.x - f.y);
      t.zy = t.yx;
      f = vec2 (F(t.x), f.x);
    }
    x = t.x;
  } else if (b < 1.) {
    x = sqrt (10. * (1. - sqrt (1. - 1.2 * (1. - b))));
  } else {
    x = 0.;
  }
  return x;
}

void SetConf ()
{
  vec3 vp;
  float tubeLen;
  boxSz = 4.;
  tCyc = tCur / 2. + 1.;
  tPhs = fract (tCyc);
  pAct = floor (boxSz * (2. * Hashv2f (1.11 + 17.33 * floor (tCyc)) - 1.)) + 0.5;
  pActP = floor (boxSz * (2. * Hashv2f (1.11 + 17.33 * floor (tCyc - 1.)) - 1.)) + 0.5;
  if (tPhs < 0.8) {
    pStomp.xz = mix (pActP, pAct, tPhs / 0.8);
    pStomp.y = 1.7;
  } else {
    pStomp.xz = pAct;
    pStomp.y = mix (0.5, 2., max (2. * abs (tPhs - 0.9) / 0.1 - 1., 0.));
  }
  tbCon.pLo = vec3 (1.5 * boxSz, 0.9, 0.);
  tbCon.pHi = pStomp;
  tbCon.pHi.y += 0.4;
  vp = tbCon.pHi - tbCon.pLo;
  tbCon.chLen = 0.5 * length (vp);
  tbCon.tRotCs[0] = sin (atan (vp.x, vp.z) + vec2 (0.5 * pi, 0.));
  tbCon.tRotCs[1] = sin (- asin (length (vp.xz) / length (vp)) + vec2 (0.5 * pi, 0.));
  tubeLen = 1.4 * boxSz;
  tbCon.ang = SecSolve (tbCon.chLen / tubeLen);
  tbCon.chDist = tbCon.chLen / tan (tbCon.ang);
  tbCon.rad = length (vec2 (tbCon.chDist, tbCon.chLen));
  tbCon.aLimCs = sin (- tbCon.ang + vec2 (0.5 * pi, 0.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, sh, nDotL;
  SetConf ();
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idPlat) {
      col4 = vec4 (0.5, 0.2, 0., 0.2);
    } else if (idObj == idGrnd) {
      col4 = vec4 (0.5, 0.2, 0., 0.2) * (0.8 + 0.2 * Fbm2 (4. * ro.xz));
    } else if (idObj == idBallR) {
      col4 = vec4 (0., 1., 0., 0.2);
      if (PrBox2Df (ro.xz - pActP, vec2 (0.5)) < 0.) col4 = mix (vec4 (1., 0., 0., 0.2) *
         (0.7 + 0.3 * sin (16. * pi * tCur)), col4, smoothstep (0.2, 0.6, tPhs));
    } else if (idObj == idBallM) {
      col4 = vec4 (1., 0., 0., 0.2);
      if (tPhs > 0.9) col4 = vec4 (0.7, 0.5, 0., 0.2);
    } else if (idObj == idStomp) {
      col4 = mix (vec4 (1., 1., 1., 0.2), vec4 (0., 0., 1., -1.),
         smoothstep (0., 0.02, length (qHit.xz) - 0.25));
    } else if (idObj == idTube) {
      col4 = vec4 (1., 1., 1., 0.2);
    }
    if (idObj == idBallR || idObj == idBallM) col4 *= 0.7 + 0.3 * smoothstep (0., 0.02,
       abs (length (qHit.xz) - 0.2) - 0.02);
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, ltDir), 0.);
      if (idObj == idTube) nDotL *= nDotL;
      sh = ObjSShadow (ro + 0.01 * vn, ltDir);
      col = col4.rgb * (0.2 + 0.8 * sh * nDotL) +
         col4.a * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
      col *= 1. - 0.9 * smoothstep (0.2, 0.3, length (ro.xz) / dstFar);
    } else col = col4.rgb * (0.5 + 0.5 * max (- dot (rd, vn), 0.));
  } else {
    col = vec3 (0.1);
  }
  return clamp (col, 0., 1.);
}

#define AA  0   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.4 * pi, -0.13 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 1., -30.);
  zmFac = 5.5;
  dstFar = 60.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

// green stuff - Del 04/12/2021

vec2 smoothRot(vec2 p,float s,float m,float c,float d){
  s*=0.5;
  float k=length(p);
  float x=asin(sin(atan(p.x,p.y)*s)*(1.0-m))*k;
  float ds=k*s;
  float y=mix(ds,2.0*ds-sqrt(x*x+ds*ds),c);
  return vec2(x/s,y/s-d);
}

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
} 
void mainImage(out vec4 k, vec2 p)
{
    vec3 col1 = vec3(0.01,0.03,0.01);
    vec3 col2 = vec3(0.5,0.9,0.3);
    vec2 uv = (p.xy - 0.5 * iResolution.xy) / iResolution.y;

uv *= rotate(fract(iTime*0.01)*6.28);

    float dd2 = length(uv);
    float dd1 = smoothstep(0.0,0.3,dd2);

    //if (abs(uv.x)>0.2)
        col2 *= 1.0-abs(uv.x*0.75);
        col1 *= 1.0-abs(uv.x*0.75);

    float dnoise = 15.0*(dd1);
    //uv *= rotate(-dnoise*0.05);

    float dns = 0.5+sin(iTime*.45)*0.5;
    dnoise = mix(dnoise,0.0,dns);

    float oo = 0.5+sin(dnoise+uv.y+uv.x*12.0+iTime*0.5)*0.5;
    uv = smoothRot(uv,6.0,0.085,16.0,.075*oo);
    vec2 d = uv*5.0;
    d.x += fract(iTime);
    float v1=length(0.5-fract(d.xy))+0.58;
    d = (uv*1.75);			// zoom
    float v2=length(0.5-fract(d.yy))-0.1525;		// border
    v1 *= 1.2-v2*v1;
    v1 = smoothstep(0.1,0.9,v1);
    vec3 col = mix(col2,col1,v1)*dd1;
    k = vec4(col*4.75,1.0);
}

/* 
This Voronoi Shader is based on:

1. An article by IQ: 
    https://www.iquilezles.org/www/articles/voronoilines/voronoilines.htm
2. Tomkh's method where you get borders based around uv\
    https://www.shadertoy.com/view/llG3zy
3.Shane's Rounded border shader::
    https://www.shadertoy.com/view/ll3GRM
4. Tomkh, Abje and glk7 all have some sort of voronoi traversal
   algorithm
5. My first attempt at a voronoi traversal with comments
   https://www.shadertoy.com/view/NltSRM
=============================================================

A new thing I thought of. We don't need to check 9 borders
(I mean since that's how I've been doing it), because we know
the direction of the rd and we know that we check cells roughly at
specific angles on the cartesian grid based on the for i for j loop.
so we can take the dot product of each id and the rd and if that
angle is greater than 180 degrees we can skip the whole calculation
including the intersection because the ray isn't moving in that
direction. 

My only issue is that the border segments I am testing against
are a set length and that leads to the rare case where a segment
gets intersected with that shouldn't. Increasing the test angle 
(by reducing "TEST_ANGLE") from 0. to -0.1 or even -0.3 should get rid
of this.

So the maximum gain is x2 since for every step we do only 4 intersections
instead of 8.

*/


#define pi acos(-1.)
#define eps 8./iResolution.y

#define maxPoints 16.
#define screenSize 4.

#define bg (0.5+0.5*sin(vec3(iTime,iTime*3.+403.,iTime*1.3+902.)))
#define fg (0.5+0.5*sin(vec3(-iTime*2.1,-iTime*0.9+403.,-iTime+902.)))


///////////////////////////////////////////////////////////////
//
// TEST_ANGLE is the angle between the rd and the id that
// we check. So if rd is (1.,0.) and the current for loop id
// is (-1.,0.) that border will be in close to the opposite
// direction of the rd so we don't need to check for an intersection
// with that border!
//
// 0. fails in only one spot as far as I could tell. 
// So anything more that 180 degrees
// from the rd's direction doesn't need to be checked
// 0.3 is max and you get some issues already there
//
////////////////////////////////////////////////////////////////

//to be safe I went -0.1
#define TEST_ANGLE -0.1

//a method for finding the intersection between
//two rays. Not effienct but very memorable.
float seg_seg(vec2 ro1, vec2 rd1, vec2 A, vec2 B){
    vec2 ro2 = A;
    vec2 rd2 = normalize(B-A);
    float maxABLange = length(B-A);

    vec2 perp = rd1.yx*vec2(-1.,1.);
    float s = (dot(ro1,perp) - dot(ro2,perp))/dot(rd2,perp);
    perp = rd2.yx*vec2(-1.,1.);
    float t = (dot(ro2,perp) - dot(ro1,perp))/dot(rd1,perp);
    return t >= 0. && s >= 0.? t : 100.;
}


vec2 rnd2(vec2 id){
    return vec2(fract(sin(dot(id,vec2(14.27,74.97)))*54329.34),
           fract(sin(dot(id+912.35,vec2(49.27,102.74)))*54329.34));
}

mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

float smin2(float a, float b, float r)
{
   float f = max(0., 1. - abs(b - a)/r);
   return min(a, b) - r*.25*f*f;
}

float point(vec2 uv, float r){
    return smoothstep(r+eps,r-eps,length(uv));
}

float ring(vec2 uv, float r){
    return smoothstep(eps+0.03, 0., abs(length(uv)-r+0.03));
}

float line(vec2 P, vec2 A, vec2 B, float r){
    vec2 PA = P-A;
    vec2 AB = B-A;
    float t = clamp(dot(PA,AB)/dot(AB,AB),0.,1.);
    return smoothstep(r+eps,r-eps,length(PA-AB*t));

}

/***************************************************

Here is the actual voronoi traversal algorithm

*************************************************** */
float voronoiEdgeHit(vec2 uv, vec2 ro, vec2 rd, inout vec3 line_color){

    vec2 st = fract(ro);
    vec2 stFL = floor(ro);
    vec2 d = vec2(10.);

    vec2 pointA;
    vec2 pointB;


    float minT = 100.;
    float t;


    //in this loop we are just looking for the closest point
    //to the curent ro. It's a standard voronoi but instead of uv
    //for every pixel we just have ro (the same value) being looked at
    //every pixel (you wont see "ro" here because of what I did above.
    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime);
        vec2 testA = stFL + coords;
        float c = length(coords.xy - st);//max(abs(coords.x),abs(coords.y));

        if(c < d.x){
            d.x = c;
            d.y = (stFL+id).x*20.+(stFL+id).y;
            pointA = testA;

            }
        }
    }    

    //In this loop I am finding every edge based on Iq's article
    //meaning I get a new st an stFLoor based on pointA
    //and find borders around pointA based on that.

    //so for each edge I check to see where it intersects 
    //with the ray based on ro and rd. We look at 25 edges
    //around our pointA and the edge that we hit first (minimum t value)
    //is what we hit so we return that t value and traverse that amount
    //along the ray.

    //get a new st and stFL based on the point in this cell that
    //we've found. This way we are garanteed that the edges will be 
    //the ones that fully enclose the cell!
    st = fract(pointA);
    stFL = floor(pointA+0.001);
    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        //and I almost forgot to normalize!!!
        if(dot(normalize(vec2(i,j)),rd) > TEST_ANGLE){
        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime);
        vec2 testB = stFL + coords;

        float distAB = length(testB-pointA);
        if( distAB > 0.001 && distAB < 2.){
            vec2 edgePoint =(pointA+testB)/2.;
            vec2 perp = normalize(testB-pointA).yx*vec2(-1.,1.);
            vec2 edgeSideA = edgePoint - perp*0.7;
            vec2 edgeSideB = edgePoint + perp*0.7;
            //Check where we intersect the current found edge
            minT = min(minT, seg_seg(ro,rd,edgeSideA,edgeSideB));
            line_color = mix(line_color,vec3(0.,0.9,1.),0.8*line(uv,edgeSideA,edgeSideB,0.05));
            //faster version
            //minT = min(minT, seg_seg_new(ro,rd,edgeSideA,edgeSideB));
           }

       }
      }
    }
    return minT;
}




//Basic Voronoi for coloring
vec4 voronoi(vec2 uv){

    vec2 st = fract(uv);
    vec2 stFL = floor(uv);
    vec2 d = vec2(10.);
    vec2 A, B=vec2(100.);

    vec2 mind;

    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime) - st;

        float c = length(coords.xy);//max(abs(coords.x),abs(coords.y));

        if(c < d.x){
            d.x = c;
            d.y = (stFL+id).x*20.+(stFL+id).y;
            A = coords;

            }
        }
    }


    mind = d;

    d = vec2(10.);

    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime) - st;

        float c = length(coords.xy);//max(abs(coords.x),abs(coords.y));

        if(length(A-coords) > 0.00){
            //d.x = c;
            //d.y = rnd2(stFL+id).y;
            B.x = smin2(B.x, dot( 0.5*(A+coords), 
                       normalize(coords-A) ), 0.06 );
            }
        }
    }
    B.y = B.x;
    return vec4(mind,B);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    float globalScale = 4.;
    uv*=globalScale ;

    //get a voronoi for coloring cells an edges
    vec4 voronoXY = voronoi(uv);

    //set basic color for all voronoi cells
    vec3 col = 0.2+0.5*sin(vec3(1., 2., 3.)/1.2+ rnd2(voronoXY.yy).y*200.);
    //col.zy *= rot(.1);

    //add some color to the eddges of the voronoi
    col = mix(col, vec3(0.), smoothstep(0.1,0.095,voronoXY.z));
    col = mix(col, vec3(1.,0.4,0.), smoothstep(0.02,0.01,voronoXY.z));
    col += smoothstep(0.05,0.03,voronoXY.z)*(0.5+0.5*sin(voronoXY.z*10.))/1.5;
    col = col.xxx;
   //define ro, rd, and set default rotation of rd
   vec2 ro = vec2(0.,0.);
   vec2 rd = vec2(1.,0.);
   rd *= rot(iTime/2.);

   //adust mouse
   vec2 m = (iMouse.xy*2.-iResolution.xy)/iResolution.y;
   //rotate ray based on mouse if mouse press
   if(iMouse.z > 0.5){rd = normalize(m.xy+ro);}

   //draw Ray
   col = mix(col, vec3(1.,0.5,0.), line(uv, ro, ro + rd*30., 0.04));

   //Draw RO
   col = mix(col, vec3(.0,.4,.9),point(uv-ro,0.1));

   //t value for traversing
   float t;

   /****************************************************

                  THIS IS THE MAIN EVENT

   ****************************************************/


    //we can do 40 x 8 seg-seg intersections without getting slow.
    //I do 15 to be nice lol
   for(float i = 0.; i < 15.; i++){
       t = voronoiEdgeHit(uv,ro,rd,col);
       ro += rd*t*1.001;
       col = mix(col, vec3(1.0,.0,.0),point(uv-ro,0.1));
       col = mix(col, vec3(1.0,.0,.0),
           ring(uv-ro,0.24+sin(iTime*4.+i/10.)*0.07));
   }

    fragColor = vec4(col,1.0);
}

float sdBox( in vec2 p, in vec2 b )
