#define LUM_COEFFS vec3(.2126, .7152, .0722)

mat2 rot(float a)
{
    float s = sin(a),
          c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 erot(vec3 p, vec3 ax, float a)
{
  return mix(dot(ax, p) * ax, p, cos(a)) + cross(ax, p) * sin(a);
}

// https://iquilezles.org/www/articles/intersectors/intersectors.htm
// axis aligned box centered at the origin, extruded by radius r
float rbi(vec3 ro, vec3 rd, float r)
{
    vec3 m = 1. / rd;
    vec3 n = m * ro;
    vec3 k = abs(m) * (r + 1.);
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    if(tN > tF || tF < 0.)
        return -1.;
    float t = tN;

    vec3 pos = ro + rd * t;
    vec3 s = sign(pos);
    ro  *= s;
    rd  *= s;
    pos *= s;

    pos -= 1.;
    pos = max(pos.xyz, pos.yzx);
    if(min(min(pos.x, pos.y), pos.z) < 0.) 
        return t;

    vec3 oc = ro - 1.;
    vec3 dd = rd * rd;
    vec3 oo = oc * oc;
    vec3 od = oc * rd;
    float ra2 = r * r;

    t = 1e20;        

    // corner
    {
        float b = od.x + od.y + od.z;
        float c = oo.x + oo.y + oo.z - ra2;
        float h = b * b - c;
        if(h > 0.) t = -b - sqrt(h);
    }
    // edge X
    {
        float a = dd.y + dd.z;
        float b = od.y + od.z;
        float c = oo.y + oo.z - ra2;
        float h = b * b - a * c;
        if(h > 0.)
        {
            h = (-b - sqrt(h)) / a;
            if(h > 0. && h < t && abs(ro.x + rd.x * h) < 1.)
                t = h;
        }
    }
    // edge Y
    {
        float a = dd.z + dd.x;
        float b = od.z + od.x;
        float c = oo.z + oo.x - ra2;
        float h = b * b - a * c;
        if(h > 0.)
        {
            h = (-b - sqrt(h)) / a;
            if(h > 0. && h < t && abs(ro.y + rd.y * h) < 1.)
                t = h;
        }
    }
    // edge Z
    {
        float a = dd.x + dd.y;
        float b = od.x + od.y;
        float c = oo.x + oo.y - ra2;
        float h = b * b - a * c;
        if( h > 0. )
        {
            h = (-b - sqrt(h)) / a;
            if(h > 0. && h < t && abs(ro.z + rd.z * h) < 1.)
                t = h;
        }
    }

    if( t > 1e19 ) t = -1.;

    return t;
}

vec4 render(vec3 ro, vec3 rd, float time)
{
    ro.xz *= -rot(time * .5);
    ro.yx *= -rot(time * .4);

    rd.xz *= -rot(time * .5);
    rd.yx *= -rot(time * .4);

    float t = rbi(ro, rd, .1);
    if (t < 0.)
        t = 100.;
    vec3 p = ro + rd * t;

    return vec4(p, t);
}

vec3 normal(vec3 p, float t)
{
	return sign(p) * normalize(max(abs(p) - 1., 0.));
}

vec3 dither(vec2 p)
{
    float r = dot(vec3(p,float(iFrame % 60)), vec3(7., 11., 9.) / 13.);
    return fract(vec3(r, r + .3334, r + .6667)) * 2. - 1.;
}

float lum(vec3 c)
{
    return dot(c, LUM_COEFFS);
}

vec3 color(float t)
{
    vec3 c = vec3(.4,.7,.01);
    c *= LUM_COEFFS;
    c = c * 2. - 1.;
    c = erot(c, normalize(vec3(1, 1, 1)), -t) * .5 + .5;
    c /= LUM_COEFFS;
    return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 ro = vec3(0, 0, -4);
    vec3 rd = normalize(vec3(uv, 1.3));

    float t = iTime;
    vec4 p = vec4(0);
    for(int i = 0; i < 6; ++i)
    {
        ro -= rd * .3;
        p = render(ro, rd, t);
        t += p.w * (.5 + .5 * sin(iTime * .02));
        if (p.w > 10.)
            break;
    }

    ro.xz *= -rot(t * .5);
    ro.yx *= -rot(t * .4);

    rd.xz *= -rot(t * .5);
    rd.yx *= -rot(t * .4);

    vec3 n = normal(p.xyz, t);
    vec3 ld = normalize(vec3(1,0,1));

    ld.xz *= -rot(t * .5);
    ld.yx *= -rot(t * .4);

    float l = dot(n, ld) * .5 + .5;
    float spec = pow(dot(ld, reflect(rd, n)) * .5 + .5, 40.);
    vec3 col = mix(color(iTime * .01) * l, vec3(l * 5.), l * l);
    col += spec * 5.;
    if (length(p.xyz) > 8.)
        col = vec3(.85);

    // reinhard tonemapping
    float lumC = lum(col);
    col *= (1. + lumC / 25.) / (1. + lumC);

    // saturation
    col = mix(vec3(lum(col)), col, 1.5);

    // gamma
    col = pow(col, vec3(1./2.2));

    // vignette
    col *= smoothstep(1.5, .5, length(fragCoord - iResolution.xy * .5) / iResolution.y);

    // dithering
    col += dither(fragCoord) / 256. - 1. / 512.;

    fragColor = vec4(col,1.0);
}

#define M_PI 3.1415926535897932384626433832795*2.0
#define M_TWO_PI (M_PI*2.0)

int CHECKER_SIZE=64;
float WAVE_FREQUENCY=10.0;
float WAVE_AMPLITUDE=0.003;
vec2 WAVE_DIR=vec2(1,1);
float STRENGTH=0.8;

// Placeholder until using an actual texture
vec3 uvToColor(vec2 uv)
{
    int ux = int(uv.x) / CHECKER_SIZE;
    int uy = int(uv.y) / CHECKER_SIZE;
    bool fill = (ux & 1) != (uy & 1);
    return fill ? vec3(0.4,0.8,0.9)*0.8 : vec3(0.1,0.4,0.5);
}


// You should replace this hash by one that you like and meets
