// be used in production.
vec2 g( vec2 n ) { return sin(n.x*n.y*vec2(12,17)+vec2(1,2)); }
//vec2 g( vec2 n ) { return sin(n.x*n.y+vec2(0,1.571)); } // if you want the gradients to lay on a circle

float noise(vec2 p)
{
    const float kF = 2.0;  // make 6 to see worms

    vec2 i = floor(p);
	vec2 f = fract(p);
    f = f*f*(3.0-2.0*f);
    return mix(mix(sin(kF*dot(p,g(i+vec2(0,0)))),
               	   sin(kF*dot(p,g(i+vec2(1,0)))),f.x),
               mix(sin(kF*dot(p,g(i+vec2(0,1)))),
               	   sin(kF*dot(p,g(i+vec2(1,1)))),f.x),f.y);
}

float fbm(vec2 uv)
{
    uv *= 2.0;
    const mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f  = 0.5000*noise( uv ); uv = m*uv;
    f += 0.2500*noise( uv ); uv = m*uv;
    f += 0.1250*noise( uv ); uv = m*uv;
    f += 0.0625*noise( uv ); uv = m*uv;
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float noise = fbm(uv);
    float c = cos((noise + iTime/WAVE_FREQUENCY) * M_TWO_PI);
    vec2 offset = normalize(WAVE_DIR) * c * WAVE_AMPLITUDE;
    vec3 col = uvToColor((uv+offset) * iResolution.xy);

    float s = clamp((c*0.5 + 0.5) * STRENGTH, 0.0, 1.0);
    s = s*s*s;
    //fragColor = vec4(mix(col, col+vec3(1,1,1), s),1.0);
    fragColor = vec4(col*(0.8) + vec3(1,1,1) * s, 1.0);
}

#define ONE vec2(1., 0.)
#define TWO vec2(2., 0.)

float dot2(vec2 a) { return a.x * a.x + a.y * a.y; }
vec2 c(float x){return vec2(x, 0.);}
vec2 c_mul(vec2 a, vec2 b) {
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}
vec2 c_div(vec2 a, vec2 b) {
    return c_mul(a.yx,b.xy).yx/dot2(b);
}
vec2 c_exp(in vec2 a) {
    float ea = exp(a.x);
    float vl = a.y;
    return ea * vec2( cos(vl), sin(vl) );
}
vec2 c_ln(in vec2 a) {
    float ql = length(a);
    return vec2( log(ql), atan(a.y, a.x));
}
vec2 c_pow(in vec2 a, in vec2 b) {
    return c_exp(c_mul(c_ln(a), b));   
}
vec2 c_sqrt(vec2 a) {
    return c_pow(a, c(.5));
}
vec2 c_cosh(vec2 a) {
    return c_div( c_exp(a) + c_exp(-a), TWO);
}
vec2 c_acosh(vec2 z) {
  return c_ln(z+c_mul(c_sqrt(z+vec2(1,0)),c_sqrt(z-vec2(1,0))));
}

vec2 Mint(vec2 x) { return c_mul(x, x) - TWO; }

