float Miter(vec2 x, vec2 s, int iter) {
    vec2 z = x; int i;
    for(i=0;i<iter;i++) {
        if(dot2(z) > 2000.) break; //iTime*100. -> ~2000 or 4.
        z = M(z, s);
    }
    return float(i)/float(iter);
}

