float cells(vec3 pos) {

    cellpos=pos-floor(pos);

    signvec=2.0*step(0.5,cellpos)-1.0;
    fsign=signvec.x*signvec.y*signvec.z;

    subpos=abs(abs(cellpos-0.5)-0.25);

    return fsign*(max(max(subpos.x,subpos.y),subpos.z)-0.25);
}



float map(vec3 p){

    float d = (-.5*length(p)+1.5)+.5 + cells(p)*5.15;    
    float m = .75; float s = .23;    

    d = smin(d, max( abs(p.z)-s, abs(p.x+p.y/1.)-.07 ) + cells(p)*0.21, m + sin(iTime) * 0.01);

    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord.xy * 2.0 / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 ray = normalize(vec3(1.*uv.x,1.*uv.y,2.5));

    vec3 color = vec3(0.);    
    const int rayCount = 2048;

    // Raymarching
    float t = 0.;
    for (int r = 1; r <= rayCount; r++)
    {
        // Ray Position
        vec3 p = vec3(0,0,-3.) + ray * t;        

        // Rotation 
       	p = rotateY(p, iMouse.x/iResolution.x * 2.* 3.14);  
        p = rotateY(p,iTime/3.);

        // Deformation 
    	float mask = max(0.,(1.-length(p/3.)));
    	p = rotateY(p,mask*sin(iTime/2.)*1.2);        
        p.y += sin(iTime+p.x)*mask*.5;
        p.z += sin(iTime+p.x)*mask*1.5;
        p *= 1.1+(sin(iTime/2.)*mask*.3);

        // distance
        float d =  map(p);   

        //color
        if(d<0.01 || r == rayCount )
        {                 

            float iter = float(r) / float(rayCount);
            float ao = (1.-iter);
            ao*=ao;
            ao = 1.-ao;

            float mask = max(0.,(.2-length(p/2.)));                       
            color += 2.*vec3(.1,.5,.2) * max(0.,(noise(p)*14.-12.6)) * mask;            
            color += vec3(.1,.5,.6) * ao * 6.;            
            color += vec3(.17,.2,.84)*(t/18.);

            break;          
        }

        t +=  d *.1;        
    }

    // vignetting effect by Ippokratis
    // https://www.shadertoy.com/view/lsKSWR
    uv = fragCoord.xy / iResolution.xy;
    uv *=  1.0 - uv.yx; 
    float vig = uv.x*uv.y * 20.0;    
    vig = pow(vig, 0.25);        
    color *= vig;

    //color adjustement
    color.y *= 1.8;
    color.x *= 1.5;

	fragColor = vec4(color, 1);
}

// from Syntopia http://blog.hvidtfeldts.net/index.php/2015/01/path-tracing-3d-fractals/
vec2 rand2n(vec2 co, float sampleIndex) {
	vec2 seed = co * (sampleIndex + 1.0);
	seed+=vec2(-1,1);
	// implementation based on: lumina.sourceforge.net/Tutorials/Noise.html
	return vec2(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
                 fract(cos(dot(seed.xy ,vec2(4.898,7.23))) * 23421.631));
}

const vec3 BLACK = vec3(0);
