vec2 distFunc(vec3 p, float time) {
	vec2 d = vec2(distPlane(p, vec4(0, 1, 0, 300)), OBJ_PLANE);
    d = opUnion(d, distKyu190a(p + vec3(0,-200. + -400. * sin(iTime) * sin(iTime), 0)));
    return d;
}

const vec2 NORMAL_COEFF = vec2(1., 0.);
vec3 computeNormal(const vec3 p, const float t){
  return normalize(vec3(distFunc(p + NORMAL_COEFF.xyy, t).x - distFunc(p - NORMAL_COEFF.xyy, t).x,
                        distFunc(p + NORMAL_COEFF.yxy, t).x - distFunc(p - NORMAL_COEFF.yxy, t).x,
                        distFunc(p + NORMAL_COEFF.yyx, t).x - distFunc(p - NORMAL_COEFF.yyx, t).x));
}

const int MAX_MARCH = 500;
int march (vec3 rayOrg, vec3 rayDir, float time, inout float minDist,
           float maxDist,
           inout vec3 intersection, inout vec3 normal) {
    vec3 rayPos = rayOrg + minDist * rayDir;
    vec2 dist = vec2(-1);
    float rayLength = 0.;
    for(int i = 0 ; i < MAX_MARCH ; i++){
        if(rayLength > maxDist) return -1;
        dist = distFunc(rayPos, time);
        rayLength += dist.x;
        rayPos = rayOrg + rayDir * rayLength;
        if(dist.x < EPSILON){
            int objId = int(dist.y);
            intersection = rayPos;
            normal = computeNormal(intersection, time);
            minDist = rayLength;
            return objId;
        }
    }
    return -1;
}

vec3 calcRay (const vec3 eye, const vec3 target, const vec3 up, const float fov,
              const float width, const float height, const vec2 coord){
  float imagePlane = (height * .5) / tan(fov * .5);
  vec3 v = normalize(target - eye);
  vec3 xaxis = normalize(cross(v, up));
  vec3 yaxis =  normalize(cross(v, xaxis));
  vec3 center = v * imagePlane;
  vec3 origin = center - (xaxis * (width  *.5)) - (yaxis * (height * .5));
  return normalize(origin + (xaxis * coord.x) + (yaxis * (height - coord.y)));
}

vec3 getMatColor(int objId, vec3 normal, vec3 intersection){
    if(objId == OBJ_PLANE){
        vec2 p = fract(intersection.xz / iChannelResolution[0].xy);
        vec3 col = deGamma(texture(iChannel0, p).rgb);
        return mix(col, vec3(0.7, 0.8, 1.), 0.); 
    }else if(objId != -1){
        return getMatKyu190a(objId);
    }
	return BLACK;
}

vec3 sky(vec3 rayDir){
	return clamp(vec3(.7, .8, 1.) + exp(dot(rayDir, LIGHT_DIR))*0.1, 0.0, 1.0);
}

float computeShadowFactor (vec3 rayOrg, vec3 rayDir,
                           float mint, float maxt, float k) {
    float shadowFactor = 1.0;
    for(float t = mint ; t < maxt ;){
        float d = distFunc(rayOrg + rayDir * t, 0.).x;
        if(d < EPSILON) break;

        shadowFactor = min(shadowFactor, k * d / t);
        t += d;
    }
    return clamp(shadowFactor, 0.0, 1.0);
}

float specular(vec3 n,vec3 l,vec3 e,float s) {    
    return pow(clamp(dot(reflect(e,n),l),0.0, 1.),s);
}

// p: center of the plane
// n: normal of the plane
bool intersectPlane(vec3 p, vec3 n, 
                    vec3 rayOrigin, vec3 rayDir,
                     inout float minDist,
                    inout vec3 intersection, inout vec3 normal){
    float d = -dot(p, n);
    float v = dot(n, rayDir);
    float t = -(dot(n, rayOrigin) + d) / v;
    if(EPSILON < t && t < minDist){
		intersection = rayOrigin + t * rayDir;
        normal = n;
        minDist = t;
        return true;
    }
    return false;
}


bool intersectSphere(vec3 sphereCenter, float radius, 
                     vec3 rayOrigin, vec3 rayDir, 
                     inout float minDist,
                     inout vec3 intersection, inout vec3 normal){
  	vec3 v = rayOrigin - sphereCenter;
  	float b = dot(rayDir, v);
  	float c = dot(v, v) - radius * radius;
  	float d = b * b - c;
  	if(d >= 0.){
    	float s = sqrt(d);
    	float t = -b - s;
    	if(t <= EPSILON) t = -b + s;
    	if(EPSILON < t && t < minDist){
      		intersection = (rayOrigin + t * rayDir);
      		minDist = t;
            normal = normalize(intersection - sphereCenter);
      		return true;
    	}
  	}
  	return false;
}

bool intersectBox(vec3 rayOrg, vec3 rayDir, vec3 boxMin, vec3 boxMax,
                  out float hit0, out float hit1) {
	float t0 = -10000.0, t1 = 10000.0;
    hit0 = t0;
    hit1 = t1;

    vec3 tNear = (boxMin - rayOrg) / rayDir;
    vec3 tFar  = (boxMax - rayOrg) / rayDir;

    if (tNear.x > tFar.x) {
        float tmp = tNear.x;
        tNear.x = tFar.x;
        tFar.x = tmp;
    }
    t0 = max(tNear.x, t0);
    t1 = min(tFar.x, t1);

    if (tNear.y > tFar.y) {
        float tmp = tNear.y;
        tNear.y = tFar.y;
        tFar.y = tmp;
    }
    t0 = max(tNear.y, t0);
    t1 = min(tFar.y, t1);

    if (tNear.z > tFar.z) {
        float tmp = tNear.z;
        tNear.z = tFar.z;
        tFar.z = tmp;
    }
    t0 = max(tNear.z, t0);
    t1 = min(tFar.z, t1);

    if (t0 <= t1) {
        hit0 = t0;
        hit1 = t1;
        return true;
    }
    return false;
}


vec3 calcColor(float time, vec3 eye, vec3 ray){
  	vec3 l = BLACK;

    float t = 999999.;
    vec3 intersection, normal;
    int objId = -1;
    float t0, t1;
    bool hitPlane = false;

    bool hitBox = intersectBox(eye, ray, 
                               vec3(-700, 0, -800), 
                               vec3(700, 900, 500),
                               t0, t1);
    if(hitBox){
        objId = march(eye, ray, time, t0, t1,
                      intersection, normal);
    }
    if(objId < 1){
        hitPlane = intersectPlane(vec3(0, -100, 0), 
                                  vec3(0, 1, 0), 
                                  eye, ray, t,
                       intersection, normal);
        if(hitPlane) objId = OBJ_PLANE;
    } else {
        t = t0;
    }
    if(objId != -1){
        vec3 matColor = BLACK;
        matColor = getMatColor(objId, normal, intersection);
        float k = computeShadowFactor(intersection + normal, LIGHT_DIR,
                                      0., 1000., 5.);
        vec3 diffuse =  clamp(dot(normal, LIGHT_DIR), 0., 1.) * matColor;
        vec3 ambient = matColor * AMBIENT_FACTOR;
        l += k * diffuse + ambient;
    } else {
        l = sky(ray);
    }

    l = mix( sky(ray), l, exp( -0.000000009*t * t ) );

  	return l;
}


const vec3 target = vec3(0, 300, 0);
const vec3 up = vec3(0, 1, 0);
float fov = radians(60.);

const float SAMPLE_NUM = 5.;
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 eye = vec3(900. * sin(iTime) , 1000., 
                    900. * cos(iTime) );
  	vec3 sum = vec3(0);
    float t = iTime;
  	for(float i = 0. ; i < SAMPLE_NUM ; i++){
    	vec2 coordOffset = rand2n(gl_FragCoord.xy, i);

    	vec3 ray = calcRay(eye, target, up, fov,
        	               iResolution.x, iResolution.y,
            	           gl_FragCoord.xy + coordOffset);

    	sum += calcColor(t, eye, ray);
  	}
 	vec3 col = (sum/SAMPLE_NUM);


  	fragColor = vec4(gammaCorrect(col), 1.);
}

