mat2 rot (float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }

float map (vec3 p)
{
  p = sin(p) + 0.8;
  float scene = 500.;
  float t = iTime/5.0;
  float falloff = 5.0;
  for (float index = 0.; index < 10.; ++index)
  {
    p.xz *= rot(t/falloff);
    p.yz *= rot(t/falloff);
    p = abs(p)-0.3*falloff;
    p *= 0.98;
    scene = max(-0.5, min(scene, max(p.x, max(p.y, p.z))));
    falloff /= 1.85;
  }
  return -scene;
}

