// Clowns in parameter space:
/*
#define PI 3.1415926536

#define G 1.0
#define MU 1.0

#define AA 4

#define DT 1.0
#define MIN_DIST 0.1
#define NUM_BODIES 8
#define ITER 200
*/

vec3 hsv2rgb_smooth( in vec3 c ) {
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float pixel_width = 2.0/iResolution.y;
    float scale = 2.0;
    uv *= scale;
    pixel_width *= scale;

    vec2[NUM_BODIES] bodies;
    for (int i = 0; i < NUM_BODIES; i++) {
        bodies[i] = vec2(cos(2.0*PI*float(i)/float(NUM_BODIES)), sin(2.0*PI*float(i)/float(NUM_BODIES)));
    }
    vec3 col = vec3(0.0);
    for (int nx = 0; nx < AA; nx++) {
        for (int ny = 0; ny < AA; ny++) {

            vec2 pos = uv + pixel_width*vec2(nx,ny)/float(AA);
            vec2 vel = vec2(0.0);
            vec2 acc = vec2(0.0);

            float dist;
            int dist_i;
            for (int i = 0; i < ITER; i++) {
                dist = 2.0*scale;
                dist_i = -1;
                bool tooClose = false;
                for (int bod = 0; bod < NUM_BODIES; bod++) {
                    vec2 body = bodies[bod];
                    vec2 r = (body-pos);
                    float r_ = length(r);
                    if (r_<dist) {
                        dist = r_;
                        dist_i = bod;
                    }
                    if (dist<MIN_DIST) {
                        tooClose = true;
                        break;
                    }
                    acc += -G*r/pow(r_,3.0);
                }
                if(tooClose) break;
                acc -= vel*MU;

                vel += DT*acc;
                pos += DT*acc;
            }
            col += hsv2rgb_smooth(vec3(fract(float(dist_i)/float(NUM_BODIES)+0.2*iTime),1.0-0.2*dist,1.0));
        }
    }
    col /= float(AA*AA);


    // Output to screen
    fragColor = vec4(col,1.0);
}

