vec4 superSamplePixel(vec2 pos) {
    vec4 sum = vec4(0.);

    for (int f=-1010; f<1010; f++) {
        float z = float(f)/1000.;

        vec3 pos3d = vec3(pos.x-.2*z, pos.y-.2*z, z);

        vec4 sp = samplePixel(pos3d);

        if (length(sp)>length(sum))
