// camera dirction
void calc4matrix(
    vec4 From, vec4 To, vec4 Up, vec4 Over,
    out vec4 Wa, out vec4 Wb, out vec4 Wc, out vec4 Wd
)
{
    // Get the normalized Wd column-vector.
    Wd = To - From;
    Wd=normalize(Wd);

    // Calculate the normalized Wa column-vector.
    Wa = cross4D(Up, Over, Wd);
    Wa=normalize(Wa);

    // Calculate the normalized Wb column-vector.
    Wb = cross4D(Over, Wd, Wa);
    Wb=normalize(Wb);

    // Calculate the Wc column-vector.
    Wc = cross4D(Wd, Wa, Wb);
    Wc=normalize(Wc);
}
