void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pp = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    float eyer = 2.0;
    float eyea = -(iMouse.x / iResolution.x) * PI * 2.0;
    float eyef = ((iMouse.y / iResolution.y)-0.24) * PI * 2.0;

	vec4 cam = vec4(
        eyer * cos(eyea) * sin(eyef),
        eyer * cos(eyef),
        0.5,
        eyer * sin(eyea) * sin(eyef));
    float ang = iTime * 0.25;
    cam = cam * (rotXY(iTime * 0.25) * rotZW(iTime * 0.5 + PI / 1.5)); // DOUBLE ROTATION

    vec4 front, left;
    vec4 to = vec4(0.0);
    vec4 up = vec4(0.0, 1.0, 0.0, 0.0);
    vec4 over = vec4(0.0, 0.0, 0.0, 1.0);
    calc4matrix(
        cam, to, up, over, 
        left, up, over, front);

    vec4 v = normalize(front*1.5 - left*pp.x - up*pp.y);

    vec4 p = cam;
    float dt = 0.01;
    vec3 cor = vec3(0.0);

    for(int i = 0; i < 250; i ++)
    {
       float f = box(p, vec4(0.0,0.0,0.0,0.0),0.5);
        if(f < 0.0)
        {
            cor = shade(p, f);
            break;
        }
        p += v*dt;
    }

    fragColor = vec4(cor,1.0);
}

vec3 WHITE = vec3(1);
vec3 RED = vec3(200, 34, 52) / 255.;

float Circle(vec2 uv, vec2 p, float r)
{
    float d = length(uv - p);
    float w = min(fwidth(d), .05);
    return smoothstep(r, r-w, d);
}

float Star(vec2 uv, float size)
{
    uv.x = abs(uv.x);

    float a = 6.28 / 5.;
    float d1 = dot(uv, vec2(sin(a), cos(a)));
    a = 3. * 6.28 /5.;
    float d2 = dot(uv, vec2(sin(a), cos(a)));
    a = 2. * 6.28 /5.;
    float d3 = dot(uv, vec2(sin(a), cos(a)));
    float d4 = uv.y;

    float d = min(max(d1, d2), max(d3, d4));
    float w = min(fwidth(d), .05);    
    return smoothstep(w, -w, d - size);
}

vec2 Remap(vec2 p, float b, float t, float l, float r)
{
    return vec2((p.x - l) / (r - l), (p.y - b) / (t - b));
}

vec3 Flag(vec2 uv)
{
    float w = fwidth(uv.y);
    float d = smoothstep(.5, .5 - w, uv.y);    

    vec2 st = Remap(uv, .5, 1., .065, .35);
    float circle1 = Circle(st, vec2(.45, .5), .35);
    float circle2 = Circle(st, vec2(.55, .5), .32);
    d += max(circle1- circle2, 0.);

    st = Remap(uv, .5, 1., .08, .45);
    st -= .5;
    float r = 0.177;
    float size = 0.028;    
    d += Star(st - vec2(0., 1.) * r, size);
    float a = 6.28 / 5.;
    d += Star(st - vec2(sin(a), cos(a)) * r, size);
    a = 2. * 6.28 / 5.;
    d += Star(st - vec2(sin(a), cos(a)) * r, size);
    a = 3. * 6.28 / 5.;
    d += Star(st - vec2(sin(a), cos(a)) * r, size);
    a = 4. * 6.28 / 5.;
    d += Star(st - vec2(sin(a), cos(a)) * r, size);

    vec3 col = mix(RED, WHITE, d);
    col *= smoothstep(w, 0., abs(uv.y - .5) - .5 + w);    
    return col;
}

