    //col -= vec3(0.,0.,pct3);

    col += vec3(pct,pct2,-pct3);


    fragColor = vec4(col,1.0);
}

#define sunDirection vec3(-7., 5., 5.)

float tun(vec3 p){
    float noise = texture(iChannel0, p.xy).r;
    return cos(p.x*.8)*.6 + cos(p.y*.5)*1.5 + cos(p.z+noise)*1.4;
}

float plan(vec3 p){
    return p.y+.4;
}

float map(vec3 p){
    float d;

    d = tun(p);

    return d;
}


vec3 spNormal(vec3 p){
    vec2 e = vec2(1., -1.) * .0005;
    float r = 1.;
    return normalize(
    e.xyy * map(p + e.xyy) +
    e.yyx * map(p + e.yyx) +
    e.yxy * map(p + e.yxy) +
    e.xxx * map(p + e.xxx));
}

float raymarch(vec3 ro, vec3 rd){
    float depth = .01;

    for(int i=0; i<255; i++){
        float d = map( ro + depth*rd );


        depth += d;
        if(d < 0.001 || depth > 100.) break;
    }

    return depth;
}

vec3 getCamRayDir(vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.-1.;
    uv.x *= iResolution.x / iResolution.y;
    vec3 rd = normalize(vec3(uv, 2.));

    return rd;
}


vec3 shade(vec3 ro, vec3 rd, vec3 p, vec3 n){
    vec3 col = vec3(1.);

    vec3 ligthDirection = normalize(sunDirection - p);
    vec3 diffuse = vec3(clamp(dot(n, ligthDirection), 0.2, 1.));
    vec3 amb = vec3(.1, .2, .3);

    if(map(p) == tun(p)){
        col = vec3(.3, .7, 1.);
    }

    return col*(diffuse+amb);
}

vec3 reflexion(vec3 ray, vec3 rd, vec3 normal){
    vec3 col;

    vec3 newOr = ray;
    vec3 newDir = reflect(rd, normal);

    float r = raymarch(newOr, newDir);
    vec3 newRay = newOr + newDir*r;
    vec3 newN = spNormal(newRay);

    vec3 newCol = shade(newOr, newDir, newRay, newN);
    col = mix(col, newCol, .5);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 ro = vec3(0., 1., -5.+iTime);
    vec3 rd = getCamRayDir(fragCoord);

    // Time varying pixel color
    float r = raymarch(ro, rd);

    vec3 ray = ro+rd*r;
    vec3 n = spNormal(ray);
    vec3 col = shade(ro, rd, ray, n);


    col+=reflexion(ray, rd, n);

    col *= exp(-0.04 * r);

    // Output to screen
    fragColor = vec4(col, 1.);
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g=0.,e,t=iTime;++i<99.;){
        p=g*d;
        p.z-=4.;
        p=R(p,normalize(vec3(1,2,3)),t*1.2);
        vec4 q=vec4(p,sin(t)*.7);
        q.xyw=R(q.xyw,vec3(0,1,0),t);
        g+=e=length(vec2(length(q.xy)-1.,length(q.wz)-1.5))-.3;
        O.xyz+=mix(vec3(1),H(atan(q.y,q.x)),.7)*.012*exp(-i*i*e);
    }
    O*=O;
}

