	pp = -1.0 + 3.0*pp;
	pp.x *= iResolution.x/iResolution.y;

	vec3 lookAt = vec3(0.0, -0.31, 0.0);

    float eyer = 3.0; 
    float eyea = (iMouse.x / iResolution.x) * pi * 2.0;
    float eyea2 = ((iMouse.y / iResolution.y)-0.94) * pi * 4.0;

	vec3 ro = vec3(
        eyer * cos(eyea) * sin(eyea2),
       eyer * cos(eyea2),
        eyer * sin(eyea) * sin(eyea2));


	vec3 front = normalize(lookAt - ro);
	vec3 left = normalize(cross(normalize(vec3(0.0,1,-0.1)), front));
	vec3 up = normalize(cross(front, left));
	vec3 rd = normalize(front*1.5 + left*pp.x + up*pp.y); 


    vec3 bh = vec3(0.0,0.0,0.0);
    float bhr = 0.1;
    float bhmass = 4.0;
   	bhmass *= 0.001;

    vec3 p = ro;
    vec3 pv = rd;
    float dt = 0.02;

    vec3 col = vec3(0.0);

    float noncaptured = 1.0;

    vec3 c1 = vec3(0.5,0.46,2);
    vec3 c2 = vec3(1.0,0.8,0.6);


    for(float t=0.0;t<1.0;t+=0.005)
    {
        p += pv * dt * noncaptured;
        vec3 bhv = bh - p;
        float r = dot(bhv,bhv);
        pv += normalize(bhv) * ((bhmass) / r);

        noncaptured = smoothstep(0.0, 0.666, sdSphere(p-bh,bhr));
        float dr = length(bhv.xz);
        float da = atan(bhv.x,bhv.z);
        vec2 ra = vec2(dr,da * (0.01 + (dr - bhr)*0.005) + 5.0 * pi + iTime*2.0 );
        ra *= vec2(10.0,10.0);

        vec3 dcol = mix(c2,c1,pow(length(bhv)-bhr,1.0)) * max(0.0,texture(iChannel1,ra*vec2(0.1,0.5)).r+0.05) * (4.0 / ((0.001+(length(bhv) - bhr)*50.0) ));

        col += max(vec3(0.0),dcol * smoothstep(0.0, 1.0, -sdTorus( (p * vec3(1.0,25.0,1.0)) - bh, vec2(0.9,0.99))) * noncaptured);

        col += vec3(1.0,1,2) * (0.8/vec3(dot(bhv,bhv))) * 0.0033 * noncaptured;

    }
    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926535897932;

float atanM(float y, float x){
    if(x >= 0.){
        return atan(y/x)/pi/2.+.75;
    }
    return (pi+2.*atan(y/x))/4./pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
