    float d3 = length(uv)/75.0;
    d -= iTime*speed;
    d2 -= iTime*speed2;
    d3 -= iTime*speed3;

    float mascara = smoothstep(R.y/2.+width*4., R.y/2.-width*4., acos(tan(length(uv.x*30.) / length(uv.y/-30.))));
    float mascara2 = smoothstep(R.y/2.+width*4., R.y/2.-width*4., (length(uv.x/8.) * length(uv.y/-8.)));
    float mascara3 = smoothstep(R.y/2.+width*4., R.y/2.-width*4., (length(uv.x) + length(uv.y)));

    col = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d*width)*float(amount))+1.)/2.;
    col2 = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d2*width)*float(amount))+1.)/2.;
    col3 = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d3*width)*float(amount))+1.)/2.;
    col = color1+col*(color2-color1);
    col *= mascara2;
    col2 = color23+col3*(color13-color23);
    col2 *= mascara;
    col3 = color22+col3*(color12-color22);
    col3 *= mascara3;
    fragColor = vec4(col-col2-col3,1.0);
    }

