#define STEP false
#define ADD false
#define FREQ 12.5663706144

float normalizedSin(float x)
{
    return 0.5 * (1.0 + sin(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float sin1 = normalizedSin(FREQ * (iTime + uv.x));
    float sin2 = normalizedSin(FREQ * (-iTime + uv.x));

    vec3 col = vec3(
        STEP ? ( 
            ADD ?
                step(0.5 * (sin1 + sin2), uv.y) :
                sin1 < sin2 ?
                    smoothstep(sin1, sin2, uv.y) :
                    smoothstep(sin2, sin1, uv.y)
        ) : step(0.1, abs(uv.y - 0.5 * (sin1 + sin2)))
    );

    fragColor = vec4(col, 1.0);
}

// analytic solution to the logistic map for r=4
// https://en.wikipedia.org/wiki/Logistic_map#Solution_when_r_=_4
float logisticR4(float n, float theta) {
    return pow(sin(pow(2.0, n) * theta * radians(180.0)), 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // transform to viewport coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // put a nice number here but don't make it too small or too big 
    float seed = 11.3;

    // put your fav (x,y) -> n function here
    float n = pow(sin(uv.x), sin(uv.y));

    // compute logistic map at current position and time
    float v = logisticR4(n, exp(seed)+iTime);

    // render
    fragColor = vec4(v,v,v,1.0);
}

