// https://www.shadertoy.com/view/3tlGD4
// float u0 = 0.0;
const vec3 color0 = vec3(0.0, 0.0, 0.5625); // blue
const float u1 = 1.0/9.0;
const vec3 color1 = vec3(0.0, 0.0, 1.0); // blue
const float u2 = 23.0/63.0;
const vec3 color2 = vec3(0.0, 1.0, 1.0); // cyan
const float u3 = 13.0/21.0;
const vec3 color3 = vec3(1.0, 1.0, 0.0); // yellow
const float u4 = 47.0/63.0;
const vec3 color4 = vec3(1.0, 0.5, 0.0); // orange
const float u5 = 55.0/63.0;
const vec3 color5 = vec3(1.0, 0.0, 0.0); // red
// float u6 = 1.0;
const vec3 color6 = vec3(0.5, 0.0, 0.0); // red

// rescaling function
#define rescale(u, v, x) (x - u)/(v - u)

vec3 jetLinear(float t)
{
	return
        + mix(color0, color1, rescale(0.0, u1, t))
        + (mix(color1, color2, rescale(u1, u2, t)) - mix(color0, color1, rescale(0.0, u1, t))) * step(u1, t)
        + (mix(color2, color3, rescale(u2, u3, t)) - mix(color1, color2, rescale(u1, u2, t))) * step(u2, t)
        + (mix(color3, color4, rescale(u3, u4, t)) - mix(color2, color3, rescale(u2, u3, t))) * step(u3, t)
        + (mix(color4, color5, rescale(u4, u5, t)) - mix(color3, color4, rescale(u3, u4, t))) * step(u4, t)
        + (mix(color5, color6, rescale(u5, 1.0, t)) - mix(color4, color5, rescale(u4, u5, t))) * step(u5, t)
        ;
}
// end of colormap -------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime / 20.0;
    vec2 pos = vec2(cos(t), sin(t)) * 5.0;

    // make uv.y consist of 100 "pixels"
    vec2 uv = trunc((fragCoord/iResolution.y + pos) * 100.0);

    // (x^2 + y^2) mod 73 mod 5
    // https://commons.wikimedia.org/wiki/File:Remainder-pattern1.png
    float val = mod(mod(dot(uv, uv), 73.0), 5.0) / 5.0;

    vec3 col = jetLinear(val);

    fragColor = vec4(col,1.0);
}

#define PI 3.1415926535897932
#define NUM_POINTS 16

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0);
    const float pw = 16.0;

    vec2 points[NUM_POINTS];
    vec3 colors[NUM_POINTS];
    for(int i = 0; i < NUM_POINTS; ++i)
    {
        //[-1; 1]
        points[i] = vec2(
            cos(iTime*2.0*PI*0.1*rand(vec2(float(i+1)*0.5574, float(i+1)*0.6425))),
            sin(iTime*2.0*PI*0.1*rand(vec2(float(i+1)*0.8464, float(i+1)*0.8345)))
        );
        //[0; 1]
        points[i] = points[i]*0.5+0.5;

        colors[i] = vec3(
            rand(vec2(float(i+1)*0.1251, float(i+1)*0.2605)),
            rand(vec2(float(i+1)*0.8681, float(i+1)*0.6385)),
            rand(vec2(float(i+1)*0.9663, float(i+1)*0.4395))
        );
    }

    float nf = 0.0;
    for(int i = 0; i < NUM_POINTS; ++i)
    {
        float dist = length(points[i] - uv);
        dist = 1.0/dist;
        dist = pow(dist, pw);
        nf += dist;
    }
    nf = 1.0/nf;

    for(int i = 0; i < NUM_POINTS; ++i)
    {
        float dist = length(points[i] - uv);
        if(dist < 0.01f)
        {
            col = vec3(1.0f);
            break;
        }
        dist = 1.0/dist;
        dist = pow(dist, pw);
        col += colors[i]*dist*nf;
    }

    col = pow(col, vec3(1.0/2.2));

    // Output to screen
    fragColor = vec4(col, 1.0);
}

/* 
This Voronoi Shader is based on:

1. An article by IQ: 
    https://www.iquilezles.org/www/articles/voronoilines/voronoilines.htm
2. Tomkh's method where you get borders based around uv\
    https://www.shadertoy.com/view/llG3zy
3.Shane's Rounded border shader::
    https://www.shadertoy.com/view/ll3GRM
4. Tomkh, Abje and glk7 all have some sort of voronoi traversal
   algorithm

=============================================================

An important discorvery I made, assuming I am understanding
tomkh's and Iq's work correctly is that actually:

IQ's method makes more sense than tomkh's here. 
If we want to find borders 
so we can find the nearest intersection, we actually 
need to find all the borders around pointA, NOT around 
the uv (or in this case the ro).

This way we are garanteed that the edges will be 
the ones that fully enclose the cell.

If you check for borders around ro, you will get borders
close to ro for the 3x3 loop which may not find the borders
in the direction ro+rd*t is even going. So you would then need 
a 5x5 loop to do that which is super slow.

==========================================================

Here is a voronoi traversal algorithm I came up with.
There are a few hacks involved:

1. When I check edges for a hit I actually check a 5x5 neighborhood
because otherwise the way I do things there are times when not all
the correct edges are drawn.

2. The weirdest thing. I find I can't use a constant value 
(such as ray origin) as an input to this algorithm. It has to 
be moving even a little bit so I jitter with sin(iTime)*0.001

No idea why. A mouse position will work find and give me the edges, 
but a set value like vec2(1.,0.) will not. 

EDIT: The jitter can be a constant value, we just need to 
be a tiny bit off from pointA when we checkk pointB for some reason.

*/

/*/////////////////////////////////////////

                  UPDATE

    I found a way to make voronoi traversal close to x2 faster!
    https://www.shadertoy.com/view/7tcSDn



////////////////////////////////////////*/

#define pi acos(-1.)
#define eps 8./iResolution.y

#define maxPoints 16.
#define screenSize 4.

#define bg (0.5+0.5*sin(vec3(iTime,iTime*3.+403.,iTime*1.3+902.)))
#define fg (0.5+0.5*sin(vec3(-iTime*2.1,-iTime*0.9+403.,-iTime+902.)))

//a method for finding the intersection between
//two rays. Not effienct but very memorable.
float seg_seg(vec2 ro1, vec2 rd1, vec2 A, vec2 B){
    vec2 ro2 = A;
    vec2 rd2 = normalize(B-A);
    float maxABLange = length(B-A);

    vec2 perp = rd1.yx*vec2(-1.,1.);
    float s = (dot(ro1,perp) - dot(ro2,perp))/dot(rd2,perp);
    perp = rd2.yx*vec2(-1.,1.);
    float t = (dot(ro2,perp) - dot(ro1,perp))/dot(rd1,perp);
    return t >= 0. && s >= 0.? t : 100.;
}


vec2 rnd2(vec2 id){
    return vec2(fract(sin(dot(id,vec2(14.27,74.97)))*54329.34),
           fract(sin(dot(id+912.35,vec2(49.27,102.74)))*54329.34));
}

mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

float smin2(float a, float b, float r)
{
   float f = max(0., 1. - abs(b - a)/r);
   return min(a, b) - r*.25*f*f;
}

float point(vec2 uv, float r){
    return smoothstep(r+eps,r-eps,length(uv));
}

float ring(vec2 uv, float r){
    return smoothstep(eps+0.03, 0., abs(length(uv)-r+0.03));
}

float line(vec2 P, vec2 A, vec2 B, float r){
    vec2 PA = P-A;
    vec2 AB = B-A;
    float t = clamp(dot(PA,AB)/dot(AB,AB),0.,1.);
    return smoothstep(r+eps,r-eps,length(PA-AB*t));

}

/***************************************************

Here is the actual voronoi traversal algorithm

*************************************************** */
float voronoiEdgeHit(vec2 uv, vec2 ro, vec2 rd){

    vec2 st = fract(ro);
    vec2 stFL = floor(ro);
    vec2 d = vec2(10.);

    vec2 pointA;
    vec2 pointB;


    float minT = 100.;
    float t;


    //in this loop we are just looking for the closest point
    //to the curent ro. It's a standard voronoi but instead of uv
    //for every pixel we just have ro (the same value) being looked at
    //every pixel (you wont see "ro" here because of what I did above.
    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime);
        vec2 testA = stFL + coords;
        float c = length(coords.xy - st);//max(abs(coords.x),abs(coords.y));

        if(c < d.x){
            d.x = c;
            d.y = (stFL+id).x*20.+(stFL+id).y;
            pointA = testA;

            }
        }
    }    

    //In this loop I am finding every edge based on Iq's article
    //meaning I get a new st an stFLoor based on pointA
    //and find borders around pointA based on that.

    //so for each edge I check to see where it intersects 
    //with the ray based on ro and rd. We look at 25 edges
    //around our pointA and the edge that we hit first (minimum t value)
    //is what we hit so we return that t value and traverse that amount
    //along the ray.

    //get a new st and stFL based on the point in this cell that
    //we've found. This way we are garanteed that the edges will be 
    //the ones that fully enclose the cell!
    st = fract(pointA);
    stFL = floor(pointA+0.001);
    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime);
        vec2 testB = stFL + coords;

        float distAB = length(testB-pointA);
        if( distAB > 0.001 && distAB < 2.){
            vec2 edgePoint =(pointA+testB)/2.;
            vec2 perp = normalize(testB-pointA).yx*vec2(-1.,1.);
            vec2 edgeSideA = edgePoint - perp;
            vec2 edgeSideB = edgePoint + perp;


          //Check where we intersect the current found edge
          minT = min(minT, seg_seg(ro,rd,edgeSideA,edgeSideB));
          //faster version
          //minT = min(minT, seg_seg_new(ro,rd,edgeSideA,edgeSideB));

        }
      }
    }
    return minT;
}




//Basic Voronoi for coloring
vec4 voronoi(vec2 uv){

    vec2 st = fract(uv);
    vec2 stFL = floor(uv);
    vec2 d = vec2(10.);
    vec2 A, B=vec2(100.);

    vec2 mind;

    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime) - st;

        float c = length(coords.xy);//max(abs(coords.x),abs(coords.y));

        if(c < d.x){
            d.x = c;
            d.y = (stFL+id).x*20.+(stFL+id).y;
            A = coords;

            }
        }
    }


    mind = d;

    d = vec2(10.);

    for(float i = -1.; i <= 1.; i++){
        for(float j = -1.; j <= 1.; j++){

        vec2 id = vec2(i,j);
        vec2 rndShift = rnd2(stFL+id);
        vec2 coords = id + 0.5+0.35*sin(pi*2.*(rndShift)+iTime) - st;

        float c = length(coords.xy);//max(abs(coords.x),abs(coords.y));

        if(length(A-coords) > 0.00){
            //d.x = c;
            //d.y = rnd2(stFL+id).y;
            B.x = smin2(B.x, dot( 0.5*(A+coords), 
                       normalize(coords-A) ), 0.06 );
            }
        }
    }
    B.y = B.x;
    return vec4(mind,B);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    float globalScale = 4.;
    uv*=globalScale ;

    //get a voronoi for coloring cells an edges
    vec4 voronoXY = voronoi(uv);

    //set basic color for all voronoi cells
