vec3 LinearToGammaSrgb( vec3 linColor )
{    
	linColor.r = ( linColor.r <= 0.0031308 ) ? ( 12.92 * linColor.r ) : ( 1.055 * pow( linColor.r, 1.0f/2.4 ) - 0.055 );
    linColor.g = ( linColor.g <= 0.0031308 ) ? ( 12.92 * linColor.g ) : ( 1.055 * pow( linColor.g, 1.0f/2.4 ) - 0.055 );
	linColor.b = ( linColor.b <= 0.0031308 ) ? ( 12.92 * linColor.b ) : ( 1.055 * pow( linColor.b, 1.0f/2.4 ) - 0.055 );
