struct SH3
{
    float data[9];
};

struct SH3Color
{
    SH3 r;
    SH3 g;
    SH3 b;
};

void SH3Scale(inout SH3 sh, float scale)
{
    for (int i=0; i<9; i++)
        sh.data[i] *= scale;       
}

float SH3Dot(SH3 coeffs, SH3 dir)
{
    float result = 0.0;
    for( int i = 0; i < 9; i++ )
        result += coeffs.data[i] * dir.data[i];
    return result;
}

SH3 SH3Evaluate(vec3 xyz)
{
    SH3 result;
    float fX = xyz.x;
    float fY = xyz.y;
    float fZ = xyz.z;

	float fC0, fC1, fS0, fS1, fTmpA, fTmpB, fTmpC;
	float fZ2 = fZ * fZ;

	result.data[0] = 0.2820947917738781;
	result.data[2] = 0.4886025119029199 * fZ;
	result.data[6] = 0.9461746957575601 * fZ2 + -0.3153915652525201;
	fC0 = fX;
	fS0 = fY;

	fTmpA = -0.48860251190292;
	result.data[3] = fTmpA * fC0;
	result.data[1] = fTmpA * fS0;
	fTmpB = -1.092548430592079 * fZ;
	result.data[7] = fTmpB * fC0;
	result.data[5] = fTmpB * fS0;
	fC1 = fX * fC0 - fY * fS0;
	fS1 = fX * fS0 + fY * fC0;

	fTmpC = 0.5462742152960395;
	result.data[8] = fTmpC * fC1;
	result.data[4] = fTmpC * fS1;

    return result;
}

vec3 toSHSpace(vec3 dir)
{
    return dir.xzy;
}

vec3 fromSHSpace(vec3 dir)
{
    return dir.xzy;
}

SH3 SH3ProjectCone(float alpha)
{
    float cosAlpha = cos(alpha);
    float sinAlpha = sin(alpha);
    float sinAlphaSqr = sqr(sinAlpha);

    SH3 sh;

    // DC
    sh.data[0] = 1.77245 * (1.0 - cosAlpha);    
    // Linear
    sh.data[1] = 0.0;
    sh.data[2] = 1.53499 * sinAlphaSqr;
    sh.data[3] = 0.0;
    // Quadratic
    sh.data[4] = 0.0;
    sh.data[5] = 0.0;
    sh.data[6] = 1.98166 * cosAlpha * sinAlphaSqr;
    sh.data[7] = 0.0;
    sh.data[8] = 0.0;
    return sh;
}

SH3 SH3ProjectSmoothCone(float alpha)
{
    alpha = max(alpha, radians(0.5));

    float alpha3 = alpha*alpha*alpha;
    float sinAlpha = sin(alpha);
    float cosAlpha = cos(alpha);
    float cosAlpha2 = cosAlpha * cosAlpha;
    float cosAlpha3 = cosAlpha2 * cosAlpha;

    SH3 sh;
    // DC
    sh.data[0] = ((alpha3 + 6.0 * alpha - 12.0 * sinAlpha + 6.0 * alpha * cosAlpha) * 1.77245) / alpha3;
    // Linear
    sh.data[1] = 0.0;
    sh.data[2] = ((alpha3 - 3.0 * cosAlpha * sinAlpha + 3.0 * alpha * cosAlpha2) * 3.06998) / (4.0 * alpha3);
    sh.data[3] = 0.0;
    // Quadractic
    sh.data[4] = 0.0;
    sh.data[5] = 0.0;
    sh.data[6] = ((-6.0*alpha - 2.0*cosAlpha2*sinAlpha - 9.0*alpha*cosAlpha + 14.0*sinAlpha + 3.0*alpha*cosAlpha3) * 3.96333) / (9.0 * alpha3);
    sh.data[7] = 0.0;
    sh.data[8] = 0.0;
    return sh;
}

// Performs the SH triple product r = a * b
// From John Snyder (appendix A8)
//
SH3 SH3Product(SH3 a, SH3 b)
{
    SH3 r;

    float  ta, tb, t;

    const float C0 = 0.282094792935999980;
    const float C1 = -0.126156626101000010;
    const float C2 = 0.218509686119999990;
    const float C3 = 0.252313259986999990;
    const float C4 = 0.180223751576000010;
    const float C5 = 0.156078347226000000;
    const float C6 = 0.090111875786499998;

    // [0,0]: 0,
    r.data[0] = C0*a.data[0]*b.data[0];

    // [1,1]: 0,6,8,
    ta = C0*a.data[0]+C1*a.data[6]-C2*a.data[8];
    tb = C0*b.data[0]+C1*b.data[6]-C2*b.data[8];
    r.data[1] = ta*b.data[1]+tb*a.data[1];
    t = a.data[1]*b.data[1];
    r.data[0] += C0*t;
    r.data[6] = C1*t;
    r.data[8] = -C2*t;

    // [1,2]: 5,
    ta = C2*a.data[5];
    tb = C2*b.data[5];
    r.data[1] += ta*b.data[2]+tb*a.data[2];
    r.data[2] = ta*b.data[1]+tb*a.data[1];
    t = a.data[1]*b.data[2]+a.data[2]*b.data[1];
    r.data[5] = C2*t;

    // [1,3]: 4,
    ta = C2*a.data[4];
    tb = C2*b.data[4];
    r.data[1] += ta*b.data[3]+tb*a.data[3];
    r.data[3] = ta*b.data[1]+tb*a.data[1];
    t = a.data[1]*b.data[3]+a.data[3]*b.data[1];
    r.data[4] = C2*t;

    // [2,2]: 0,6,
    ta = C0*a.data[0]+C3*a.data[6];
    tb = C0*b.data[0]+C3*b.data[6];
    r.data[2] += ta*b.data[2]+tb*a.data[2];
    t = a.data[2]*b.data[2];
    r.data[0] += C0*t;
    r.data[6] += C3*t;

    // [2,3]: 7,
    ta = C2*a.data[7];
    tb = C2*b.data[7];
    r.data[2] += ta*b.data[3]+tb*a.data[3];
    r.data[3] += ta*b.data[2]+tb*a.data[2];
    t = a.data[2]*b.data[3]+a.data[3]*b.data[2];
    r.data[7] = C2*t;

    // [3,3]: 0,6,8,
    ta = C0*a.data[0]+C1*a.data[6]+C2*a.data[8];
    tb = C0*b.data[0]+C1*b.data[6]+C2*b.data[8];
    r.data[3] += ta*b.data[3]+tb*a.data[3];
    t = a.data[3]*b.data[3];
    r.data[0] += C0*t;
    r.data[6] += C1*t;
    r.data[8] += C2*t;

    // [4,4]: 0,6,
    ta = C0*a.data[0]-C4*a.data[6];
    tb = C0*b.data[0]-C4*b.data[6];
    r.data[4] += ta*b.data[4]+tb*a.data[4];
    t = a.data[4]*b.data[4];
    r.data[0] += C0*t;
    r.data[6] -= C4*t;

    // [4,5]: 7,
    ta = C5*a.data[7];
    tb = C5*b.data[7];
    r.data[4] += ta*b.data[5]+tb*a.data[5];
    r.data[5] += ta*b.data[4]+tb*a.data[4];
    t = a.data[4]*b.data[5]+a.data[5]*b.data[4];
    r.data[7] += C5*t;

    // [5,5]: 0,6,8,
    ta = C0*a.data[0]+C6*a.data[6]-C5*a.data[8];
    tb = C0*b.data[0]+C6*b.data[6]-C5*b.data[8];
    r.data[5] += ta*b.data[5]+tb*a.data[5];
    t = a.data[5]*b.data[5];
    r.data[0] += C0*t;
    r.data[6] += C6*t;
    r.data[8] -= C5*t;

    // [6,6]: 0,6,
    ta = C0*a.data[0];
    tb = C0*b.data[0];
    r.data[6] += ta*b.data[6]+tb*a.data[6];
    t = a.data[6]*b.data[6];
    r.data[0] += C0*t;
    r.data[6] += C4*t;

    // [7,7]: 0,6,8,
    ta = C0*a.data[0]+C6*a.data[6]+C5*a.data[8];
    tb = C0*b.data[0]+C6*b.data[6]+C5*b.data[8];
    r.data[7] += ta*b.data[7]+tb*a.data[7];
    t = a.data[7]*b.data[7];
    r.data[0] += C0*t;
    r.data[6] += C6*t;
    r.data[8] += C5*t;

    // [8,8]: 0,6,
    ta = C0*a.data[0]-C4*a.data[6];
    tb = C0*b.data[0]-C4*b.data[6];
    r.data[8] += ta*b.data[8]+tb*a.data[8];
    t = a.data[8]*b.data[8];
    r.data[0] += C0*t;
    r.data[6] -= C4*t;
    // entry count=13
    // **multiplications count=120**
    // **addition count=74**
    return r;
}

// http://filmicworlds.com/blog/simple-and-fast-spherical-harmonic-rotation/
SH3 SH3Rotate(SH3 src, mat3x3 mat)
{
	float m00 = mat[0][0];
	float m01 = mat[0][1];
	float m02 = mat[0][2];
	float m10 = mat[1][0];
	float m11 = mat[1][1];
	float m12 = mat[1][2];
	float m20 = mat[2][0];
	float m21 = mat[2][1];
	float m22 = mat[2][2];

	const float s_c3 = 0.94617469575; // (3*sqrt(5))/(4*sqrt(pi))
	const float s_c4 = -0.31539156525;// (-sqrt(5))/(4*sqrt(pi))
	const float s_c5 = 0.54627421529; // (sqrt(15))/(4*sqrt(pi))

	const float s_c_scale = 1.0/0.91529123286551084;
	const float s_c_scale_inv = 0.91529123286551084;

	const float s_rc2 = 1.5853309190550713*s_c_scale;
	const float s_c4_div_c3 = s_c4/s_c3;
	const float s_c4_div_c3_x2 = (s_c4/s_c3)*2.0;

	const float s_scale_dst2 = s_c3 * s_c_scale_inv;
	const float s_scale_dst4 = s_c5 * s_c_scale_inv;

	SH3 dst;

	dst.data[0] = src.data[0];

	// 9 multiplies
	dst.data[1] = ( m11) * src.data[1] + (-m12) * src.data[2] + ( m10) * src.data[3];
	dst.data[2] = (-m21) * src.data[1] + ( m22) * src.data[2] + (-m20) * src.data[3];
	dst.data[3] = ( m01) * src.data[1] + (-m02) * src.data[2] + ( m00) * src.data[3];

	// 48 multiplies
	// Sparse matrix multiply
	float sh0 =  src.data[4+3] + src.data[4+4] + src.data[4+4] - src.data[4+1];
	float sh1 =  src.data[4+0] + s_rc2*src.data[4+2] +  src.data[4+3] + src.data[4+4];
	float sh2 =  src.data[4+0];
	float sh3 = -src.data[4+3];
	float sh4 = -src.data[4+1];

	// Rotations.  R0 and R1 just use the raw matrix columns
	float r2x = m00 + m01;
	float r2y = m10 + m11;
	float r2z = m20 + m21;

	float r3x = m00 + m02;
	float r3y = m10 + m12;
	float r3z = m20 + m22;

	float r4x = m01 + m02;
	float r4y = m11 + m12;
	float r4z = m21 + m22;

	// dense matrix multiplication one column at a time

	// column 0
	float sh0_x = sh0 * m00;
	float sh0_y = sh0 * m10;
	float d0 = sh0_x * m10;
	float d1 = sh0_y * m20;
	float d2 = sh0 * (m20 * m20 + s_c4_div_c3);
	float d3 = sh0_x * m20;
	float d4 = sh0_x * m00 - sh0_y * m10;

	// column 1
	float sh1_x = sh1 * m02;
	float sh1_y = sh1 * m12;
	d0 += sh1_x * m12;
	d1 += sh1_y * m22;
	d2 += sh1 * (m22 * m22 + s_c4_div_c3);
	d3 += sh1_x * m22;
	d4 += sh1_x * m02 - sh1_y * m12;

	// column 2
	float sh2_x = sh2 * r2x;
	float sh2_y = sh2 * r2y;
	d0 += sh2_x * r2y;
	d1 += sh2_y * r2z;
	d2 += sh2 * (r2z * r2z + s_c4_div_c3_x2);
	d3 += sh2_x * r2z;
	d4 += sh2_x * r2x - sh2_y * r2y;

	// column 3
	float sh3_x = sh3 * r3x;
	float sh3_y = sh3 * r3y;
	d0 += sh3_x * r3y;
	d1 += sh3_y * r3z;
	d2 += sh3 * (r3z * r3z + s_c4_div_c3_x2);
	d3 += sh3_x * r3z;
	d4 += sh3_x * r3x - sh3_y * r3y;

	// column 4
	float sh4_x = sh4 * r4x;
	float sh4_y = sh4 * r4y;
	d0 += sh4_x * r4y;
	d1 += sh4_y * r4z;
	d2 += sh4 * (r4z * r4z + s_c4_div_c3_x2);
	d3 += sh4_x * r4z;
	d4 += sh4_x * r4x - sh4_y * r4y;

	// extra multipliers
	dst.data[4+0] = d0;
	dst.data[4+1] = -d1;
	dst.data[4+2] = d2 * s_scale_dst2;
	dst.data[4+3] = -d3;
	dst.data[4+4] = d4 * s_scale_dst4;

	return dst;
}

SH3 ScaleSHBands(SH3 sh, float dcScale, float linearScale, float quadraticScale)
{
    SH3 result = sh;

    result.data[0] *= dcScale;

    result.data[1] *= linearScale;
    result.data[2] *= linearScale;
    result.data[3] *= linearScale;

    result.data[4] *= quadraticScale;
    result.data[5] *= quadraticScale;
    result.data[6] *= quadraticScale;
    result.data[7] *= quadraticScale;
    result.data[8] *= quadraticScale;

    return result;
}

SH3 RotateZH(SH3 sh, vec3 d)
{    
    float dcScale = sh.data[0] * 3.54491;
    float linearScale = sh.data[2] * 2.04665;
    float quadraticScale = sh.data[6] * 1.58533;

    return ScaleSHBands(SH3Evaluate(d), dcScale, linearScale, quadraticScale);
}

SH3 ConvolveCos(SH3 sh)
{
    float dcScale = 1.0 * kPi;
    float linearScale = 2.0 / 3.0 * kPi;
    float quadraticScale = 1.0 / 4.0 * kPi;
    return ScaleSHBands(sh, dcScale, linearScale, quadraticScale);
}

SH3Color ConvolveCos(SH3Color sh)
{
    float dcScale = 1.0 * kPi;
    float linearScale = 2.0 / 3.0 * kPi;
    float quadraticScale = 1.0 / 4.0 * kPi;

    SH3Color result;
    result.r = ScaleSHBands(sh.r, dcScale, linearScale, quadraticScale);
    result.g = ScaleSHBands(sh.g, dcScale, linearScale, quadraticScale);    
    result.b = ScaleSHBands(sh.b, dcScale, linearScale, quadraticScale);
    return result;
}

