///////////////////////////////////////////////////////////////////////
vec3 GetBentCone()
{
    // define this in SH space
    float theta = iTime * 0.2;//(sin(iTime * 0.1) * 0.5 + 0.5) * kPi;
    float phi = -iTime * 1.5; //sqr(cos(iTime * 0.5)) * kPi;

    vec3 dir;
    dir.x = sin(theta) * cos(phi);
    dir.y = sin(theta) * sin(phi);
    dir.z = cos(theta);

    return dir;
/*
    return normalize(vec3((sin(iTime)*0.5 + 0.5)*2.0, 0, 1));
*/
}

Scene Scene_Setup()
{
    vec3 objCenter = vec3(0, 2, 0);
    Scene scene;

    scene.sphere.center = objCenter;
	scene.sphere.radius = 1.3;

    // bent cone dir
    vec3 bentConeDir = GetBentCone();
    float bentConeLength = 1.8;

    scene.cylinder.a = objCenter;
    scene.cylinder.b = scene.cylinder.a + bentConeDir * bentConeLength;
    scene.cylinder.radius = 0.05;   

    return scene;
}

bool Scene_RayTrace(Scene scene, Ray r, inout HitRecord hitRecord)
{
    float xmin = kInf;

    // for each Sphere s
	Sphere s = scene.sphere;
    {
        float x = RaySphereIntersect(r, s, 0.0);
        if (x > 0.0 && x < xmin)
        {
            xmin = x;

            vec3 p = r.origin + x * r.dir;
            vec3 n = normalize(p - s.center);
            vec3 t = normalize(cross(kWorldUp, n));
            vec3 b = normalize(cross(n, t));
            n = normalize(cross(t, b));
            hitRecord.geomType = kGeomType_Sphere;
            hitRecord.p = p;
            hitRecord.t = t;
            hitRecord.b = b;
            hitRecord.n = n;
        }
    }

    // for each Cylinder c
    Cylinder c = scene.cylinder;
    {                
        vec4 cylResult = cylIntersect(r.origin, r.dir, c.a, c.b, c.radius);
        if (cylResult.x > 0.0 && cylResult.x < xmin)
        {
            xmin = cylResult.x;

            vec3 p = r.origin + xmin * r.dir;
            hitRecord.geomType = kGeomType_Cylinder;
            hitRecord.p = p;
        }    
    }


    return (xmin < kInf);
}

///////////////////////////////////////////////////////////////////////
// Closest Hit Shader
///////////////////////////////////////////////////////////////////////
vec3 ClosestHit(HitRecord hitRecord, Scene scene)
{
    // bent cone dir visualization
    if (hitRecord.geomType == kGeomType_Cylinder)
    {
        return vec3(1.0, 0.0, 0.0);
    }

    vec3 n = hitRecord.n;

    SH3 dirEval = SH3Evaluate(n);

    vec3 bentConeDir = GetBentCone();

    // visibility
    float alpha = radians(60.0);//(cos(iTime) * 0.5 + 0.5) * kPi;// * 0.5;
    SH3 shVis = SH3ProjectCone(alpha);    
    //SH3 shVis = ProjectSmoothCone(alpha);

    // rotate visibility to direction of bentCone
    shVis = RotateZH(shVis, bentConeDir);
    //shVis = SH3Rotate(shVis, rotationAlign(bentConeDir, vec3(0,0,1)));

    // radiance    
    SH3Color shRadiance = GetRadianceSH();

    SH3Color shMaskedRadiance;
    shMaskedRadiance.r = SH3Product(shRadiance.r, shVis);
    shMaskedRadiance.g = SH3Product(shRadiance.g, shVis);
    shMaskedRadiance.b = SH3Product(shRadiance.b, shVis);

    // irradiance
    SH3Color shIrrad = ConvolveCos(shMaskedRadiance);        

    vec3 result;
    result.r = max(SH3Dot(dirEval, shIrrad.r), 0.0);
    result.g = max(SH3Dot(dirEval, shIrrad.g), 0.0);
    result.b = max(SH3Dot(dirEval, shIrrad.b), 0.0);

    return result;
}

Ray Ray_Setup(vec2 screenCoord)
{
	vec2 uv = screenCoord.xy / iResolution.xy * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;

    Ray r;
    r.origin = vec3(0.0);
    r.dir = toSHSpace(normalize(vec3(uv.x, uv.y, 1.0))); // this is in SH space
    return r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    Ray r = Ray_Setup(fragCoord);       

    Scene scene = Scene_Setup();

    // default is sky color
    vec3 radiance = vec3(0.392, 0.584, 0.929);

    HitRecord hitRecord;
    if (Scene_RayTrace(scene, r, hitRecord))
    {        
		radiance = ClosestHit(hitRecord, scene);

        // so we can see better
        //radiance *= 3.0;
	}

    fragColor.rgb = LinearToGammaSrgb(radiance);
    fragColor.a = 1.0;
}

#define PI 3.1415926538

vec2 rot(vec2 uv,float a,vec2 origin){

    vec2 p = (uv - origin);

    float c = cos(a);
    float s = sin(a);

	return mat2(c,-s,s,c)*p + origin;
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

#define NUM_OCTAVES 2

float fbm ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2. + shift;
        a *= 0.5;
    }
    return v;
}

vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

float snoise(vec2 v) {

    // Precompute values for skewed triangular grid
    const vec4 C = vec4(0.211324865405187,
                        // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,
                        // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,
                        // -1.0 + 2.0 * C.x
                        0.024390243902439);
                        // 1.0 / 41.0

    // First corner (x0)
    vec2 i  = floor(v + dot(v, C.yy));
    vec2 x0 = v - i + dot(i, C.xx);

    // Other two corners (x1, x2)
    vec2 i1 = vec2(0.0);
    i1 = (x0.x > x0.y)? vec2(1.0, 0.0):vec2(0.0, 1.0);
    vec2 x1 = x0.xy + C.xx - i1;
    vec2 x2 = x0.xy + C.zz;

    // Do some permutations to avoid
    // truncation effects in permutation
    i = mod289(i);
    vec3 p = permute(
            permute( i.y + vec3(0.0, i1.y, 1.0))
                + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(
                        dot(x0,x0),
                        dot(x1,x1),
                        dot(x2,x2)
                        ), 0.0);

    m = m*m ;
    m = m*m ;

    // Gradients:
    //  41 pts uniformly over a line, mapped onto a diamond
    //  The ring size 17*17 = 289 is close to a multiple
    //      of 41 (41*7 = 287)

    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;

    // Normalise gradients implicitly by scaling m
    // Approximation of: m *= inversesqrt(a0*a0 + h*h);
    m *= 1.79284291400159 - 0.85373472095314 * (a0*a0+h*h);

    // Compute final noise value at P
    vec3 g = vec3(0.0);
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * vec2(x1.x,x2.x) + h.yz * vec2(x1.y,x2.y);
    return 130.0 * dot(m, g);
}

#define NUM_OCTAVES2 2

float fbms ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES2; ++i) {
        v += a * snoise(_st);
        _st = rot * _st * 2. + shift;
        a *= 0.5;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= R.x/R.y;

    uv *= 2.*iTime/10.;

    float sc = 5.;

    //uv.x += iTime/10.;

    float ys = smoothstep(0.7,0.5,uv.y);

    float a = (fbm(uv*sc + vec2(0.,-iTime))*2. - 1.);
    a = snoise(uv*2.+ vec2(0.,-iTime))/1.5;
    a = (fbms(uv*sc));

    uv = rot(uv,a,vec2(uv - fbms(uv)));

    float l = smoothstep(0.49,0.51,uv.y);
    l = smoothstep(0.49,0.51,length(uv/iTime*10.));

    vec3 col = vec3(l);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.141592652
float circle(vec2 uv, vec2 c, float r){
    return smoothstep(r, r+2./iResolution.x, length(uv-c));
}

float unsmoothstep(float mn, float mx, float x) {
    x-=mn;
    x/=mx-mn;
    return x + (x - (x * x * (3.0 - 2.0 * x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tim = -iTime*.1;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - .5);
    uv.x*=iResolution.x/iResolution.y;

    float angl = atan(uv.y, uv.x);

    float adir=-angl+PI*.5;
    // -angl+PI*.5

    vec3 col = vec3(1.);
    float iCount=12.;
    for(float i=0.; i<iCount; i++){
        float dir = floor(adir/(PI/3.)+.5)*(PI/3.);

        if(mod(i+.1, 2.)<.5){
            // todo
            dir = floor((adir-PI/6.)/(PI/3.)+.5)*(PI/3.) + PI/6.;
        }
        float timMod1 = mod(-tim+i*(1./iCount), 1.);
        timMod1 = unsmoothstep(0.,.8,pow(smoothstep(0.,1.,timMod1),.8));
        float circ = circle(
            uv, 
            vec2(sin(dir)*timMod1, cos(dir)*timMod1), 
            cos(timMod1*6.-PI*.65)*.1
        );

        col *= circ;


    }

    col*=circle(uv, vec2(0.), sin(iTime)*.04+.038);

    // Output to screen
    fragColor = vec4(col*-1.+1.,1.0);
}

