#define MIN_DIST 0.001
#define MAX_DIST 1000.0
#define GAMMA 2.2

mat3 cameraMatrix(vec3 lookFrom, vec3 lookAt, float roll) {

    vec3 w = normalize(lookAt - lookFrom);
    vec3 u = normalize(cross(w, vec3(sin(roll), cos(roll), 0.0)));
    vec3 v = normalize(cross(u,w));
    return mat3(u,v,w);


}

float unionOp(float d1, float d2) {
    return min(d1, d2);
}

