float noise( in vec2 p ) { //simplex noise function taken from https://www.shadertoy.com/view/Msf3WH
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(500.0) );

}



float heightMap(vec2 grid) { //FBM mountain

    grid *= 0.05;
    mat2 m = mat2(1.6, 1.2, -1.2, 1.6);
    float f = 0.;
    int numOctaves = 5;
    float octVal = 0.5000;
    for(int i = 0; i < numOctaves; i++) {
        f += octVal*noise(grid);
        grid = m*grid;
        octVal = octVal * 0.5;
    }

    f = 0.5 + 0.5*f;
    f *= smoothstep(0.0, 0.005, abs(grid.x-0.6));

    return max(f, 0.0);

    //return sin(grid.x)*sin(grid.y);

}


bool rayMarch(vec3 rayOrigin, vec3 rayDir, inout float dist) {
    float increment = 0.01;
    float lh = 0.;
    float ly = 0.;
    for (float d = MIN_DIST; d < MAX_DIST; d += increment) {
        vec3 pos = rayOrigin + rayDir*d;
        float height = heightMap(pos.xz);
        if(pos.y < height) {
            dist = d - increment + increment * (lh - ly)/(pos.y-ly-height+lh);
            return true;
        }
        increment = 0.01*d;
        lh = height;
        ly = pos.y;

    }
    return false;
}

vec3 computeNormal(vec3 p) {
    float e = 0.0001; //epsilon, tiny increment
    float xNew = heightMap(vec2(p.x - e,p.z)) - heightMap(vec2(p.x + e, p.z));
    float yNew = 2.*e;
    float zNew = heightMap(vec2(p.x, p.z - e)) - heightMap(vec2(p.x, p.z + e));
    return normalize(vec3(xNew, yNew, zNew));
}

float computeSoftShadows(vec3 pos, vec3 lightPos) {
    float k = 1.;
    float res = 1.;
    float ph = 1e20;
    for(float t = MIN_DIST; t < MAX_DIST;) {
        vec3 lightVector = pos + lightPos*t;
        float h = heightMap(lightVector.xz);
        if(h < 0.001) return 0.0;
        float y = h*h/(2.*ph);
        float dist = sqrt(h*h-y*y);
        res = min(res, k*dist/max(0.,t-y));
        ph = h;
        t += h;
    }
    return res;
}

vec3 computeLighting(vec3 pos, vec3 normal) {
    vec3 sun = vec3(500., 100., 40.);
    //vec3 lightVal = normalize(lightPos - pos);

    vec3 lightVal = normalize(sun);
    float l = clamp(dot(normal, lightVal), 0., 1.);
    //float l = computeSoftShadows(pos, lightVal);

    vec3 lightColor = vec3(l, l, l);
    vec3 ambient = vec3(0.7, 0.9, 1.0)/2.;
    lightColor = clamp(lightColor + ambient, 0., 1.);



    return lightColor * GAMMA;
}



vec3 computeTerrainColor(vec3 rayOrigin, vec3 rayDir, float dist) { 
    vec3 c = vec3(0);
    vec3 fog = vec3(dist/75.);
    //c = vec3(0., 0.5, 1.);
    vec3 pos = rayOrigin + rayDir*dist;
    vec3 normal = computeNormal(pos);

    vec3 lightIntensity = computeLighting(pos, normal);

    vec3 grass = vec3(0.1, 1.0, 0.1);
    vec3 dirt = vec3(0.44, 0.31, 0.22);
    vec3 snow = vec3(1);
    vec3 water = vec3(0.2, 0.5, 1.0);
    vec3 waterBorder = vec3(0.0, 0.8, 1.0);

    vec3 surfaceCol = grass;
    //surfaceCol = texture(iChannel0, 0.5*pos.xz).xyz*0.5*vec3(1);
    //surfaceCol = vec3(incX, incY, incZ);
    surfaceCol = mix(surfaceCol, dirt, min(pos.y/1.5-0.3 + 0.7, 1.));
    if(pos.y >= -0.002 && pos.y <= 0.01) surfaceCol = water;
    //if(pos.y >= 0.001 && pos.y <= 0.012) surfaceCol = waterBorder;
    surfaceCol = mix(surfaceCol, snow, clamp(pos.y*2.-2.5, 0., 1.));
    //}



    c = surfaceCol * lightIntensity;


    c = mix(c, fog, 0.5);
    return c;

}

