//middle square method
float msm(float seed, float r){
    float l = 0.;
    float temp = seed;
    while(temp >= 1.){
        temp /= 10.;
        l += 1.;}

    float l2 = l*2.;
    if((sqrt(10.)*seed) < pow(10., l))l2 -= 1.;
    float l3 = ceil((l2-l)/2.);

    for(float i=0.;i<r;i++){
        if(seed == 0.)
        return 0.;
        seed *= seed;
        seed = floor(seed/pow(10., l3))-floor(seed/pow(10., l3+l))*pow(10., l);    
    }return seed/pow(10., l);
}

//linear congruential generator
float lcg(float m, float a, float c, float seed, float r){
    for(float i=0.;i<r;i++){
        seed = mod(a*seed+c, m);
    }return seed/m;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord;
    vec3 col = vec3(0.);

    float lcgmask = lcg(uv.x, uv.y, 1., t, 100.);
    float msmmask = msm(uv.x, t);
    col += mix(lcgmask, msmmask, clamp((uv.y/R.y-.5)*10., 0., 1.));


    fragColor = vec4(col,1.0);
}

