float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float sc = 32.;

    uv *= 1.5;
    uv.x += cos(0.01 * uv.y + 0.5 * iTime) * thc(12., -0.3 * iTime + 0.1 * h21(uv));// + thc(1., 6. * length(uv) - iTime);
    uv.y += sin(0.01 * uv.x + 0.5 * iTime) * ths(12., -0.3 * iTime + 0.1 * h21(uv));

    vec2 ipos = floor(sc * uv)/sc + 0.;

    float a = atan(ipos.y, ipos.x);
    float r = length(ipos);
    r = log(r) + 0.4 * thc(3.,4. * r + iTime);

    float sc2 = 3. + 2. * cos(3. * a + iTime);


    float val = 5. * r + a + iTime;
    vec2 fpos = fract(vec2(thc(1., val), ths(1.,val)) + sc2 * ipos) - 0.5;

    float d = length(fract(thc(4.,a + iTime) * fpos) - 0.5);
    float rd = 1. + thc(1., a + 4. * r - iTime);
    float k = 0.4;
    float s = 1.-smoothstep(-k,k,rd-d);  
    //s -= step(d, 0.2 * rd);
    s = clamp(4. * s * s, 0., 1.);

    fpos = fract(sc * uv) - 0.5;

    d = mlength(fpos);
    rd = min(0.45, 0.1 * thc(40., r + 1.5 * h21(ipos) + iTime) + 0.4 * s);
    rd *= step(0.11, rd);
    float s2 = step(d, rd) - smoothstep(-0.5,0.5, rd -d);
    s2 = clamp(5. * s2 * s2, 0., 1.);
    s *= 2. * pow(1.-length(uv),4.);
    vec3 col = s2 * pal(s +10.* h21(ipos) + iTime, vec3(1.), vec3(1.), vec3(1.), vec3(0.,0.33,0.66));






    // Output to screen
    fragColor = vec4(col,1.0);
}

