// Ray tracing
#define MAX_STEPS 250
#define EPSILON.001
#define MIN_DIST 0.
#define MAX_DIST 1000.

float towerSDF(vec3 p,float width,float height){
    vec2 p2=vec2(norminf2(p.xz),p.y);
    p2.x-=width;
    p2.y-=height;
    return length(max(p2,0.))+min(0.,max(p2.x,p2.y));
}
hit scene(vec3 p){
    //Scene's SDF
    hit res;
    p.x-=iTime;
    p.z-=.5;

    vec3 fp;fp.xz=fract(p.xz)-.5;fp.y=p.y+2.8;
    vec3 ip;ip=floor(p);
    vec3 neighbor;neighbor.xz=fp.xz-edge(fp.xz);neighbor.y=fp.y;

    float width=.45;
    float max_height=2.;
    float me=towerSDF(fp,width,max_height*hash21(ip.xz));
    float next=towerSDF(neighbor,width,max_height);

    vec3 fp2=fp;fp2.y=-fp.y+5.6;
    vec3 neighbor2=neighbor;neighbor.y*=-1.;
    float me2=towerSDF(fp2,width,max_height*hash21(ip.zx+vec2(.1,.5)));
    float next2=towerSDF(neighbor,width,max_height);

    res.dist=me;
    res.dist=min(min(me,next),min(me2,next2));

    res.mat=1;
    return res;
}

hit trace(vec3 cam,vec3 dir,float start,float end){
    hit res;

    float depth=start;int mat=0;
    for(int i=0;i<MAX_STEPS;i++){
        hit p=scene(cam+depth*dir);
        if(p.dist<EPSILON){
            res.dist=depth;res.mat=p.mat;
            return res;
        }
        depth+=p.dist;
        if(p.dist>=end){
            res.dist=end;res.mat=0;
            return res;
        }
    }
    res.dist=end;res.mat=0;
    return res;
}

vec3 estimateNormal(vec3 p){
    return normalize(vec3(scene(vec3(p.x+EPSILON,p.y,p.z)).dist-scene(vec3(p.x-EPSILON,p.y,p.z)).dist,scene(vec3(p.x,p.y+EPSILON,p.z)).dist-scene(vec3(p.x,p.y-EPSILON,p.z)).dist,scene(vec3(p.x,p.y,p.z+EPSILON)).dist-scene(vec3(p.x,p.y,p.z-EPSILON)).dist));
}

vec3 backgroundColor=vec3(.15);
vec3 displayColor(vec3 cam,vec3 dir,vec2 st){
    vec3 color;vec3 p;int material_id;hit obj;

    obj=trace(cam,dir,MIN_DIST,MAX_DIST);
    p=cam+obj.dist*dir;
    material_id=obj.mat;

    vec3 bg=mix(vec3(.1294,.298,.3725),vec3(.3882,.2392,.3608),(st.x+1.)/2.);
    if(material_id==0){
        color=bg;
    }else if(material_id==1){
        vec3 p2=p;
        p2.x-=iTime;
        p2.y/=2.5;
        vec3 color=(1.-abs(estimateNormal(p).y)>EPSILON)?mix(vec3(0.),vec3(.9843,.9922,.3882),smoothstep(.9997,.9999,hash31(floor(p2*56.)))):vec3(0.);
        return mix(bg,color,2.5*pow(1./(obj.dist*obj.dist),.6));
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // fragment
    vec2 st=fragCoord.xy/iResolution.xy;
    st=st*2.-1.;
    st.x*=iResolution.x/iResolution.y;
    float t=iTime;

    // ray tracing
    //vec3 cam=vec3(cos(t),0.,sin(t))*7.+vec3(0.,2.,0.);
    vec3 cam=vec3(5.,0.,0.);
    vec3 dir=normalize(vec3(st,-1.1));
    mat4 view=viewMatrix(cam,vec3(0.),vec3(0.,1.,0.));
    dir=(view*vec4(dir,0.)).xyz;

    // shading
    vec3 color=displayColor(cam,dir,st);

    fragColor=vec4(color,1.);
}

