// BigWings, FabriceNeyret, iq, and Blackle for sharing their knowledge
//
// Inspired by wblut's work.
//
// cmarangu/CM2 nails this down with precise control where is the
// untransformed space and should be rotated/slices. I'm just sneaky
// avoiding problems with a least amount of artifacts.
// https://www.shadertoy.com/view/tddGzf
//
// Cone tracing and effects took from TekF's shader. Would like to
// see explanation with graphics and pictures what they are doing v-v
// https://www.shadertoy.com/view/MsBGWm

const vec3 EPS = vec3(0., 0.01, 0.0001);
const float PI = acos(-1.);
const float TAU = 2.0 * PI;

// https://jbaker.graphics/writings/DEC.html
float sd_dodecahedron(vec3 p, float radius) {
    const float phi = 1.61803398875;
    const vec3 n = normalize(vec3(phi, 1, 0));

    p = abs(p / radius);
    float a = dot(p, n.xyz);
    float b = dot(p, n.zxy);
    float c = dot(p, n.yzx);
    return (max(max(a, b), c) - n.x) * radius;
}
float sd_icosahedron(vec3 p, float radius){
    const float q = 2.61803398875;
    const vec3 n1 = normalize(vec3(q, 1, 0));
    const vec3 n2 = vec3(0.57735026919);

    p = abs(p / radius);
    float a = dot(p, n1.xyz);
    float b = dot(p, n1.zxy);
    float c = dot(p, n1.yzx);
    float d = dot(p, n2) - n1.x;
    return max(max(max(a, b), c) - n1.x, d) * radius;
}
float sd_icosahestar(vec3 p){
    float radius = 1.5;
    return min(sd_dodecahedron(p, radius), sd_icosahedron(p.zyx, radius));
}

const int NUM_CUTS = 5;
vec3 CUT_DIR[NUM_CUTS];
float CUT_WIDTH[NUM_CUTS];
float CUT_OFFSET[NUM_CUTS];

void setup_cuts() {
    CUT_DIR[0] = normalize(vec3(0., 1., 0.9));
    CUT_DIR[1] = normalize(vec3(1., 0., 0.));
    CUT_DIR[2] = normalize(vec3(0., 0.6, 0.));
    CUT_DIR[3] = normalize(vec3(-1., 0.5, 0.1));
    CUT_DIR[4] = normalize(vec3(-1., 1., 0.));

    CUT_WIDTH[0] = 0.3;
    CUT_WIDTH[1] = 0.2;
    CUT_WIDTH[2] = 0.4;
    CUT_WIDTH[3] = 0.2;
    CUT_WIDTH[4] = 0.6;

    CUT_OFFSET[0] = 0.5;
    CUT_OFFSET[1] = -0.2;
    CUT_OFFSET[2] = 0.3;
    CUT_OFFSET[3] = -0.4;
    CUT_OFFSET[4] = 0.;
}

struct Cut {
    vec3 off;
    float d;
    float sign;
};

Cut op_cut(vec3 p, vec3 n, float w, float width) {
    float dt = dot(p, n) - w;
    float dcut = abs(dt) - width;
    float s = sign(dt);
    return Cut(width * n * s, dcut, s);
}

Cut merge_cuts(Cut a, Cut b) {
    // Sign computing is not right as expected,
    // but I didn't stumble on this problem yet
    return Cut(a.off + b.off, min(a.d, b.d), a.sign * b.sign);
}

Cut dummy_cut() {
    return Cut(vec3(0.), 9999., 1.);
}

// https://suricrasia.online/demoscene/functions/ blackle is a qt >:3c
vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(ax, p) * ax, p, cos(ro)) + cross(ax, p) * sin(ro);
}

float back_out(float t) {
  float f = 1.0 - t;
  return 1.0 - (f * f - f * 0.8 * sin(f * PI));
}

float map(vec3 p) {
    float time = mod(iTime / 14., 1.);
    float dist = 999.;

    float trig_wave = min(time, 1. - time);
    float saddle = 4. * trig_wave - 0.5;

    Cut cut = dummy_cut();
    for (int i = NUM_CUTS - 1; i >= 0; --i) {
        float harm = saddle * float(NUM_CUTS) - float(i);
        float delay = 0.4;
        float release = clamp((harm - delay) / (1. - delay), 0., 1.);
        release = 0.50 - 0.5 * cos(release);
        if (release == 0.) continue;

        Cut current_cut =
            op_cut(p, CUT_DIR[i], CUT_OFFSET[i], CUT_WIDTH[i] * release);
        cut = merge_cuts(cut, current_cut);
        // Adjust position in the direction of cut
        p -= current_cut.off;

        // Rotate space on the last cut
        int last_cut = NUM_CUTS - 1;
        if (i == last_cut) {
            float rot_dir = current_cut.sign;
            p = erot(p, CUT_DIR[last_cut],
                     rot_dir * back_out(release) * PI);
        }
    }

    dist = sd_icosahestar(p);
    dist = max(dist, -cut.d);

    return dist;
}

float trace(vec3 ro, vec3 rd) {
    float t = 0.;
    for (int i = 0; i < 20; i++) {
        float d = map(ro + t * rd);
        if (abs(d) < 0.0001 || t > 20.) break;
        t += d;
    }
    return t;
}

mat3 get_camera(vec3 eye, vec3 at) {
    vec3 zaxis = normalize(at - eye);
    vec3 xaxis = normalize(cross(zaxis, vec3(0., 1., 0.)));
    vec3 yaxis = cross(xaxis, zaxis);
    return mat3(xaxis, yaxis, zaxis);
}

vec3 get_normal(vec3 p, float r) {
    mat3 k = mat3(p, p, p) - mat3(r);
    return normalize(vec3(map(p)) - vec3(map(k[0]), map(k[1]), map(k[2])));
}

vec3 sky2(vec3 rd) {
    vec3 col = vec3(0.);
    col+=smoothstep(0.2,1.5,dot(rd, normalize(vec3(0.,-1.,0.)))) * 0.1*vec3(0.67843,0.67451,0.709);
    col+=smoothstep(.2,1.0, dot(rd, normalize(vec3(0,1,-3)))) * 0.2 * vec3(0.3647,0.2902,0.63137);
    col+=smoothstep(-0.4,0.4, dot(rd, normalize(vec3(0.9,0.2,0.6)))) * 0.2 * vec3(0.1,0.4,0.3);
    col+=smoothstep(-0.4,0.4, dot(rd + vec3(0.0,0.7,0.0), normalize(vec3(0.0,-0.2,0.0)))) *
           vec3(0.1, 0.0, 0.3) * 0.1;
    return col;
}

// Thanks al-ro
vec3 sky(vec3 rd){
    return mix(0.12 * vec3(0.75, 0.5, 1.0),
                0.4 * vec3(0.25, 0.5, 1.0), 0.5 + 0.5 * normalize(rd).y);
}

//https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x){
    return clamp((x * (2.51 * x + 0.03)) / (x * (2.43 * x + 0.59) + 0.14), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord/iResolution.xy * 2. - 1.)
            * vec2(iResolution.x / iResolution.y, 1.);
    float time = iTime;

    setup_cuts();

    vec3 target = vec3 (0., 0., 0.);
    float an = TAU * time / 20. + PI;
    vec3 ro = target + 2.8 * vec3(cos(an), .8, sin(an));
    mat3 cam = get_camera(ro, target);
    float zoom = 1.;
    vec3 rd = cam * vec3(uv, zoom);

    float cone_radius = .7071 / (iResolution.y * zoom);

    float coverage = -1.0;
    vec3 cover_dir = vec3(0.);

    const float aperture = .025;
    const float focus = 1.9;

    vec3 col = vec3(0.);

    float t = 0.;
    for (int i = 0; i < 25; ++i) {
        float radius = t * cone_radius + aperture * abs(t - focus);
        vec3 pos = ro + t * rd;
        float dist = map(pos);

        if (dist < radius) {
            vec3 normal = get_normal(pos, radius);

            vec3 albedo = vec3(.15);
            vec3 refl_col = vec3(0.);
            // if the normal not looking outside of the sphere
            // so it's inner plane and should be colored
            if (dot(pos, normal) < 0.5) {
                albedo = vec3(3.5, 0.0, 0.0);

                vec3 rd = reflect(rd, normal);
                float t = trace(ro + normal * 0.02, rd);
                vec3 nro = ro + rd * t;
                vec3 new_norm = get_normal(nro, radius);
                refl_col = vec3(.95, 0., 0.) *
                           (dot(new_norm, normalize(ro)) * 0.5 + 0.5);

            }

            vec3 ambient =
                vec3(.1) * smoothstep(.7, 2.0, length(pos.xz) + abs(pos.y));
            vec3 directional =
                3.0 * vec3(1, .1, .13) *
                max(dot(normal, normalize(vec3(-2, -2, -1))), .0);
            directional *=
                smoothstep(.5, 1.5, dot(pos, normalize(vec3(1, 1, -1))));

            float f = 1.0 - abs(dot(normal, rd));
            float fresnel = f*f*f*f*f;
            fresnel = mix(.03, 1.0, fresnel);

            vec3 reflection = sky(reflect(rd, normal));

            vec3 sample_color = mix(albedo * (ambient + directional),
                                    reflection, vec3(fresnel));
            sample_color += refl_col;

            // bottom light
            {
                float dif = 0.02 * clamp(0.5 - 0.5 * normal.y, 0., 1.);
                sample_color += dif;
            }

            float new_coverage = -dist / radius;
            vec3 new_coverage_dir = normalize(normal - dot(normal, rd) * rd);

            new_coverage +=
                (1.0 + coverage) * (.5 - .5 * dot(new_coverage_dir, cover_dir));
            new_coverage = min(new_coverage, 1.0);

            if (new_coverage > coverage) {
                col += sample_color * (new_coverage - coverage) * .5;

                cover_dir =
                    normalize(mix(new_coverage_dir, cover_dir,
                                  (coverage + 1.0) / (new_coverage + 1.0)));
                coverage = new_coverage;
            }
        }
        t += max(dist, radius );
        if (dist < -radius || coverage > 1.0)
            break;
    }
    col += (1.0 - coverage) * .5 * sky(rd);

    // Tonemapping
    col = ACESFilm(col);

    col = clamp(col, 0., 1.);

    // Gamme correction
    col = pow(col, vec3(0.4545));

    // Dithering
    col += sin(fragCoord.x * 114.0) * sin(fragCoord.y * 251.9) / 512.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}

precision highp float;



float random (in vec2 p) { 
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3. - 2.0 * f);

    return mix(a, b, u.x) + 
            (c - a)* u.y * (1. - u.x) + 
            (d - b) * u.x * u.y;
}

float light(in vec2 pos,in float size,in float radius,in float inner_fade,in float outer_fade){
	float len = length(pos/size);
	return pow(clamp((1.0 - pow( clamp(len-radius,0.0,1.0) , 1.0/inner_fade)),0.0,1.0),1.0/outer_fade);
}


float flare(in float angle,in float alpha,in float time){
	float t = time;
    float n = noise(vec2(t+0.5+abs(angle)+pow(alpha,0.6),t-abs(angle)+pow(alpha+0.1,0.6))*7.0);
   //	n = 1.0;
    float split = (15.0+sin(t*2.0+n*4.0+angle*20.0+alpha*1.0*n)*(.3+.5+alpha*.6*n));

    float rotate = sin(angle*20.0 + sin(angle*15.0+alpha*4.0+t*30.0+n*5.0+alpha*4.0))*(.5 + alpha*1.5);

    float g = pow((2.0+sin(split+n*1.5*alpha+rotate)*1.4)*n*4.0,n*(1.5-0.8*alpha));

    g *= alpha * alpha * alpha * .5;
	g += alpha*.7 + g * g * g;
	return g;
}

#define SIZE 2.8
#define RADIUS 0.07
#define INNER_FADE .85
#define OUTER_FADE 0.02
#define SPEED .1
#define BORDER 0.19

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5)/iResolution.y;
	float f = .0;
    float f2 = .0;
    float t = iTime * SPEED;
	float alpha = light(uv,SIZE,RADIUS,INNER_FADE,OUTER_FADE);
	float angle = atan(uv.x,uv.y);
    float n = noise(vec2(uv.x*20.+iTime,uv.y*20.+iTime));

	float l = length(uv);
	if(l < BORDER){
        t *= .8;
        alpha = (1. - pow(((BORDER - l)/BORDER),0.22)*0.7);
        alpha = clamp(alpha-light(uv,0.2,0.0,1.3,.5)*.55,.0,1.);
        f = flare(angle*1.0,alpha,-t*.5+alpha);
        f2 = flare(angle*1.0,alpha*1.2,((-t+alpha*.5+0.38134)));

	}else if(alpha < 0.1){
		f = alpha;
	}else{
		f = flare(angle,alpha,t)*1.3;
	}
	fragColor = vec4(vec3(f*1.5+f2*f2,f*alpha*0.5+f2*f2*.5,f*alpha*0.0+f2*(sin(angle-t*.5)*.0)),.5);
}

