/*
I found a way method of finding intersections between two
segments in a plane. 
by found I mean I found it on stackexchange

https://stackoverflow.com/questions/563198/how-do-you-detect-where-two-line-segments-intersect/565282#565282

The previous method I had (in the source of this fork) 
requires way more calculation and if you want something you
can whip out of you back pocket in a pinch that isn't the 
thing you'll want.

This is way cooler.

you have teh intersection of two rays expressed as:

ro1 + rd1*t = ro2 + rd2*s (where s and t are the distances)

You can just eliminate one by taking the dot product
of every vector in this equation by a vector perpendicular
to one of either rd1 or rd2!

Because dot(rd1,perp_rd1) will be 0, 
you eliminate that term including the t!

Beautiful!

We just need to remember to repeat this for s so we can make
sure we have a valid intersection (s and t both betwen the 
end ranges of the segments. Or in the case of rays, just >= 0)

*/
#define line1Col vec3(1.,.2,.5)
#define line2Col vec3(1.,.3,.3)

float distToLine(vec2 A, vec2 B, vec2 p){

    vec2 PA = p - A;
    vec2 BA = B - A;
    float d = dot(PA,BA);
    float t = clamp(d/(length(BA)*length(BA)),0., 1.);//Vektorprodukt
    vec2 normal = PA - BA*t;
    return length(normal);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5,0.5)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;

      ////////////////////////////////////////////
      //                                       //
////////  BACKGROUND GRID DESIGN             //
      //                                   //
      /////////////////////////////////////

    vec2 st = uv;//save the unscaled uv
    //I'm only making an 8(*aspect)x8 grid, higher variables
    //may end up with graphs completely outside it
    uv*=10.;
    //graph background
    vec3 col = vec3(0.1);
    //center highlights
    col = mix(col,vec3(0.16),1.0-length(uv/8.));
    //dust 
    float specks = fract(sin(dot(uv,vec2(123.,16.)))*433413.);

    //nice soft texture 
    float tex = texture(iChannel0,st).x;
    col = mix(col,vec3(0.25),pow(tex,2.));

    //small grid lines
    vec2 lines = fract(uv*5.);
    lines = smoothstep(0.45,0.52,abs(lines-0.5));
    col = mix(col,vec3(0.24),lines.x);
    col = mix(col,vec3(0.24),lines.y);

    //larger grid lines
    lines = fract(uv);
    lines = smoothstep(0.47,0.52,abs(lines-0.5));
    col = mix(col,vec3(0.5),lines.x);
    col = mix(col,vec3(0.5),lines.y);

    //axis lines
    lines = smoothstep(0.0,0.02,abs(uv));
    col = mix(col,vec3(0.6),1.0-lines.x);
    col = mix(col,vec3(0.6),1.-lines.y);
    col = mix(col,vec3(0.1),step(0.1,specks)*0.2);

      ////////////////////////////////////////////
      //                                       //
////////  LINE INTERSECTION CALCULATIONS     //
      //                                   //
      /////////////////////////////////////

    //Line Points
    vec2 A1 = vec2(-3.4+sin(iTime),-2.4+sin(iTime+0.5));
    vec2 B1 = vec2(3.4+sin(iTime+4.),3.4+sin(iTime+2.));
    vec2 ro1 = A1;
    vec2 rd1 = normalize(B1-A1);
    float maxLength1 = length(B1-A1);

    vec2 A2 = vec2(-3.3+sin(iTime+1.4),-3.6+sin(iTime+3.));
    vec2 B2 = vec2(2.3+sin(iTime+0.2),3.1+sin(iTime+0.3))*10.;
    vec2 ro2 = A2;
    vec2 rd2 = normalize(B2-A2);
    float maxLength2 = length(B2-A2);
    //we know that teh intersection of two rays is
    // ro1+rd1*t = ro2+rd2*s
    // There is a really elegant solution for this
    // use dot everything by a perpendicualr vector
    // to rd1 and its unknown will cancel out.
    vec2 perpRD1 = rd1.yx*vec2(-1.,1.);
    float s = (dot(ro1,perpRD1) - dot(ro2,perpRD1)) / dot(rd2,perpRD1);

    //we just need to repeat for the other side to check
    //validity for both rays

    vec2 perpRD2 = rd2.yx*vec2(-1.,1.);
    float t = (dot(ro2,perpRD2) - dot(ro1,perpRD2)) / dot(rd1,perpRD2);

    //when valid, either s or t will lead to the samer
    // intersection point

    vec2 validPoint = ro1 + rd1*t;



      ////////////////////////////////////////////
      //                                       //
////////        DRAWING EVERYTHING           //
      //                                   //
      /////////////////////////////////////


    //line 1
    float f = 1.0-smoothstep(0.02,0.06,abs(distToLine(A1, B1, uv)));
    col = mix(col, vec3(1.,.2,.5), f);//0.5

    //line 2
    f = 1.0-smoothstep(0.02,0.06,abs(distToLine(A2, B2, uv)));
    col = mix(col, vec3(1.,.3,.3), f);


    //segment end points line 1
    f = 1.-smoothstep(0.03,0.08,length(uv-A1)-0.02);
    col = mix(col, line1Col, f);
    //segment end points line 1
    f = 1.-smoothstep(0.03,0.08,length(uv-B1)-0.02);
    col = mix(col, line1Col, f);

    //segment end points line 2
    f = 1.-smoothstep(0.03,0.08,length(uv-A2)-0.02);
    col = mix(col, line2Col, f);
    //segment end points line 2
    f = 1.-smoothstep(0.03,0.08,length(uv-B2)-0.02);
    col = mix(col, line2Col, f);


    // if s and t are both between 0. and maxLength they are
    // valid for both the linear interpolations we need.
    // so show the intersection point.
    if(s >= 0. && t >= 0. && t <= maxLength1 && s <= maxLength2){

    //intersection
    f = 1.-smoothstep(0.02,0.04,abs(length(uv-validPoint)-0.15));
    col = mix(col, vec3(1.,0.7,0.), f);
    f = 1.-smoothstep(0.03,0.08,length(uv-validPoint)-0.015);
    col = mix(col, vec3(1.,0.7,0.), f);

   }


      ////////////////////////////////////////////
      //                                       //
////////          POST PROCESSING            //
      //                                   //
      /////////////////////////////////////


    //vignette
    uv = fragCoord/iResolution.xy;
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity
    vig = pow(vig, 0.15); // change pow for modifying the extend of the  vignette

    // Output to screen
    fragColor = vec4(col*vig,1.0);
}

//TDF2021 ShaderShowDown q-f : gyabo
//Ports to ShaderToy. original as follows,
//https://gist.github.com/kumaashi/eee4971857e6dd7a541d8dc455ebf2eb
//https://twitter.com/gyabo/status/1469569058811293696
#define time iTime
#define v2Resolution iResolution
#define gl_FragCoord fragCoord
#define texFFTSmoothed iChannel0
#define fragColor out_color

vec4 plas( vec2 v, float time )
{
	float c = 0.5 + sin( v.x * 10.0 ) + cos( sin( time + v.y ) * 20.0 );
	return vec4( sin(c * 0.2 + cos(time)), c * 0.15, cos( c * 0.1 + time / .4 ) * .25, 1.0 );
}

vec4 getf(vec2 uv) {
  uv = abs(uv * 0.15);
  //float m0 = texture(texFFTSmoothed, uv.x).x * 5.0; //Bonzomatic
  //float m1 = texture(texFFTSmoothed, uv.y).x * 5.0;  //Bonzomatic
  float m0 = texture(texFFTSmoothed, uv.xy * 2.0).x * 0.5;
  float m1 = texture(texFFTSmoothed, uv.yx * 2.0).x * 0.5;
  return vec4(
  m0 * m1,
  m0 - m1,
  m0 + m1, 1.0) * 2.0;
}

float box(vec3 p, vec3 s) {
  p = abs(p) - s;
  return max(p.x, max(p.y, p.z));
}

vec2 rot(vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  return vec2(
  p.x * c - p.y * s,
  p.x * s + p.y * c);
}

float map(vec3 p) {
  float t = length(mod(p, 2.0) - 1.0) - 0.45;
  float h = length(getf(mod(p.xz * 0.2, 1.0)).xyz) * 0.5;
  t = min(t, (h + 1.0) - dot(abs(p), vec3(0, 1, 0)));
  t = min(t, length(mod(p.xz, 2.0) - 1.0) - 0.1);
  t = min(t, length(mod(p.zy, 2.0) - 1.0) - 0.1);
  t = min(t, length(mod(p.xy, 2.0) - 1.0) - 0.1);
  vec3 ap = p;
  float bd = box(mod(ap, 2.0) - 1.0, vec3(0.4));
  t = max(-bd, t);

  return t;
}

vec3 getnor(vec3 p) {
  float t = map(p);
  vec2 d = vec2(0.001, 0.0);
  return normalize(vec3(
    t - map(p + d.xyy),
    t - map(p + d.yxy),
    t - map(p + d.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(gl_FragCoord.x / v2Resolution.x, gl_FragCoord.y / v2Resolution.y);
  vec2 auv = vec2(gl_FragCoord.x / v2Resolution.x, gl_FragCoord.y / v2Resolution.y);
  auv = auv * 2.0 - 1.0;
	uv -= 0.5;
	uv /= vec2(v2Resolution.y / v2Resolution.x, 1);
  vec3 dir = normalize(vec3(uv, 1.0));
  dir.xz = rot(dir.xz, time * 0.01);
  //dir.zy = rot(dir.zy, time * 0.03);

  vec3 pos = vec3(0, 0, time);
  float t = 0.0;
  for(int i = 0 ; i < 100; i++) {
    t += map(pos + dir * t) * 0.75;
  }

  vec3 ip = pos + dir * t;  
  vec3 N = normalize(getnor(ip));
  vec3 V = normalize(ip);
  vec3 L = normalize(vec3(1,2,3));
  vec3 H = normalize(N + V);
  float D = max(0.0, dot(N, L));
  float S = max(0.0, pow(dot(H, N), 64.0));

  vec3 fog = vec3(2,2,3) * t * 0.02;
  float vvvv = 1.0 - dot(auv * 0.4, auv);
  out_color = vec4(map(ip + 0.5));
  out_color *= D;
  out_color *= S;
  out_color.xyz += fog;
  out_color += getf(uv) * 0.5;
  out_color *= vvvv;
}

