// Intersecting two line segments.

float cro( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

bool intersect( vec2 a1, vec2 b1, vec2 a2, vec2 b2, out vec2 point )
{
    float d = cro(b2-a2,b1-a1);
    float s = cro(a1-a2,b1-a1) / d;
    float t = cro(a1-a2,b2-a2) / d;
    point = a1 + (b1-a1)*t; // or point = a2 + (b2-a2)*s;
    return s>=0.0 && t>=0.0 && t<=1.0 && s<=1.0;
}

/*
// same math as above, alternative writing by mla (see comments)
bool intersect( vec2 a1, vec2 b1, vec2 a2, vec2 b2, out vec2 point )
{
    vec2 st = inverse(mat2(b1-a1,a2-b2))*(a2-a1);
    point = a1 + (b1-a1)*st.x;
    return s>=0.0 && t>=0.0 && t<=1.0 && s<=1.0;
    // alternative range test with single comparison
    // st = abs(st-0.5); return max(st.x,st.y)<0.5;
}
*/

// https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdLine( in vec2 p, in vec2 a, in vec2 b)
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp(dot(pa,ba)/(dot(ba,ba)),0.0, 1.0);
    return length(pa-ba*h);
}

// https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdDisk( in vec2 p, in vec2 c, in float r )
{
    return length(p-c)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // animate
    vec2 a1 = vec2(-2.0+vec2(1.5,1.0)*sin(iTime*1.1+vec2(0.0,0.5)));
    vec2 b1 = vec2( 2.0+vec2(1.5,1.0)*sin(iTime*1.2+vec2(5.0,2.0)));
    vec2 a2 = vec2(-2.0+vec2(1.5,1.0)*sin(iTime*1.3+vec2(3.0,1.0)));
    vec2 b2 = vec2( 2.0+vec2(1.5,1.0)*sin(iTime*1.4+vec2(1.5,4.5)));

    // NDC coords
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // background
    vec3 col = vec3(0.15) - 0.04*length(p);

    p *= 3.5;

    // segment 1
    {
    float    d = sdLine(p,a1,b1)-0.02;
    d = min( d,  sdDisk(p,a1,0.06) );
    d = min( d,  sdDisk(p,b1,0.06) );
    col = mix(col, vec3(0.0,0.7,0.7), smoothstep(0.01,0.0,d) );
    }

    // segment 2
    {
    float    d = sdLine(p,a2,b2)-0.02;
    d = min( d,  sdDisk(p,a2,0.06) );
    d = min( d,  sdDisk(p,b2,0.06) );
    col = mix(col, vec3(0.2,0.5,1.0), smoothstep(0.01,0.0,d) );
    }

    // intersection
    vec2 pos;
    if( intersect(a1, b1, a2, b2, pos) )
    {
        float d = sdDisk(p,pos,0.03);
        d = min( d, abs(d-0.2) ) - 0.01; // onion, see https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
        col = mix(col, vec3(1.0,0.7,0.0), smoothstep(0.01,0.0,d));
    }    

    // cheap dither (color banding removal)
    col += (1.0/512.0)*sin(fragCoord.x*2.0+13.0*fragCoord.y);

    fragColor = vec4(col,1.0);
}

// Original Bonzomatic Shader
// https://gist.github.com/gam0022/6332d497d886bdf9d3dbd714b88d852c

#define time iTime
#define PI acos(-1.)
#define TAU (2. * PI)
#define saturate(x) clamp(x, 0., 1.)

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.)) + min(0., max(q.x, max(q.y, q.z)));
}

void U(inout vec4 m, float d, float a, float b, float c) {
    if (d < m.x) m = vec4(d, a, b, c);
}

void rot(inout vec2 p, float a) { p *= mat2(cos(a), sin(a), -sin(a), cos(a)); }

vec4 map(vec3 p) {
    vec3 pos = p;
    p = mod(p, 1.) - 0.5;
    vec4 m = vec4(1, 1, 1, 1);

    float s = 1.;
    for (int i = 0; i < 5; i++) {
        p = abs(p) - 0.5;
        rot(p.xy, -0.5);
        p = abs(p) - 0.4 + 0. * cos(TAU * time / 4.);
        rot(p.yz, -0.1);

        float a = 1.4;
        p *= a;
        s *= a;
    }

    U(m, sdBox(p, vec3(0.5, 0.05, 0.05)) / s, 1., 1., 0.);
    U(m, sdBox(p, vec3(0.5 + 0.5 * (cos(TAU * time / 4.)), 0.06, 0.05)) / s, 0., 0.1, 0.5);
    U(m, sdBox(p, vec3(0.2, 0.6, 0.1)) / s, 0., saturate(cos(TAU * (time + pos.z / 8.))), -0.5);

    return m;
}

vec3 fbm(vec3 p) { return sin(p) + sin(p * 2.) / 2. + sin(p * 4.) / 4.; }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec2 m;
    m.x = atan(uv.x / uv.y) / 3.14;
    m.y = 1. / length(uv) * .2;
    float d = m.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, time);
    vec3 ray = vec3(uv, 1.1 + cos(TAU * time / 8.));
    ray += 0.1 * fbm(vec3(1, 2, 3) + TAU * time / 4.);
    // rot(ray.xy, time);
    // rot(ray.yz, time);
    ray = normalize(ray);

    float t = 0.;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + ray * t;
        vec4 m = map(p);
        float d = m.x;
        if (m.y == 1.) {
            t += d;
            if (d < 0.001) {
                col += 0.005 * float(i);
                break;
            }
        } else {
            t += abs(d) * 0.5 + 0.01;
            col += saturate(0.001 * vec3(1. + m.w, 1, 1. - m.w) * m.z / abs(d));
        }
    }

    col = mix(vec3(0), col, exp(-0.7 * t));

    fragColor = vec4(col, 1);
}

//Original code (line breaks added):
// float i,s,e,m;vec3 d,w,q,p;d=FC.rgb/r.y-1.;
// for(w+=4.;i++<2e2;e=clamp(length(p)/s-m/s,w.y-m,.2)+i/1e6)
// {i>1e2?d/=d,o:o+=exp(-e*5e3);s=2.;p=w+=d*e;p.xz*=rotate2D(t*.2);
// p-=q=round(p);p.y=w.y-(m=fsnoise(q.zx)*4.);for(int j;j++<9;p.y-=m)
// s/=e=min(dot(p,p),.4)+.1,p=abs(p)/e-.2;}o*=e/20.;
//
// Twitter: https://twitter.com/zozuar/status/1467319312067219456
// twigl: bit.ly/3Gjl1OR
//
// seems to descend through @gaziya5 https://twitter.com/gaziya5/status/1360236394929561607
// from @pabloandrioli's shadertoy.com/view/4lX3Rj
//
// it is based on kaliset fractals
// http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/
// http://www.fractalforums.com/3d-fractal-generation/kaliset-3d-fractal-used-as-coloring-and-bump-mapping-for-de-systems/
//
// ...with the ray marching method of Distance Estimation (DE)
// https://inst.eecs.berkeley.edu/~cs184/sp12/assignments/Archive/HW6/Fractal%20Renderer.htm friendly intro
// http://blog.hvidtfeldts.net/index.php/2011/06/distance-estimated-3d-fractals-part-i/ guide with examples
//
// ray marching is a form of ray casting which uses variable step sizes based on how open the space is, which saves steps
// while preserving accuracy and enables various effects
//
// here the ray does something like zeno's paradox approaching the surface without touching it

//
// I've done a little rearranging and replacing constants with named variables, but I mostly wanted to just open this up and
// comment it to understand what's happening, without making it too hard to see how each piece relates to the original
// ultra-compact twigl code
//
// I think it's hard to understand code without changing it
// suggestion: start by finding where to restore the original behavior, then see what interesting differences you can cause
//


//from the twigl library https://github.com/doxas/twigl
mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float fsnoise(vec2 c){ //fast smooth noise
    return fract(sin(dot(c, vec2(12.9898, 78.233))) * 43758.5453);
}

//myclamp fixes a bug in the original, which depends on clamp() working this way
//clamp() has undefined behavior if minval > maxval
// but this will always clamp to maxval if minval > maxval
float myclamp(float x, float minval, float maxval){
    return min(max(x,minval),maxval);
    //return max(min(x,maxval),minval); // debug code for producing unwanted results of clamp() on some platforms
    //return clamp(x, minval, maxval); // original calculation with undefined behavior
}

vec3 render(vec2 uv){
    float i, //iterator
     s, //scale factor
     e, //ray marching step size / distance estimate
     m; // height of tower (affects shape)
    vec3 d, //direction of ray
     w, //position of tip of ray
     q, //tile of ray tip
     p; //position of ray tip within tile
    d=vec3(uv,0)-1.0; //set ray direction
    //d/=length(d); //seems like the direction vector should be normalized, but you can't have it all in ultrashort code
    vec3 o;

    bool original=false; //make this true to see the original behavior of @zozuar's code
    bool badpixel=false; //for debugging tests

    float tallest= original ? 4. : .4+iTime*0.1; //I like the look when the towers grow, it doesn't have to be a static scene
    float rotspeed= original ? .2 : .01; //it's easier to study when it's slow
    float mainloops=200.;
    int iterations= original ? 9 : 14 ; //nicer with a bit higher number

    if(original) w+=tallest; //move ray starting position up and away from the center of rotation to fly around
    else w=vec3(0.,tallest,0.); //move up with tower growth, but sit at the xz origin and pivot to look around
    while(i<mainloops){
        i+=1.;
        if(i>mainloops/2.){ //when halfway through fixed-length ray marching loop, switch to shadows
            d=vec3(1.,1.,1.);
            //d/=length(d);
        }else{
            o+=exp(-e*5000.); //the larger the step size, the less brightness is added, and a fixed number of steps is taken
            //the ray is marched to more distant objects through more unimpeded long steps
        }
        s=2.0; //initialize the scale factor for this loop
        w+=d*e; //advance ray
        p=w; //start dividing position into tile coordinates, and position within tile
        p.xz*=rotate2D(iTime*rotspeed); //rotate the world, not the camera
        q=round(p); p-=q; //get the tile and position within tile
        m=abs(fsnoise(q.zx))*tallest;//height / shapeseed of towers from smooth noise function
        //
        //here's where the magic happens: the fractal math to calculate a distance estimate to
        // the geometry and use it to produce the next ray marching step distance
        //
        if(1==0){ //convenience branch for quickly trying modified behavior
            //branch to mess with
            //iterations= 9; //convenience assignment, doesn't need to be in the loop
            p.y=w.y-m; //comment out for repeating vertical tiles
            p=abs(p);
            for(int j; j<iterations; j++){
                const float a=.4, b=.2; //these seem to be arbitrary tuning factors under .5 and .25
                e=min(length(p)*length(p),a)+(.5-a);
                //e=.5; //boxes lol
                //e=.48; //boxes of boxes
                //e=.6; //broken
                s/=e; p/=e;
                p=abs(p - vec3(b,m+b,b));
            }
            e=(length(p)-m)/s;
            e=myclamp(e,w.y-m,.2)+i/1e6;
        }else{
            //branch to keep original behavior
            p.y=w.y-m;
            for(int j; j++<iterations; p.y-=m){
                s/=e=min(dot(p,p),.4)+.1; //e is getting used as a temporary variable
                //e is always between .1 and .5, so scale factor s will be increased 2X to 10X per iteration
                p=abs(p)/e-.2; //the magnitude of p is likewise increased, but then it is reduced
            }
            e=myclamp((length(p)/s)-(m/s),w.y-m,.2)+i/1e6;
        }
        //notes:
        //e is back to its main purpose as step size
        //if(e<0.) badpixel=true; // debugging: it'll be green if e ever goes negative
    }
    if(!original){ //debugging code, enabling or disabling shouldn't change anything unless the soften shadows is uncommented
        if(e<0.1){ //e should either be negligible or slightly over .2 without doing this
            e=0.;
            e=.1; //soften shadows
        }else{
            e=.2;
        }
    }
    //e=.2; // uncomment to deactivate shadows
    o*=e/20.+ (original ? .0 : .001) ; //intensity correction and shadow rendering, based on shadow ray final step size (did the ray hit anything?)
    if(o.r>1.0){ //badcolor debugging code (helped to figure out the clamp bug)
        o=vec3(1.,0.,0.);
    }
    if(o.r<0.0){
        o=vec3(0.,0.,1.);
    }
    if(badpixel){
        o=vec3(0.,1.,0.);
    }
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int aa=0; //anti-aliasing through super-sampling, will average aa^2 samples, if 2 or more
    //because there's a lot of fine detail and edges, it looks a lot nicer with anti-aliasing
    if(aa<2){
        vec2 uv = fragCoord/iResolution.xy;
        vec3 o=render(uv);
        // Output to screen
        fragColor = vec4(o,1.0);
    }else{
        vec3 osum;
        for(int i=0; i<aa; i++){
            for(int j=0; j<aa; j++){
                vec2 uv = (fragCoord+vec2(i,j)/float(aa))/iResolution.xy;
                osum+=render(uv);
            }
        }
        // Output to screen
        fragColor = vec4(osum/float(aa*aa),1.0);
    }
}

#define M_PI 3.14159265358979323846
vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}


float simp(vec2 uv) {
    uv *= 5.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
	float f = 0.5000*noise( uv ); uv = m*uv;
	f += 0.2500*noise( uv ); uv = m*uv;
	f += 0.1250*noise( uv ); uv = m*uv;
	f += 0.0625*noise( uv ); uv = m*uv;
    f = 0.2 + 0.8*f;
    return f;
}


vec4 bumpFromDepth(vec2 uv, vec2 resolution, float scale) {
  vec2 step = 1. / resolution;

  float height = simp(uv);

  vec2 dxy = height - vec2(
      simp(uv + vec2(step.x, 0.)), 
      simp(uv + vec2(0., step.y))
  );

  return vec4(normalize(vec3(dxy * scale / step, 1.)), height);
}

vec4 bumpFromTex(vec2 uv, vec2 resolution, float scale) {
  vec2 step = 1. / resolution;

  float height = simp(uv);

  vec2 dxy = height - vec2(
      length(texture(iChannel0, uv + vec2(step.x, 0.))), 
      length(texture(iChannel0, uv + vec2(0., step.y)))
  );

  return vec4(normalize(vec3(dxy * scale / step, 1.)), height);
}


float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 offset = bumpFromDepth(uv + vec2(floor(iTime*4.0)/4.0), iResolution.xy, .1)/80.0;


    // Output to screen
                 texture(iChannel0, uv + offset.xy)*0.4;


