#define PI2 6.28318530718

float rand(in float x) {
  return fract(sin(x) * 1e4);
}

float rand(in vec2 st) {
  vec2 r = fract(sin(st) * 2.7644437);
  return fract(r.y * 276.44437 + r.x);
}

vec3 rand(in vec3 s) {
  float t = 4096.0 * sin(dot(s, vec3(17.0, 31.0, 11.0)));
  return vec3(fract(512.0 * t), fract(64.0 * t), fract(8.0 * t)) - 0.5;
}

// simplex perlin noise
float snoise(in vec3 p) {
  vec3 s = floor(p + dot(p, vec3(0.33333333333)));
  vec3 x = p - s + dot(s, vec3(0.16666666667));

  vec3 e = step(0.0, x - x.yzx);
  vec3 i1 = e * (1.0 - e.zxy);
  vec3 i2 = 1.0 - e.zxy * (1.0 - e);

  vec3 x1 = x - i1 + 0.16666666667;
  vec3 x2 = x - i2 + 0.33333333333;
  vec3 x3 = x - 0.5;

  vec4 w = vec4(dot(x, x), dot(x1, x1), dot(x2, x2), dot(x3, x3));
  w = max(0.6 - w, 0.0);
  w = pow(w, vec4(4.0));
  w *= vec4(dot(rand(s), x), dot(rand(s + i1), x1), dot(rand(s + i2), x2), dot(rand(s + 1.0), x3));

  return dot(w, vec4(30.0, 50.0, 30.0, 40.0));
}

vec2 rot(in vec2 st, in float angle) {
  float c = cos(angle), s = sin(angle);
  return mat2(c, -s, s, c) * st;
}

float alfa0(in float r_, in float r, in float theta) {
  // tan(alfa) = y'/x' = (r'*sin(theta) + r*cos(theta)) / (r'*cos(theta) - r*sin(theta))
  float s = sin(theta), c = cos(theta);
  return atan(r_ * s + r * c, r_ * c - r * s);
}

float borealis_green1(in vec2 st, in float step2) {
  const float a = 100.0;
  float r = length(st);
  float theta = atan(st.y, st.x);

  float f_ = a / r;                                 // the equation with respect to theta
  float n_ = trunc((f_ - theta) / PI2) * PI2 + PI;  // periodic N
  float r_ = -a / ((theta + n_) * (theta + n_));    // r' = -a / theta^2
  float alfa = alfa0(r_, r, theta);
  r += 4.0 * cos(rot(st, alfa).x - iTime) + rand(r) * 2.0;
  // recalc:
  float f = a / r;                                // the equation with respect to theta
  float n = trunc((f - theta) / PI2) * PI2 + PI;  // periodic N
  float func = a / (theta + n);                   // the equation with respect to r

  return smoothstep(0.0, step2, abs(r - func));
}

float borealis_green2(in vec2 st, in float step2) {
  st.x = -st.x;
  const float a = 1.55;
  float r = length(st);
  float theta = atan(st.y, st.x);

  float f_ = log(r) / log(a);                       // the equation with respect to theta
  float n_ = trunc((f_ - theta) / PI2) * PI2 + PI;  // periodic N
  float r_ = pow(a, theta + n_) * log(a);           // r' = pow(a, theta) * log(a)
  float alfa = alfa0(r_, r, theta);
  r += 4.0 * cos(rot(st, alfa).x + iTime) + rand(r) * 2.0;
  // recalc:
  float f = log(r) / log(a);                      // the equation with respect to theta
  float n = trunc((f - theta) / PI2) * PI2 + PI;  // periodic N
  float func = pow(a, theta + n);                 // the equation with respect to r

  return smoothstep(0.0, step2, abs(r - func));
}

float borealis_blue(in vec2 st, in float step2) {
  const float a = 100.0;
  float r = length(st);
  float theta = atan(st.y, st.x);

  float f_ = a / r;                                 // the equation with respect to theta
  float n_ = trunc((f_ - theta) / PI2) * PI2 + PI;  // periodic N
  float r_ = -a / ((theta + n_) * (theta + n_));    // r' = -a / theta^2
  float alfa = alfa0(r_, r, theta);
  r += 4.0 * cos(rot(st, alfa).y + iTime) + rand(r) * 2.0;  // it's weird, but pretty good
  // recalc:
  float f = a / r;                                // the equation with respect to theta
  float n = trunc((f - theta) / PI2) * PI2 + PI;  // periodic N
  float func = a / (theta + n);                   // the equation with respect to r

  return smoothstep(0.0, step2, abs(r - func));
}

float particles(in vec2 st, in float step1) {
  float r = rand(floor(st));
  return smoothstep(step1, 1.0, r) * max(0.0, sin(r * 34433.0 + 2.0 * iTime));
}

vec3 color;
const vec3 GREEN = vec3(0.0, 1.0, 0.1647);
const vec3 GREEN2 = vec3(0.0039, 0.2275, 0.1216);
const vec3 GREEN3 = vec3(0.0275, 0.2118, 0.098);
const vec3 BLUE = vec3(0.0039, 0.0196, 0.0667);
const vec3 BLUE2 = vec3(0.0, 0.1451, 0.3608);
const vec3 BLUE3 = vec3(0.4039, 0.5294, 0.7137);
const vec3 BLUE4 = vec3(0.0863, 0.1451, 0.2);

float christmas_tree(in vec2 st) {
  const float a = 0.4;
  float x = st.x - 9.0, y = st.y + 31.0;
  float f = 0.27 * cos(3.2 * y * a + abs(x * a)) * (y * a - 15.0) - 2.4 * abs(x * a) -
            0.46 * (y * a + abs(y * a)) + 5.0 * (y * a - abs(y * a - 2.0));

  if (abs(f) < 6.0) color += GREEN3 * particles(st, 0.3);  // extra particles around
  return step(f, -4.0);
}

#define scale 100.0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 st = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
  st *= scale;

  // alignment
  vec2 st2 = vec2(st.x - 15.0, st.y - 2.0 * rand(floor(st.x)));
  st2 = floor(st2);
  st2.y = st2.y * 1.2 - 35.0;
  st2.x -= 17.0;
  st2 = rot(st2, 4.5);

  // background
  color = BLUE * max(st.y * -0.09, 1.0);

  // green 1
  color += GREEN2 * (1.0 - borealis_green1(st2, 1.0));
  color += GREEN * (1.0 - borealis_green1(st2, 6.0));
  color += BLUE * (1.0 - borealis_green1(st2, 14.0));

  // green 2
  vec2 st4 = rot(st2, -PI);
  st4.x += -10.0;
  st4.y += 5.0;
  color += GREEN2 * (1.0 - borealis_green2(st4, 2.0));
  color += GREEN * (1.0 - borealis_green2(st4, 7.5));
  color += BLUE * (1.0 - borealis_green2(st4, 16.0));

  // blue
  st2 = rot(st2, -0.6);
  st2 *= 0.5;
  color += BLUE * (1.0 - borealis_blue(st2, 1.0));
  color += BLUE * (1.0 - borealis_blue(st2, 3.0));
  color += BLUE * (1.0 - borealis_blue(st2, 6.0));

  // particles and snow
  vec2 st3 = st;
  st3.y += 0.1 * iTime * (3.0 + 15.0 * rand(floor(st3.x)));
  st2.y += 0.1 * iTime * (3.0 + 15.0 * rand(floor(st2.x)));
  color += BLUE2 * particles(st2, 0.995);
  color += BLUE3 * particles(st3, 0.992);

  st = floor(st);
  color *= christmas_tree(st);

  // cliffs
  float x2 = st.x * 0.09 + 1.0;
  if (x2 < 0.0 || x2 > 3.4) {
    float threshold = snoise(vec3(x2, x2, 10.0)) * 30.0 - 15.0;
    if (st.y < threshold + 2.0 * rand(st)) color = BLUE4 * particles(st, 0.0);
    if (st.y < threshold - 1.0) color = vec3(0.0);
  }
  float x1 = st.x * 0.01;
  if (st.y < snoise(vec3(x1, x1, 10.0)) * 26.0 - 20.0) color = vec3(0.0);

  fragColor = vec4(color, 1.0);
}

