// function to draw the grid
// has a lot of parameters but allows avoiding some code duplication
vec4 grid(vec2 uv, float cellSize, vec3 color1, vec3 color2, float ratio) {
    if ((mod(uv.x * ratio, cellSize) <= cellSize/2.0 && mod(uv.y, cellSize) <= cellSize/2.0) || (mod(uv.x * ratio, cellSize) >= cellSize/2.0 && mod(uv.y, cellSize) >= cellSize/2.0)) {
        return vec4(color1, 1.0);
    } else {
        return vec4(color2, 1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates of the pixel
    vec2 uv = fragCoord/iResolution.xy;

    // duration of the animation in seconds
    float duration = 5.0;

    // colors of the grid
    vec3 color1 = vec3(1.0, 1.0, 1.0);
    vec3 color2 = vec3(0.0, 0.436, 1.0);

    // ratio of the display surface so we have a proper circle and not an ellipse
    float ratio = iResolution.x / iResolution.y;

    // center of the circle, moves with time
    // this is a very simple movement only for me to explore the iTime parameter and the cos function
    vec2 center = vec2(cos(TAU / duration * iTime) * 0.2 + 0.5, cos(TAU / duration * iTime) * 0.2 + 0.5);

    // radius of our circle
    float radius = 0.3;

    // size of each cell of the grid
    float cellSize = 0.5;

    // circle equation, true if the pixel is inside the circle
    // (pixel.x - center.x)^2 + (pixel.y - center.y)^2 <= radius^2
    // pixels in the circle get an inverted color
    /*if (square(uv.x * ratio - center.x) + square(uv.y - center.y) <= square(radius)) {
        fragColor = 1.0 - grid(uv, cellSize, color1, color2, ratio);
    } else {
        fragColor = grid(uv, cellSize, color1, color2, ratio);
    }*/

    // change suggested by hamtarodeluxe, replace the commented lines above + removes aliasing
    vec4 col = grid(uv, cellSize, color1, color2, ratio);
    float squareRadius = square(radius);
    // returns smooth Hermite interpolation between 0 and 1 when
    // squareRadius < square(uv.x * ratio - center.x) + square(uv.y - center.y) < squareRadius + 0.005
    float t = smoothstep(squareRadius, squareRadius + 0.005, square(uv.x * ratio - center.x) + square(uv.y - center.y));
    // mix is simply a lerp function
    fragColor = mix(1.-col, col, t);

}

#define B (1.-fract(t*2.))
#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.,t=iTime;
	for(;i++<99.;)
    {
        p=g*d;;
        p.z-=-t*2.;
        p=R(p,vec3(.577),clamp(sin(t/4.)*6.,-.5,.5)+.6);
        p=asin(sin(p));
        vec4 q=vec4(p,0);
        s=2.;
        for(int i=0;i++<4;){
            q=abs(q);
            q=q.x<q.y?q.zwxy:q.zwyx;
            s*=e=11./clamp(dot(q,q),.2,9.);
            q=q*e-vec4(6);
        }
        g+=e=abs(length(q.y)/s)+.002;
        c+=mix(vec3(1),H(log(s)*.3+.5),.5)*8e-5/e;  
    }
    c*=c*c;
    O=vec4(c,1);
}

#define foldAmount 4

// snow function from https://www.shadertoy.com/view/XtBfzw
float Snow(vec2 uv,float scale)
{
	float w=smoothstep(1.,0.,-uv.y*(scale/10.));if(w<.1)return 0.;
	uv+=iTime/scale;uv.y+=iTime*2./scale;uv.x+=sin(uv.y+iTime*.5)/scale;
	uv*=scale;vec2 s=floor(uv),f=fract(uv),p;float k=3.,d;
	p=.5+.35*sin(11.*fract(sin((s+p+scale)*mat2(7,3,6,5))*5.))-f;d=length(p);k=min(d,k);
	k=smoothstep(0.,k,sin(f.x+f.y)*0.01);
    	return k*w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // center the origin
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 1.5;

    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    float angle = (5./6.) * 3.1514;


    uv.x = abs(uv.x);
    uv.y += tan(angle) * .5;

    vec2 n = vec2(sin(angle), cos(angle));
    float d = dot(uv-vec2(.5, 0), n);
    uv -= n*max(0., d)*2.;

    n = vec2(sin((2./3.)* 3.1415), cos((2./3.)* 3.1415));
    float scale = 1.;

    // re center it
    uv.x += 0.5;
    for (int i = 0; i < foldAmount; i++)
    {
        uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5;

        // mirror around the center
        uv.x = abs(uv.x);

        // expand the line
        uv.x -= .5;

        // mirror
        uv -= n*min(0., dot(uv, n))*2.;

    }

    // create line segment
    d = length(uv - vec2(clamp(uv.x, -1., 1.), 0));
    col += smoothstep(1./iResolution.y, .0, d/scale);
    uv /= scale;
    col.rg /= uv;

    float speed = 5.;

    col.rg *= vec2(tan(iTime * speed) / 2.0, cos(iTime * speed) / 4.0);

    float c=smoothstep(1.,0.3,clamp(uv.y*.3+.8,0.,.75));
	c+=Snow(uv,105.)*.3;
	c+=Snow(uv,104.)*.5;
	c+=Snow(uv,113.)*.8;
	c+=Snow(uv,12.);

    col += c;

    fragColor = vec4(col, 1.0);
}

//東京
mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
float box(vec2 p,vec2 b){
    vec2 q = abs(p)-b;
    return length(max(vec2(0.),q))+min(0.,max(q.x,q.y));
}
float box3(vec3 p,vec3 b){
    vec3 q =abs(p)-b;
    return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.y,q.z)));
}
float tokyo(vec3 p){
	p*=2.;
	vec3 op = p;
	p.x -=6.;
	float d = box3(p-vec3(0.,4.,0.),vec3(5.,1.,1.));
	float q = min(d,box3(p-vec3(0.,0.,0.),vec3(4.,3.,1.)));
	q = max(q,-min(d,box3(p-vec3(0.,0.,0.),vec3(3.,2.,2.))));
	d = min(d,q);
	d =min(d,box3(p,vec3(4.,1.,1.)));
	d = min(d,box3(p,vec3(1.,7.,1.)));
	p.y-=-5.;
	p.x = abs(p.x)-2.;
	p.xy*=rot(-.885);
	d = min(d,box3(p,vec3(1.,2.5,1.)));

	p =op;
	p.x+=6.;
	float d2 = box3(p-vec3(0.,4.,0.),vec3(5.,1.,1.));
	float q2 = min(d2,box3(p-vec3(0.,0.,0.),vec3(4.,3.,1.)));
	q2 = max(q2,-min(d2,box3(p-vec3(0.,0.,0.),vec3(3.,2.,2.))));
	d2 = min(d2,q2);

	d2 = min(d2,box3(p-vec3(0.,5.,0.),vec3(1.,2.,1.)));
	d2 = min(d2,box3(p-vec3(0.,-5.,0.),vec3(1.,2.,1.)));
	d2 = min(d2,box3(p-vec3(1,-6.5,0.),vec3(2.,.5,1.)));
	p.y-=-4.;
	p.x = abs(p.x)-2.;
	p.xy*=rot(-.885);
	d2 = min(d2,box3(p,vec3(.5,2.5,1.)));	

	d= min(d2,d);
	return d/2.;
}
float diam(vec3 p,float s){
	 p = abs(p);
	 return (p.x+p.y+p.z-s)*inversesqrt(3.);
}

vec3 op;
float di(vec3 p){
    float q = 0.;
    vec4 pp = vec4(p,1.1);
    const float cc = 2.;
    for(float i=1.; i<=cc ;i++){
      q += (asin(sin(pp.x))+acos(cos(pp.z)))/pp.a;
      pp*=1.1;
      pp.xyz +=vec3(1.1,2.,3.);
      pp.xyz = abs(pp.xyz);
      pp.xz *=rot(-.785*i+i);

    }
    return q/(cc+pp.a);

}
vec2 sdf(vec3 p){
vec3 tp = p;

   op = p;

    vec2 h;
    p.xy *=rot(p.z*.1);
    p.y= -abs(p.y)+(3.+asin(sin(iTime+p.z*.1))*.5);

    h.x = length(p+1.)-.1;
    p.y +=2.+di(p);
    h.x = min(h.x, box(p.xy,vec2(6.1,.5) ) );
	h.x *=.65;
    h.y = 1.;

    vec2 t;

    tp.xz*=rot(pow(fract(iTime*.3),.125)*3.14);
    tp.xy*=rot(pow(fract(iTime*.3+.5),.125)*3.14);
    t.x = mix(diam(tp,2.5),box3(tp,vec3(1.1)),asin(sin(floor(iTime)+pow(fract(iTime),.5)))*.3+.2);
	t.x =mix(t.x , tokyo(-tp),clamp(sin(iTime),-.5,.5)+.5);
	 tp = op;
    vec4 ttp = vec4(tp,1.);
	ttp.yz*=rot(iTime*.01);
    for(float i=0.;i<=6.;i++){
        ttp*=1.3;
        ttp.xyz = abs(ttp.xzy)-11.;
        ttp.xz*=rot(-.785+i);
	    ttp.xy*=rot(i);
        t.x = min(t.x,box3(ttp.xyz,vec3(.5))/ttp.a);
    }

    t.y = 2.;
    h = t.x < h.x ? t:h;
    return h;

}
