const float pi = 3.1415926535897932;

float atanM(float y, float x){
    if(x >= 0.){
        return atan(y/x)/pi/2.+.75;
    }
    return (pi+2.*atan(y/x))/4./pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int amount = 2;
    float width = 2.5;
    float speed = 0.05;
    float speed2 = 0.075;
    float speed3 = 0.10;
    vec3 color1 = vec3(1.0, 1.5, 2.0);
    vec3 color2 = vec3(-0.1, 0.0, 0.1);
    vec3 color12 = vec3(1.0, 2.5, 2.0);
    vec3 color22 = vec3(-0.11, 0.11, 0.11);
    vec3 color13 = vec3(2.0, 2.5, 2.0);
    vec3 color23 = vec3(-0.01, 0.01, 0.01);


    vec2 R = iResolution.xy;
    vec2 uv = fragCoord-R/2.;
    vec3 col;
    vec3 col2;
    vec3 col3;
    float d = length(uv);
    float d2 = length(uv)/-50.;
    float d3 = length(uv)/-75.0;
    d -= iTime*speed;
    d2 -= iTime*speed2;
    d3 -= iTime*speed3;

    float mascara = smoothstep(R.y/2.+width*4., R.y/2.-width*4., sin(length((uv*2.*pi + iTime))));
    float mascara2 = smoothstep(R.y/2.+width*4., R.y/2.-width*4., length(uv.x) + length(uv.y));

    col = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d*width)*float(amount))+1.);
    col2 = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d2*width)*float(amount))-1.);
    col3 = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d3*width)*float(amount))+1.);
    col = color1+col*(color2-color1);
    col *= mascara2-mascara/2./pi;
    col2 = color23+col3*(color13-color23);
    col2 *= mascara2;
    col3 = color22+col3*(color12-color22);
    col3 *= mascara2;
    fragColor = vec4(col-col2-col3,1.0);
}

const vec3 CAM_START_POS = vec3(0, 0, -3.0);
const vec3 CAM_DIR = vec3(0, 0, 1.0);
const float focal_length = 1.0;

const vec3 red = vec3(1.0, 0.0, 0.0);
const vec3 green = vec3(0.0, 1.0, 0.0);
const vec3 black = vec3(0.0);

struct Ray {
    vec3 Origin;
    vec3 Direction;
};

Ray getCamRay(in vec3 origin, in vec2 uv, in float aspect_ratio) {
    float viewport_height = 2.0;
    float viewport_width = aspect_ratio * viewport_height;

    vec3 horizontal = vec3(viewport_width, 0, 0);
    vec3 vertical = vec3(0, viewport_height, 0);
    vec3 lower_left_corner = origin - horizontal/2.0 - vertical/2.0 + vec3(0, 0, focal_length);

    return Ray(origin, normalize(lower_left_corner + uv.x * horizontal + uv.y*vertical - origin));
}


float hit_sphere(in vec3 center, in float radius, in Ray r) {
    vec3 oc = r.Origin - center;
    float a = dot(r.Direction, r.Direction);
    float b = 2.0 * dot(oc, r.Direction);
    float c = dot(oc, oc) - radius*radius;
    float discriminant = b*b - 4.0*a*c;
    if (discriminant < 0.0) {
        return -1.0;
    } else {
        return (-b - sqrt(discriminant) ) / (2.0*a);
    }
}

float hit_hplane(in float h, in Ray r) {
    if (r.Direction.y == 0.0) {
        return -1.0;
    }
    return (h - r.Origin.y) / r.Direction.y;
}

vec3 at(in Ray r, float t) {
    return r.Origin + r.Direction * t;
}


vec3 rayColor(in Ray r) {
    float t = hit_sphere(vec3(0,0, -1), 0.5, r);
    if (t > 0.0) {
        vec3 N = normalize(at(r, t) - vec3(0,0,-1));
        return vec3(dot(N, vec3(0.0, 1.0, 0.0)));
    } 
    t = hit_hplane(-0.5, r);
    if (t > 0.0) {
        vec3 hp = at(r, t);
        float x = mod(hp.x, 1.0);
        float y = mod(hp.z, 1.0);
        if((x < 0.05) || (y < 0.05)) {
            return green;
        }
        return black;
    }
    vec3 unit_direction = normalize(r.Direction);
    t = 0.5*(unit_direction.y + 1.0);
    return (1.0-t)*vec3(1.0, 1.0, 1.0) + t*vec3(0.5, 0.7, 1.0);

}

const float SUBPIXELS = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 camPos = CAM_START_POS + iTime * CAM_DIR;

    vec3 col;

    for(float i = 0.0; i < SUBPIXELS; i++) {
        for(float j = 0.0; j < SUBPIXELS; j++) {
            vec2 subpixel_uv = vec2(i, j) / iResolution.xy / SUBPIXELS;
            Ray ray = getCamRay(camPos, uv + subpixel_uv, iResolution.x / iResolution.y);
            col += rayColor(ray);
        }
    }
    col /= SUBPIXELS*SUBPIXELS;

    // Output to screen
    fragColor = vec4(col,1.0);
}

// setup copied from: https://www.shadertoy.com/view/XsXfRH


vec2 worley(in vec4 P)  // animated 3D cellular noise
{
    vec3 p;
    vec3 Pi = floor(P.xyz);
    vec3 Pf = fract(P.xyz);
    float F1 = 1.0, F2 = 1.0;
    for(p.z = -1.0; p.z <= 1.0; ++p.z)
        for(p.y = -1.0; p.y <= 1.0; ++p.y)
            for(p.x = -1.0; p.x <= 1.0; ++p.x)
            {
                vec3 Pn = Pi + p;
                Pn = fract(sin(vec3(dot(Pn, vec3(127.1, 311.7, 251.3)),
                                    dot(Pn, vec3(269.5, 183.3, 478.5)),
                                    dot(Pn, vec3(307.3, 284.9, 154.1)))) * 43758.5453);
                Pn = 0.5 * sin(P.w + 6.2831 * Pn) + 0.5;
                float d = length(p + Pn - Pf);
                if(d < F1)
                {
                    F2 = F1;
                    F1 = d;
                }
                else if(d < F2)
                    F2 = d;
            }
    return vec2(F1, F2);
}


