// r2 as a function
vec2 quasi(float n)
{
    float g = 1.32471795;
    float a1 = 1.0/g;
    float a2 = 1.0/(g*g);
    float x = fract((a1*n));//float x = mod((0.5+a1*n),1.0);
    float y = fract((a2*n));//float y = mod((0.5+a2*n),1.0);
