float r(in vec2 z) {return sqrt(z.x*z.x+z.y*z.y);}
float theta(in vec2 z) {return atan(z.y,z.x);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = vec2(fragCoord.x/iResolution.y*2.0-1.0*iResolution.x/iResolution.y,fragCoord.y/iResolution.y*2.0-1.0);
    vec2 z = vec2(0.0,0.0);
    float t = iTime;
    bool inside=true;int iter = 0;
    for (int i = 0;i<100;i++){
        z = vec2(cos(t*theta(z))*pow(r(z),t)+p.x,sin(t*theta(z))*pow(r(z),t)+p.y);
        if (r(z) >= 2.0) {inside = false;break;}
        iter++;
    }

    vec3 col;
    if (inside == true) col = vec3(0.0,0.0,0.0);
    else col = vec3(float(iter)/40.0,float(iter)/30.0,float(iter)/20.0);
    fragColor = vec4(col,0.0);
}

/*
——如何分割网格？
uv坐标默认是在0到1之间，如果我们把uv乘以一个系数，这样在0到1之间的图形就会重复生成网格。
a. 把uv乘以5，把坐标等比放大5倍：uv *= 5.0
b. 使用fract()函数单位化变量，使之在0.0到1.0之间：vec2 f_uv = fract(uv)
c. 使用floor()函数对uv取整，就知道是在哪个网格了：vec2 i_uv = floor(uv)

——计算最短距离
如何计算像素点到相邻网格中随机离散点的距离？从网格坐标来说，就是x坐标从-1(左)到1(右)，y坐标从 -1(下)到1(上)，一共9 个网格的3x3区域可以用两个 for 循环遍历：

*/

// Referenced Information:
// Created by inigo quilez - iq/2013  
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// https://gameinstitute.qq.com/community/detail/122097

// 随机方法  
vec2 hash(vec2 p) {
  p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
  return fract(sin(p) * 18.5453);
}

// return distance, and cell id  
vec2 voronoi(in vec2 x) {
  vec2 n = floor(x);              // cell(n)  
  vec2 f = fract(x);              // 当前像素在 cell space 的坐标  
  vec3 m = vec3(8.);               // 影响每个 cell 的大小，影响背景颜色  
   // 遍历相邻的 9 个 cell  
  for(int j = -1; j <= 1; j++) {
    for(int i = -1; i <= 1; i++) {
      vec2 g = vec2(float(i), float(j));  // 临近的 cell id offset  
      // n+g 临近的 cell(n+g) 的随机像素坐标 o (cell space)  
      vec2 o = hash(n + g);   // 影响 cell 的颜色  
      // ❤  
      vec2 r = g - f + (0.5 + 0.5 * sin(iTime + 6.2831 * o));  
      // vec2  r = g - f + o;     // cell(n+g)[o] - cell(n)[f]   
      // ❤  
      float d = dot(r, r);  

      m.x = min(m.x, d);
    }
  }
  return vec2(sqrt(m.x), m.y + m.z);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy; // 归一化坐标
  uv -= .5; // -1 to 1
  uv.x *= iResolution.x / iResolution.y; // aspect ratio

  // computer voronoi patterm  
  vec2 c = voronoi((14.0 + 6.0 * sin(0.2 * iTime)) * uv);  

  fragColor = vec4(c.x, c.x, c.x, 1.0);
}

