        + scene(t-mb*(2./9.), uv+(1.+blur)*vec2(-.66/s, 0.), a0, wt0, ws0, blur)
        + scene(t-mb*(3./9.), uv+(1.+blur)*vec2(0., .66/s), a0, wt0, ws0, blur)
        + scene(t-mb*(4./9.), uv+(1.+blur)*vec2(0., -.66/s), a0, wt0, ws0, blur)
        + scene(t-mb*(5./9.), uv+(1.+blur)*vec2(.5/s, .5/s), a0, wt0, ws0, blur)
