        + scene(t-mb*(7./9.), uv+(1.+blur)*vec2(-.5/s, -.5/s), a0, wt0, ws0, blur)
        + scene(t-mb*(8./9.), uv+(1.+blur)*vec2(.5/s, -.5/s), a0, wt0, ws0, blur)

        ;
